css:
@-moz-document domain("fantasy.premierleague.com") {
.ism .ism-header {
    background: none;
}

.ism .ism-game-header__logo {
    display: none;
}

.ism .ism-sponsor--header {
 	display: none;   
}

.mainFooter {
    display: none;
}

.masthead {
    display: none;
}

.ism .ism-deadline-bar {
    background-image: none;
}

.ism .ism-section-title {
    display: none;
}

.ism .ism-element__shirt {
    height: 60px;
}

.ism .ism-pitch__unit {
    min-height: 96px;
}

.ism .ism-pitch {
    min-height: 402px;
        background-size: 1178px 545px;
}

.ism .ism-fixtures__heading__logo {
    display: none; 
}

.ism .ism-element__data {
	background-color: #38003C;
    color: #fff;   
}

.ism .ism-alert-wrap {
    margin: 0;
}

.ism .ism-sponsor--powered {
    display: none;
}

.ism .ism-nav__list .ism-nav__list__item>.ism-nav__tab.is-active {
    background-color: #38003C;
    color: #fff;
}

.ism .ism-nav__tab {
    border-radius: 0;
}

.ism .ism-bench {
    background: #e0e0e0;
    border-top: 5px solid #38003C;
    padding-top: 10px;
}

.ism .ismjs-sub-drop-active, .ism .ismjs-sub-drag-active {
    background-color: #607D8B; 
}

.ism .ism-main {
    padding-top: 1rem;
}

.ism .ism-sidebar {
    padding-top: 1rem;
}

.wrapper {
    margin: 0 auto;
    padding: 0;
    max-width: 1200px;
}

.ism .ism-squad-wrapper {
    background: rgb(234, 234, 234);
}

.ism .ism-bench__unit:first-of-type::after {
    background: none;
}

.ism-page-home .ism-game-header-wrap {
    background: none;
}

.ism .ism-game-header {
    padding-bottom: 0;
}

.ism .ism-element-row--pitch {
    padding-bottom: 2rem;
}
}
