css:
@-moz-document domain("arstechnica.com") {


h1,
h2,
h3 {
    font-family: inherit !important;
    color: #1888CD !important;
}
h2 a {
    color: #CCCCCC !important;
}
h1[itemprop="headline"] {
    color: #CCCCCC !important;
}
span[itemprop="name"], em, i {
    color: #01CD74;
}
span[itemprop="name"]:hover, a:hover, .overlay:hover {
    color: #FF4E00 !important;
    cursor: pointer;
    cursor: hand;
}
nav[id="header-nav-primary"], .dropdown-toggle {
    font-family: Opensans !important;
    font-size: 14px;
}
div[class="tag"] {
    font-family: Opensans !important;
}
.plain {
    color: #E7E6E5 !important;
}
.date {
    color: #FF4E00 !important;
}
.site-header, .site-wrapper, .site-footer {
    background-color: #131414 !important;
}
.comment-count-number {
    color: #00C770 !important;
}
.byline {
    color: #585B5E !important;
}

}
