css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("foxnews.com") 
{

  body, body.home, #content, div#takeover
  {
    background-color: #111 !important;
    color: #999 !important;
  }
#headline-callout #prog_alert_news_mod {
    background-color: #304b3f;
    opacity: 0.8;
  }
  #weather .your-weather .close {
    opacity: 0.8;
  }
  article .social-count {
    opacity: 0.5;
  }
  .m img {
    opacity: 0.7;
  }
  .AR_14.ob-widget .ob-rec-image-container img.ob-show {
    opacity: 0.7;
  }
  .AR_22.ob-widget .ob-rec-image-container img.ob-show {
    opacity: 0.7;
  }
  .CRAB_1.ob-widget .ob-rec-image-container img.ob-show {
    opacity: 0.7;
  }
  .AR_10 .strip-img {
    opacity: 0.7;
  }
  .masthead .search .search-wrap input {
    background-color: RGB(136, 156, 152) !important;
  }
  #top.fn .search.active input[type="text"] {
    width: 260px;
    opacity: 1;
    background-color: RGB(136, 156, 152) !important;
  }
  #top.fn .search a {
    background-color: #304B3F;
  }
  p
  {
    color: #999 !important;
  }
  #top.fn .social {
    opacity: 0.6;
  }
  #top.fn .logo {
    opacity: 0.8;
  }

  #top.fn
  {
    xbackground-color: #1f2731;
    background-color: #1f2731;
    border-bottom-color: #242e3b;
  }

  #section-branding h1, .section-branding h1,
  .OUTBRAIN .AR_14.ob-strip-layout .ob-widget-header,
  div.mod-8 > header >h2,section.mod-8 > header >h2
  {
    color: #699;
  }
  .masthead .branding .logo {
    opacity: 0.8;
  }
  .masthead.us {
    background: #1f2731;
  }
  #wrapper {
    background: #111;
  }

  body.world #doc {
    background: #242e3b;
  }
  #bottom {
    background: #0A2020;
  }
  body.editable.fyre-editor-field
  {
    color: green !important;
    background-color: black !important;
  }
  
  a,
  #account > div > div > div li a, 
  #user-options p,
  div.ob-widget-section.ob-first > ul > li > a,
  .AR_14.ob-strip-layout .ob-rec-text
  {
    color: #599 !important;
  }

  li.ob-dynamic-rec-container
  {
    background-color: #101013;
    background: transparent -moz-linear-gradient(center top , #101013 70%, #202027) repeat scroll 0% 0%;

  }
  section h2, section h2 a {
    color: #242e3b;
  }
  #mkt-snap .mkts {
    opacity: 0.7;
  }
  #mkt-snap .mkts li a p.mkt-up {
    color: #000 !important;
  }
  #mkt-snap .mkts li.up a p.mkt-per {
    color: #000 !important;
  }
  #mkt-snap .mkts li a p.mkt-per {
    color: #000 !important;
  }
  #mkt-snap .mkts li a p.current {
    color: #000 !important;
  }
  #mkt-snap .mkts li a h3 {
    color: #000 !important;
  }
  section#mkt-snap {
    border-top: 2px solid #848484;
  }
  body .OUTBRAIN .CRAB_3.ob-custom-layout {
    opacity: 0.6;
  }
  .OUTBRAIN .HOP_15.ob-strip-layout .ob-widget-header {
    border-top: solid 2px #848484;
  }
  .HOP_15.ob-strip-layout .ob-widget-header {
    color: #242e3b !important;
  }
  section h2 {
    border-top: 2px solid #848484;
  }
  section#opinion {
    opacity: 0.7;
  }
  .block + .block {
    opacity: 0.7;
  }
  [id^="signup"] {
    opacity: 0.7;
  }
  .fyre .fyre-widget {
    opacity: 0.7;
  }
  article div[itemprop="articleBody"] iframe {
    opacity: 0.7;
  }
  #rail .subscribe, .article #rail .subscribe {
    opacity: 0.7;
  }
  .mod-8 .more {
    opacity: 0.7;
  }
  
  a:hover,
  a:visited
  {
    color: #9cc !important;
    text-decoration: none !important;
  }

  
  div#nav-sub
  {
    background: transparent -moz-linear-gradient(center top , #000, #fff 30%, #fff 70%, #000 100%) repeat scroll 0% 0%;
    xbackground: transparent -moz-linear-gradient(center top , #202027, #033 30%, #033 70%, #202027 100%) repeat scroll 0% 0%;
    border-radius: 10px;
  }
  
  #nav-sub li a, #nav-sub li.dv-item a
  {
    xcolor: #099;
    text-shadow: none;
  }
  
  #section-footer-1
  {
    background-color: #185252;
    background-image: none;
    border-top: 5px solid #099;
  }
  

  .trending .c-item a,
  .trending .c-item .dv-item h3 a, .more span
  {
    color: #066 !important;
  }
  
  .c-var-49 div.dv-feature-35, .c-var-49.c-bdr-19 .c-item
  {
    background-color: #101012;
  }
  
  .cat-name a, span.cat-name, h2.title
  {
    color: #455;
  }
  
  #opinion .bkt .byline span a
{
  color: #455;
  font-weight: 700;
}
  
  .social-count span.fbk, .social-count span.sv, .social-count span.twt
  {
    border-radius: 5px;
    background-color: #333;
    color: #999;
  }
  
  section#features-faces
  {
    visibility: hidden;
  }
 
  div.rc-wc, div#top-728x90.ad.dc, div.c.c-multi, div#zerglayout
  {
    visibility: hidden !important;
    height: 0px;
  }

  div.c.c-tab.bank-rate-mod
  {
    visibility: hidden;
    height: 0px;
  }

 
  div #prog_alert_news_mod > p > a,
  #live.alert a, 
  #live.alert p, 
  #live.alert > div > a, 
  #prog-alert.alert a, 
  #prog-alert.alert p, 
  #prog-alert.alert > div > a
  {
    color: black !important;
  }
 .mod-8 li span {
    background: #185252 !important;
  } 
  .AR_22.ob-strip-layout .ob-widget-header {
    color: #BBB;
  }
  #live header .fn-go {
    opacity: 0.7;
  }
  #doc #live {
    opacity: 0.7;
  }
}
