css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("johncms.com") {
  
  html {padding: 5px}
  
  body {
    background-color: #2A323B;
    max-width: 700px;
    margin: 5px auto;
    box-shadow: 0 0 6px #16191D;
    border-radius: 5px
  }

  body, td, input, textarea {
    font-family: 'Century Gothic', Verdana, Tahoma, Arial;
    font-size: 10pt
  }
  
  a:link, a:visited {
    color: #3B3975;
    text-decoration: none;
    transition:all .3s
  }
  
  a:active {
    color: #666600;
    text-decoration: underline
  }  
  a:hover {
    color: #ff0000;
    text-decoration: none
  }
  
  h3 {
    font-weight: normal;
    margin: -5px 0 3px
  }
  
  small {font-size: 8pt}
  
  table.logo  {
    background-color: #DDE0E5;
    border-radius: 5px 5px 0 0
  }
  
  table.logo img {
    height: 15px;
    margin: 0 0 -5px
  }
  
  .header {
    padding: 8px 4px;
    margin: 0;
    border: 0
  }
  
  .maintxt {
    border: 0;
    font-weight: normal;
    margin: 0;
    padding: 0
  }  
  
  .maintxt p {color: #D2D4D6; margin: 5px}
  .maintxt p a {display: inline-block; color: #A2CDFA; padding: 5px 5px 6px; margin-left: -5px}
  .maintxt p a:hover {color: #EAF4FE}
  .maintxt p b{font-weight: normal}
  
  .maintxt div, .maintxt div p  {color: #2A323B}
  .maintxt div p a {color: #1F2465!important}
  .maintxt div p a:hover {color: #A81D32!important}
  
  .phdr {
    color: #1F252C;
    background-color: #8C9BA9;
    padding: 7px 4px 8px;
    border: 0;
    border-top: 1px solid #6E8193;
    border-bottom: 1px solid #6E8193
  }  
  .phdr a {color: #003D7D}
  .phdr a:hover {color: #C7003C}
  .phdr b {font-weight: normal}
  .phdr h3 {margin: 0}
  
  .menu {
    background-color: #D5DBE0;
    padding: 6px 5px 7px;
    border: 0;
    border-bottom: 1px solid #B7C1CA
  }
  
  .topmenu {
    color: #555;
    background-color: #dadde0;
    font-size: 8pt;
    margin: 0;
    padding: 5px 4px 6px;
    border: 0;
    border-bottom: 1px solid #B0B6BC
  }

  .list1 {
    background-color: #CFD4DC;
    padding: 6px 5px 7px;
    border: 0;
   border-bottom: 1px solid #B1BAC6
  }
  
  .list2 {
    background-color: #E6E6ED;
    padding: 6px 5px 7px;
    border: 0;
    border-bottom: 1px solid #C8C8D7
  }
  
  .sub {
    font-size: 8pt;
    margin-top: 5px;
    padding-top: 4px
  }
  .list1 .sub {border-top: 1px solid #C0C7D1}
  .list2 .sub {border-top: 1px solid #D7D7E2}
  
  .gmenu {
    background-color: #C8E8E4;
    border: 0;
    border-bottom: 1px solid #B6E0DB
  }
  
  .bmenu {
    background-color: #C5D7DE;
    border: 0;
    border-bottom: 1px solid #B5CCD5
  }
  
  .rmenu {
    background-color: #E8CCDC;
    border: 0;
    border-bottom: 1px solid #E0BAD0
  }
  .rmenu p a {color: #D52E2E!important}
  .rmenu p a:hover {color: #991414!important}
  
  .user {
    background-color: #C1CFE8;
    padding: 5px 4px 6px;
    margin: 0;
    border: 0;
    border-bottom: 1px solid #A6BBE0
  }
  
  .topmenu {font-size: 9pt}
  
  .currentpage, a.pagenav {
    display: inline-block;
    padding: 7px 14px 8px;
    margin: -4px -3px -5px 0
  }
    
  .currentpage {
    color: #555;
    background-color: #CCD0D4;
    border-color: #BEC3C8
  }
  
  a.pagenav {background-color: #E9EBEE}
  
  .fmenu {
    background-color: #21262C;
    border-top: 1px solid #0B0C0F;
    border-bottom: 1px solid #0B0C0F;
    padding: 9px 5px 9px
  }
  
  .fmenu+div {
    background-color: #374049;
    padding: 8px 5px;
    margin-top: 0;
    border-radius: 0 0 5px 5px;
    /*display: none*/
  }
  
  .list1 table.desc{border-top: 1px solid #C0C7D1}
  .list2 table.desc{border-top: 1px solid #D7D7E2}

  table.desc td{font-size: 8pt}
  
  
  /* Дополнительные ссылки (Костыли) */
  /*Ссылки низа темы форума */
  .topmenu+p+form+p+div a, .topmenu+p+form+p+div+div a, .topmenu+p+form+p+div+div+a {
    display: inline-block;
    color: #A2CDFA;
    padding: 6px 5px 7px
  }
  .topmenu+p+form+p+div a:hover, .topmenu+p+form+p+div+div a:hover, .topmenu+p+form+p+div+div+a:hover {color: #EAF4FE}
  .topmenu+p+form {margin:5px}

  /*Форма низа загрузок */
  .phdr+p+form {
    color: #ddd;
    margin: 0 6px 7px
  }  
  .phdr+p+form input {margin-bottom: 5px}
  
  /*Комментарии загрузок*/
  .maintxt p+a, .list1+br+br+a, .list2+br+br+a {
    display: inline-block;
    color: #A2CDFA;
    padding: 6px 5px 7px    
  }
  .maintxt p+a:hover {color: #EAF4FE}
  
}
