css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {

  html{
	font-size: 1.5em;
	color: #333;
}

body{
	padding: 1rem;
	padding-bottom: 2rem;
}

* {
	box-sizing: border-box;
	line-height: 1rem;
	font-size: 0.66666rem;
	margin:0;
}

p, ul, li, h1, h2, h3, h4,
.mw-body-content > div {
	max-width: 32rem;
	margin: 0 auto;
}

ul, ol{
	padding-left: 2rem;
}

	h1 {
		line-height:2rem!important;
		font-size: 1.7rem!important;
		margin: 0 auto!important;
		margin-top:1rem!important;
	}
	
	h2, h2 * {
		line-height: 2rem;
		font-size: 1.2rem;
	}
	
	p {
		margin: 0 auto!important;;
		margin-top:1rem!important;
		text-align: justify;
	}
	
p + p{
	margin-top: 1rem;
}

img{
	vertical-align:bottom;
}

h1, h2{
	margin-top: 1rem;
	line-height:2rem;
	color: #333;
}

h1{
	font-size:1.75rem;
}

h2{
	font-size: 1.25rem;
}
	
	.mw-body{
		color: hsl(0, 0%, 30%)
	}
	
	.mw-body a{
		color: hsl(240, 70%, 30%)
	}
	
	sup {
		vertical-align: middle;
		position: relative;
		bottom: .25rem;
	}
	
	sup * {
		font-size: .5rem;
	}
	
	#content {
		max-width: 25cm;
		border: none;
		margin: 0 auto;
		border: 1px solid rgb(213, 213, 213);
		padding: 1rem!important;
	}
  
	.vectorTabs {
		background: none !important;
		height:1rem!important;
	}
	
	.vectorTabs ul{
		display: flex;
		background: none!important;
	}
	
	.vectorTabs ul li{
		float:none;
		line-height:1rem !important;
		background: none!important;
		background-image: none!important;
		border: none!important;
	}
	
	.vectorTabs ul li span {
		background: none!important;
	}
	
	.vectorTabs ul li span a{
		padding: 0 .5rem!important;
	}
	
	#mw-page-base{
		background: none!important;
	}
	
	#simpleSearch {
		margin-top: 0 !important;
		height:1rem !important;
	}
	
	#searchform{
	  margin-top: 0 !important;
	}
	
	#searchInput{
		line-height: 1rem;
		padding: 0 !important;
		margin: 0!important;
		position: relative;
    bottom: 2px;
		padding-left: .25rem!important;
	}
	
	#p-personal li{
	 	margin:0 .25rem!important;
		opacity: .5;
		display: inline-block!important;
		height:1rem!important;
		line-height:1rem!important;
		background-image: none!important;
	}
	
	#mw-panel a{
		color: hsl(217, 21%, 44%) !important;
		font-size: .6rem;
	}
	
	.mw-editsection, .mw-editsection * {
		opacity: 0.6;
		font-size: .5rem;
	}
	
	.toc {
		padding: 1rem!important;
		margin-top: 1rem;
	}
	
	.infobox{
		margin-left: 1rem;
		margin-right: 1rem;
		padding: 1rem!important;
	}
	
	.infobox th{
		text-align: right;
		max-width: 50%;
		font-weight: normal;
		font-style: italic;
	}
	
	.infobox th:after{
		content: ":";
		display: inline-block;
	}
}
