css:
@-moz-document domain("tweetdeck.twitter.com") {

/* Uncomment @import line to use Fira Sans via Google Font,
   for more unicode characters and font variants.
   Remember then to remove the @font-face */

/* @import 'https://fonts.googleapis.com/css?family=Fira+Sans:300,300i,400,400i,500,500i,700,700i&subset=latin-ext'; */

@font-face {
  font-family: 'Fira Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Fira Sans'), local('FiraSans-Regular'), url(data:;base64,) format('woff2');
}

html {
    font-family: 'Fira Sans', "HelveticaNeue","Helvetica Neue",Helvetica,Arial,Verdana,sans-serif !important;
}
.avatar,
.prf-img,
.compose-account {
    border-radius: 50%;
}
.tweet-button {
    overflow: hidden;
}
.app-search {
    clear: both;
}
.dropdown-menu li {
    transition: all .3s;
}
* {
    outline: 0 !important;
}
.quoted-tweet {
    border: 0;
    border-radius: 3px;
    background-color: rgba(0, 0, 0, .05);
}
.stream-item {
    border-bottom: 0;
}
.stream-item:nth-child(odd) {
    background-color: #F8F8F8;
}
.tweet-detail-wrapper {
    border: 0;
}
.tweet-stats,
.tweet-detail-actions {
    border-top: 0;
}
}
