css:
@-moz-document domain("1d4chan.org") {
div.mw-body,
div.mw-body p,
div.mw-body h1,
div.mw-body h2,
div.mw-body h3,
div.mw-body h4,
div.mw-body ol li,
div.mw-body ul li,
div.mw-body dl dd,
div.mw-body table.mw-collapsible,
div.mw-body table.mw-collapsible b,
div.mw-body table.mw-collapsible strong,
div.mw-body table.mw-collapsible span,
div#mw-navigation div#mw-panel h3 
{
 	color: #FFFFFF;  
}

div.mw-body center table
{
 	color: black;
}

div#footer ul li
{
	color: darkgrey;
}

body,
#mw-head
{
    background-color: #161616;
}

#content
{
	background-color: #2a2a2a;
}

div.mw-body-content table
{
	color: #FAF0E6;
}

div#toc, div.thumbinner
{
	background-color: #161616;
}

div.mw-body table.mw-collapsible th
{
 	background-color: #161616;  
}

div.mw-collapsible,
div.catlinks
{
 	background-color: #161616;   
}

div.vectorTabs ul li.new span,
div.vectorTabs ul li#ca-nstab-main span,
div.vectorTabs ul li#ca-talk span,
div.vectorTabs ul li#ca-view span,
div.vectorTabs ul li#ca-edit span,
div.vectorTabs ul li#ca-addsection span,
div.vectorTabs ul li#ca-history span,
div.vectorTabs ul li#ca-viewsource span
{
 	background-color: #161616;   
}

div.vectorTabs ul li.selected span
{
 	background-color: #2a2a2a !important;   
}

div.mw-body div.mw-body-content div#mw-content-text ul#filetoc,
div.mw-body div.mw-body-content div#mw-content-text div.mw-imagepage-section-metadata table.mw_metadata tbody tr th,
div.mw-body div.mw-body-content div#mw-content-text div.mw-imagepage-section-metadata table.mw_metadata tbody tr td
{
 	background-color: #161616;
}

div#simpleSearch,
div#simpleSearch input#searchInput,
div#simpleSearch input#searchButton
{
 	background-color: #161616;
    color: #FFFFFF;
}

/*From Daniel Devac*/
table.wikitable > tr > th,
table.wikitable > * > tr > th 
{
    background-color: #2a2a2a;
    text-align: center;
}

table.wikitable 
{
    margin: 1em 0;
    background-color: #2a2a2a;
    border: 1px solid #636;
    border-collapse: collapse;
    color: black;
}

li.gallerybox div.thumb 
{
    text-align: center;
    border: 1px solid #ccc;
    background-color: #161616;
    margin: 2px;
}

/*Userstyles Settings Managed*/
/*[[theme]]*/
}

