body {
    background-image: url(http://i.imgur.com/aBt8nks.jpg);
    color: #c4c8cd;
    background-attachment: fixed;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
    background-color: #131313;
    background-size: 100%;
}
#main {
    background-color: rgba(0,0,0,0) !important;
}
div, span, a, input{
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
}

/*  記事掲載日 */
#main .art_date {
    font-weight: 600;
    color: #FFF;
    font-size: 12px;
    text-shadow: 0px 0px 1px rgba(0, 0, 0, 1);
}

/* TITLE */
#main .art_title {
    display: block;
    border-left: solid 0px #ff6a00;
    border-bottom: solid 0px #ff6a00;
    margin-top: -10px;
    margin-left: -20px;
    margin-bottom: -10px;
    padding-left: 0px;
    font-size: 180%;
    width: 95%;
}
#main .art_title a {
    text-decoration: none;
    color: black;
    display: block;
    overflow: hidden;
    text-indent: -500px;
    transform: rotate(-1.2deg);
}
#main .art_title a:after {
	content: "　Craft Simulator v3.38*9";
    color: #fff;
    font-family: 'Baloo Paaji', cursive;
    font-size: 65px;
    font-style: normal;
    font-weight: 400;
    text-shadow: 4px 3px 0px #333, 
    	12px 8px 0px rgba(0,0,0,0.333333333315),
    	1px 1px 15px rgba(0, 0, 0, 1);
    line-height: 1.3;
    text-indent: -510px;
    padding-left: 10px;
}

/* SECTION 
    #crafter_class  */

/* 上のタブ */
.ui-tabs .ui-tabs-nav .ui-tabs-anchor {
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
    float: left;
    padding: 5px 15px;
    text-decoration: none;
}

/* 上部メニュー */
.ui-corner-all, .ui-corner-bottom, .ui-corner-right, .ui-corner-br {
    border-radius: 5px;
}


/* レシピレベル選択 */
#crafter_class .crafter_recipe_level li {
    display: table-cell;
    vertical-align: middle;
    font-weight: bold;
    text-decoration: none;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
 	font-size: 50%;
}

/* Input */
#crafter_car_craftsmanship, #crafter_car_control, #crafter_car_cp {
    font-family: Play,Trebuchet MS,Tahoma,Verdana,Arial,sans-serif;
    width: 40px;
}

/* 制作物選択 */
#crafter_class .crafter_recipe_name {
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
}




/* SECTION
    #craft_user  */

/* リザルト */
.crafter_recipe_icon, .craft_action_icon {
    -webkit-filter: drop-shadow(0 0 2px rgba(0,0,0,0.9));
}
#recipe_init_quality {
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
}
#recipe_max_quality

/* アクションセット */
#craft_action_set_name {
    padding: 6px !important;
    width: 60%;
}
#craft_action_set_save {
    color: #999;
    font-weight: 600;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif !important;
}

#crafter_class .crafter_class_content {
    padding: 0 0 20px 0;
    margin-bottom: 5px;
    background-color: rgba(0,0,0,0.7);
    border: #141415 2px solid;
	box-shadow: 0px 0px 16px 1px #000000;
    -moz-box-shadow:0px 0px 16px 1px #000000;
    -webkit-box-shadow:0px 0px 16px 1px #000000;          
}

#craft_action_icon_select {
    background-color: rgba(0,0,0,0.7);
    border: #141415 2px solid;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
	box-shadow: 0px 0px 16px 1px #000000;
    -moz-box-shadow:0px 0px 16px 1px #000000;
    -webkit-box-shadow:0px 0px 16px 1px #000000;                                            
}

#craft_output {
    background-color: rgba(0,0,0,0.8);
    border: #141415 2px solid;
    box-shadow: 0px 0px 16px 1px #000000;
    -moz-box-shadow:0px 0px 16px 1px #000000;
    -webkit-box-shadow:0px 0px 16px 1px #000000;        
}
#crafter_class .crafter_app_utility {
    background-color: rgba(0,0,0,0.7);
    border: #141415 1px solid;
	padding: 10px 20px 20px 15px;
    margin-bottom: 5px;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
}

#crafter_app #crafter_app_subtitle {
    padding: 0 0 2px 7px;
    border-left: #775a3c 7px solid;
}
#crafter_app #crafter_app_save_name {
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
    border: #000 2px solid;
    padding: 2px;
    background: #111;
}
#crafter_app #crafter_app_panel input:checked + label:before {
	color: #00ceff;
    background-color: #313131;
}
#crafter_app #crafter_app_panel input:checked + label:after {
	background-color: #636363;
}
#crafter_app #crafter_app_panel input + label:before {
	background-color: #636363;
}
#crafter_app #crafter_app_panel input + label:after {
	color: #00ceff;
    background-color: #313131;
}
#crafter_app #crafter_app_tooltip input:checked + label:before, #crafter_app_follow input:checked + label:before {
	color: #00ceff;
    background-color: #313131;
}
#crafter_app #crafter_app_tooltip input:checked + label:after, #crafter_app_follow input:checked + label:after {
    background-color: #636363;
}
#crafter_app #crafter_app_tooltip input:checked + label:after, #crafter_app_follow input + label:after {
    background-color: #636363;
}
#crafter_app #crafter_app_tooltip input + label:before, #crafter_app_follow input + label:before {
    background-color: #636363;
}
#crafter_app #crafter_app_tooltip input + label:after, #crafter_app_follow input + label:after {
	color: #00ceff;
    background-color: #313131;
}
#crafter_app #crafter_app_state_import #crafter_app_state_class input:checked + label {
	color: #00ceff;
    background-color: #313131;
}
#crafter_app #crafter_app_state_import #crafter_app_state_class input + label {
    background-color: #636363;
}
#crafter_app #crafter_app_state_import #crafter_app_state_url {
    border: #000 2px solid;
    padding: 2px;
    background: #111;
}
#crafter_app #crafter_app_state_import #crafter_app_state_get {
    background-color: #0e0e0e;
    border: #000000 2px solid;
    padding: 0px 9px;
    border-radius: 2px 2px 2px 2px;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
}




/* SECTION
    #craft_process */


#craft_process {
    background-color: rgba(0,0,0,0.7);
    border: #141415 2px solid;
    box-shadow:0px 0px 16px 1px #000000;
    -moz-box-shadow:0px 0px 16px 1px #000000;
    -webkit-box-shadow:0px 0px 16px 1px #000000;
}


#crafter_class .crafter_recipe_slider {
    background-color: #34373e;
    border: #1c1c23 1px solid;
    box-shadow: rgba(23, 23, 23, 0.23) 0px 0px 6px 3px;
}

#crafter_class .crafter_class_recipe {
    padding: 10px 39px 0px 43px;
    background-color: rgba(0,0,0,0.3);
    border-radius: 9px;
    box-shadow: rgba(0,0,0, 0.65098) 0px 0px 6px 3px;
}

#craft_action_macro_button.ui-button .ui-button-text, #craft_action_clear_button.ui-button .ui-button-text {
    line-height: 0.9;
    z-index: 0;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
}


#craft_action_set #craft_action_set_name {
    border: #000 2px solid;
    padding: 6px;
    width: 60%;
    background: #111;
}

#craft_action_icon_select .craft_action_type {
    margin-top: 5px;
    margin-bottom: 12px;
    padding: 0 0 2px 7px;
    border-left: #775a3c 7px solid;
    border-bottom: #ff6a00 0px double;
    font-size: 100%;
}

#craft_process .craft_process_mark {
    padding: 0 0 2px 7px;
    border-left: #775a3c 7px solid;
    border-bottom: #ff6a00 0px double;
    font-size: 100%;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
}
#craft_process_sortable .craft_action_string {
    font-size: 70%;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
}

#craft_result {
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
}



.ui-state-active a, .ui-state-active a:link, .ui-state-active a:visited {
    color: #dcdcdc;
}
.ui-state-default a, .ui-state-default a:link, .ui-state-default a:visited {
    color: #ddd;
}

.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    border: 2px solid #141415;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
    border-bottom-right-radius: 1px;
    border-bottom-left-radius: 1px;
    background: rgba(0,0,0,0.7);
    font-weight: normal;
    color: #8a8a8a;
}
.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active {
    border: 2px solid #141415;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
    border-bottom-right-radius: 1px;
    border-bottom-left-radius: 1px;
    background: rgba(0,0,0,0.9);
    font-weight: normal;
    color: #d2d2d2;
}
.ui-state-hover, .ui-widget-content .ui-state-hover, .ui-widget-header .ui-state-hover, .ui-state-focus, .ui-widget-content .ui-state-focus, .ui-widget-header .ui-state-focus {
    border: 2px solid #211b16;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
    border-bottom-right-radius: 1px;
    border-bottom-left-radius: 1px;
    background: rgba(13, 27, 66, 0.74);
    font-weight: normal;
    color: #fff;
}



#craft_diff_progressbar {
    background-color: #161616;
    height: 20px;
    margin-bottom: 8px;
    border: #040404 2px solid;
}
#craft_quality_progressbar {
    background-color: #161616;
    height: 20px;
    margin-bottom: 8px;
    border: #040404 2px solid;
}


#craft_action_set #craft_action_set_list li, li.craft_action_set_item {
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
    border: #000 2px solid;
    background-color: #171819;
}


#craft_process #craft_action_effective_additional .craft_action_additional_number, #craft_action_effective_additional_clone .craft_action_additional_number {
    color: #d6e8d6;
}
#craft_process #craft_action_effective_additional, #craft_action_effective_additional_clone {
    border-bottom: #798 3px double;
}

.icon_text {
    font-size: 72%;
    font-weight: bold;
}

#craft_process_sortable li {
    border: #141415 2px solid;
    background-color: #1c2538;
    border-radius: 7px;
}
#craft_process_sortable li.failed {
    border: #330e0e 2px solid;
    background-color: #381c1c;
}
#craft_process_sortable li.uncertain {
    border: #997 2px solid;
    background-color: #efd;
}
#craft_process_sortable li.uncertain {
    border: #081f21 2px solid;
    background-color: #1b2b2b
}
#craft_process_sortable li.inexecutable {
    border: #1f1f1f 2px solid;
    background-color: #2f2f2f;
}

#craft_process_sortable .ui-buttonset {
    margin-top: -5px;
}
#craft_process_sortable .ui-button {
    margin-top: -5px;
}
#craft_process_sortable .craft_action_buff {
    margin-top: -4px;
}
#craft_process_sortable .craft_action_effect {
    margin-top: -2px;
}

#craft_process_sortable .craft_action_number {
    font-size: 90%;
    font-weight: bold;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
    color: #6ca9d6;
}

#craft_process_sortable .craft_action_icon_name {
    font-size: 80%;
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
    font-weight: bold;
}
#craft_process_sortable .craft_action_buff {
    padding-top: 3px;
}
#craft_process_sortable .craft_action_effect {
    margin: 0px;
}
#craft_process_sortable .ui-button {
    color: rgb(80, 80, 80);
}
#craft_process_sortable .ui-state-active {
    color: rgb(216, 206, 154);
}


#craft_process_sortable #craft_action_template_maker,#craft_process_sortable #craft_action_template_comf,#craft_process_sortable #craft_action_template_muscle {
    font-family: "Play", "Kazesawa-Regular", "メイリオ", Meiryo, sans-serif;
    border: #000 2px solid;
    background-color: #171819;
}


/* ツールチップ */
#action_tooltip {
    border: 3px rgba(0,0,0,0.9) outset;
    background: rgba(10,10,10,0.9);
    box-shadow: 5px 5px 10px #000;
}

/* ロドストアイテム */
#eorzeadb_tooltip .db-tooltip__l_main {
    opacity: 0.98;
}


.contents {
    background-color: #1f1f1f;
    border: #192540 2px solid;
}
}
