css:
@-moz-document url-prefix("https://www.computerforum.com/") {
.secondaryContent a {
    color: rgb(0, 0, 0);
}

.node .tinyIcon {
    background: none;
}

.button.primary {
    background-color: #000000 !important;
}

.button {
    background-color: rgb(0, 0, 0);
}

.bbCodeQuote .attribution {
    color: rgb(255, 255, 255);
    background-color: rgb(0, 0, 0);
    border-bottom: 1px solid rgb(0, 0, 0);
}

.PageNav a:hover, .PageNav a:focus {
    background-color: rgb(100, 100, 100);
    border-color: rgb(100, 100, 100);
}

.PageNav a.currentPage {
    background-color: rgb(0, 0, 0);
    border-color: rgb(0, 0, 0);

}

.discussionList .sectionHeaders dd a[href]:hover {
    color: rgb(255, 255, 255);
    text-decoration: none;
    background-color: rgb(0, 0, 0);
}

.footerLinks a.globalFeed {
    width: 0px;
}



.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus,.tabs li:hover a, .tabs.noLinks li:hover,.breadcrumb .crust:hover a.crumb,body .navTabs .navTab.PopupClosed .navLink:hover,.PageNav a:hover, .PageNav a:focus,.PageNav a.currentPage,.footer .pageContent,.blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label,.button,a.callToAction span{background-color:#000!important;border-color:hsl(0,0%,24%)!important;}

a.callToAction{border-color:hsl(0,0%,24%)!important;}

body .tabs li.active:hover a{color:#fff!important;}

body .profilePage .tabs li.active:hover a{color:#000!important;}

#header{background:linear-gradient(hsl(0,0%,0%),hsl(0,0%,22%))!important;}

.nodeList .categoryStrip,.subHeading,.discussionList .sectionHeaders{background:linear-gradient(hsl(0,0%,0%),hsl(0,0%,12%))!important;border-color:hsl(0,0%,24%)!important;}

.breadcrumb .crust:hover .arrow span{border-left-color: #000!important;}

.sharePage{display:none!important;}

.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon{-webkit-filter:grayscale();filter:grayscale(1);}

.navTabs .navTab.selected .tabLinks{background-color:#fff!important;}
}
