css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nytimes.com") {

#TP_container_shadow, #TP_container, #bColumn, #cCol, div.cColumn, #login, #masthead, #navigation, div.subNavigation, #insideNYTimes, #footer, #toolsRight, div.nextArticleLink, #adxLeaderboard, div.clearfix, #articleExtras, #articleInline, #subnavMagazine, div.entry-meta, div.nav, #readerscomment, div.emailAlertModule, #articleToolsTop, div.navigation, div.articleTools, #upNextWrapper, #memberTools, #mainTabs, #adxSponLink_googleAds, .nytd_google_ads nytd_google_single_ad, .singleAd{
   display:none !important;
}

div.story.clearfix{
  display: block !important;
}

#shell{
  position:relative;
  top:-40px;
}

#article{
  margin-top:15px !important;
}

#page, #shell, #aCol, #aColumn, #bColumn, div.blog.wrap{
  border: 0px !important;
}

#main, #page{
  background: none !important;
}

a{
  text-decoration: none !important;
}

}
