css:
@-moz-document url(https://42friends.fr/) {

@font-face {
  font-family: 'Material Icons';
  font-style: normal;
  font-weight: 400;
  src: local('Material Icons'), local('MaterialIcons-Regular'), url(http://fonts.gstatic.com/s/materialicons/v17/2fcrYFNaTjcS6g4U3t-Y5UEw0lE80llgEseQY3FEmqw.woff2) format('woff2');
}

body {
    height: 100vh;
	background: #eee !important;
    background: -webkit-linear-gradient(to left, #263238 , #263238);
    background: -webkit-linear-gradient(right, #263238, #263238);
    background: linear-gradient(to left, #263238 , #263238);
    font-family: 'Open Sans', Helvetica, sans-serif;
}

#head {
    width: 100%;
    height: 64px;
    line-height: 50px;
    vertical-align: middle;
    background-color: #546e7a;
    text-align: center;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 5;
    font-weight: 300;
    box-shadow: 0 1px 8px rgba(0,0,0,.3);
    font-family: Roboto,RobotoDraft,Helvetica,Arial,sans-serif;
    -webkit-font-smoothing: antialiased;
    font-size: 21px;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    -webkit-box-shadow: 0 1px 8px rgba(0,0,0,.3);
    overflow: hidden;
    color: #fff;
    padding: 0 0 0 8px;
}

.title {
    display: inline-block;
    line-height: 60px;
}

#list .head {
    font-size: 18px;
    width: 100%;
    height: 60px;
    padding-top: 18px;
    padding-left: 10px;
    font-weight: 300;
    border-bottom: 0px solid #90a4ae;
    background-color: #78909c;
    color: #ecf0f1;
    box-shadow: 0 1px 8px rgba(0,0,0,.3);
    border-radius: 2px 2px 0 0;
    margin: 1rem 0 0rem;
}

#container {
    height: 100%;
    overflow-x: hidden;
    z-index: 4;
    width: 70%;
    padding-top: 1rem;
    padding-bottom: 1rem;
    margin-left: -.75rem;
    margin-right: -.75rem;
    margin: 0 auto;
}

#list .online {
    background-color: #90a4ae;
}

#list {
    width: 100%;
    list-style-type: none;
    margin-top: 64px;
    margin-bottom: 100px;
    box-shadow: 0 3px 6px rgba(0,0,0,0.12), 0 6px 6px rgba(0,0,0,0.24);
}

#list .invit, #list .person {
    position: relative;
    right: 0;
    width: 100%;
    color: #ecf0f1;
    padding: 10px;
    height: 60px;
    line-height: 40px;
    vertical-align: middle;
    border-bottom: 0px solid #d9d9d9;
    background-color: rgba(44, 62, 80, 0.45);
    -webkit-transition: right 500ms ease-in-out;
    transition: right 500ms ease-in-out;
    box-shadow: 0 0 0 !important;
    margin: 0rem 0 1rem;
    border-radius: 0 0 2px 2px;
}

#list {
    -webkit-box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
}

#list .person {
    position: relative;
    right: 0;
    width: 100%;
    color: #ecf0f1;
    padding: 10px;
    height: 60px;
    line-height: 40px;
    vertical-align: middle;
    border-bottom: 0px solid #d9d9d9;
    background-color: rgba(44, 62, 80, 0.45);
    -webkit-transition: right 500ms ease-in-out;
    transition: right 500ms ease-in-out;
    -webkit-box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
    margin: 0rem 0 0rem;
    border-radius: 0px;
        will-change: top,opacity;
    transition-property: background-color, padding-left;
    transition-duration: 0.8s, 0.5s;
    transition-timing-function: linear, ease-out;
    -webkit-animation-duration: 0.5s;
       -moz-animation-duration: 0.5s;
       -o-animation-duration: 0.5s;
       animation-duration: 0.5s;
       -webkit-animation-fill-mode: both;
       -moz-animation-fill-mode: both;
       -o-animation-fill-mode: both;
        animation-fill-mode: forwards;
       -webkit-animation-name: vanishIn;
       animation-name: vanishIn;
}

#form_add {
    display: none;
    -webkit-box-shadow: 0 8px 10px 1px rgba(0,0,0,0.14),0 3px 14px 2px rgba(0,0,0,0.12),0 5px 5px -3px rgba(0,0,0,0.4),0 0 100px 2000px rgba(0,0,0,0.4);
    box-shadow: 0 8px 10px 1px rgba(0,0,0,0.14),0 3px 14px 2px rgba(0,0,0,0.12),0 5px 5px -3px rgba(0,0,0,0.4),0 0 100px 2000px rgba(0,0,0,0.4);
    position: fixed;
    left: 0;
    right: 0;
    background-color: #fafafa;
    width: 55%;
    margin: auto;
    overflow-y: auto;
    border-radius: 2px;
    will-change: top,opacity;
    transition-property: background-color, padding-left;
    transition-duration: 0.5s, 0.3s;
    transition-timing-function: linear, ease-out;
    top: 50px;
    z-index: 9;
    -webkit-animation-duration: 0.5s;
       -moz-animation-duration: 0.5s;
       -o-animation-duration: 0.5s;
       animation-duration: 0.5s;
       -webkit-animation-fill-mode: both;
       -moz-animation-fill-mode: both;
       -o-animation-fill-mode: both;
        animation-fill-mode: forwards;
       -webkit-animation-name: vanishIn;
       animation-name: vanishIn;
}

.slideLeft{
    -webkit-animation-name:slideLeft;
    animation-name:slideLeft;
}

.vanishIn {
	-webkit-animation-name:vanishIn;
	animation-name:vanishIn;
}
.vanishOut {
	-webkit-animation-name:vanishOut;
	animation-name:vanishOut;
} 
    
@-webkit-keyframes slideLeft{
    0% {
		-webkit-transform-origin:0 0;
		-webkit-transform:translateX(0%);}
	100% {
		-webkit-transform-origin:0 0;
		-webkit-transform:translateX(-15);}
}

@keyframes slideLeft{
    0% {
		transform-origin:0 0;
        transform:translateX(0%);}
    100% {
        transform-origin:0 0;
        transform:translateX(-15);}
}

@-webkit-keyframes vanishIn {
	0% {
		opacity:0;
		-webkit-transform-origin:25% 25%;
		-webkit-transform:scale(1.5,1.5);
		-webkit-filter:blur(45px);
	}
	100% {
		opacity:1;
		-webkit-transform-origin:25% 25%;
		-webkit-transform:scale(1,1);
		-webkit-filter:blur(0px);
	}
}
@keyframes vanishIn {
	0% {
		opacity:0;
		transform-origin:25% 25%;
		transform:scale(1.5,1.5);
		-webkit-filter:blur(45px);
	}
	100% {
		opacity:1;
		transform-origin:25% 25%;
		transform:scale(1,1);
		-webkit-filter:blur(0px);
	}
}
@-webkit-keyframes vanishOut {
	0% {
		opacity:1;
		-webkit-transform-origin:25% 25%;
		-webkit-transform:scale(1,1);
		-webkit-filter:blur(0px);
	}
	100% {
		opacity:0;
		-webkit-transform-origin:25% 25%;
		-webkit-transform:scale(1.5,1.5);
		-webkit-filter:blur(10px);
	}
}
@keyframes vanishOut {
	0% {
		opacity:1;
		transform-origin:25% 25%;
		transform:scale(1,1);
		-webkit-filter:blur(0px);
	}
	100% {
		opacity:0;
		transform-origin:25% 25%;
		transform:scale(1.5,1.5);
		-webkit-filter:blur(10px);
	}
}
    

#add:hover {
    width: 65px;
    height: 65px;
    border-radius: 50%;
    position: fixed;
    bottom: 26px;
    right: 28px;
    background-color: #e74c3c;
    text-align: center;
    line-height: 65px;
    vertical-align: middle;
    font-size: 40px;
    color: white;
    cursor: pointer;
    box-shadow: 0 5px 11px rgba(0,0,0,0.18), 0 4px 15px rgba(0,0,0,0.15);
    z-index: 5;
    font-weight: 300;
    display: none;
    transition: .2s ease-out;
}

#add {
    width: 65px;
    height: 65px;
    border-radius: 50%;
    position: fixed;
    bottom: 26px;
    right: 28px;
    background-color: #e74c3c;
    text-align: center;
    line-height: 65px;
    vertical-align: middle;
    font-size: 40px;
    color: white;
    cursor: pointer;
    box-shadow: 0 2px 5px 0 rgba(0,0,0,0.16),0 2px 10px 0 rgba(0,0,0,0.12);
    z-index: 5;
    font-weight: 300;
    display: none;
    transition: .2s ease-out;
    will-change: top,opacity;
    transition-property: background-color, padding-left;
    transition-duration: 0.5s, 0.3s;
    transition-timing-function: linear, ease-in;
        -webkit-animation-duration: 0.5s;
       -moz-animation-duration: 0.5s;
       -o-animation-duration: 0.5s;
       animation-duration: 0.5s;
       -webkit-animation-fill-mode: both;
       -moz-animation-fill-mode: both;
       -o-animation-fill-mode: both;
        animation-fill-mode: forwards;
       -webkit-animation-name: vanishIn;
       animation-name: vanishIn;
    
}

#add:active {
    width: 65px;
    height: 65px;
    border-radius: 50%;
    position: fixed;
    bottom: 26px;
    right: 28px;
    background-color: #e74c3c;
    text-align: center;
    line-height: 65px;
    vertical-align: middle;
    font-size: 40px;
    color: white;
    cursor: pointer;
    box-shadow: 0 5px 11px rgba(0,0,0,0.18), 0 4px 15px rgba(0,0,0,0.15);
    z-index: 5;
    font-weight: 300;
    display: none;
    will-change: top,opacity;
    transition-property: background-color, padding-left;
    transition-duration: 0.8s, 0.5s;
    transition-timing-function: linear, ease-out;
    -webkit-animation-duration: 0.5s;
       -moz-animation-duration: 0.5s;
       -o-animation-duration: 0.5s;
       animation-duration: 0.5s;
       -webkit-animation-fill-mode: both;
       -moz-animation-fill-mode: both;
       -o-animation-fill-mode: both;
        animation-fill-mode: forwards;
       -webkit-animation-name: vanishOut;
       animation-name: vanishOut;
}

#list .person.active {
    right: 60px;
    will-change: top,opacity;
    transition-property: background-color, padding-left;
    transition-duration: 0.8s, 0.5s;
    transition-timing-function: linear, ease-out;
    z-index: 9;
}

.cross {
    position: absolute;
    right: -60px;
    top: 0;
    display: inline-block;
    font-size: 20px;
    line-height: 60px;
    vertical-align: middle;
    text-align: center;
    color: white;
    height: 60px;
    width: 60px;
    background-color: rgba(229, 57, 55, 0.54)!important;
    cursor: pointer;
    border-bottom: 0px solid #d9d9d9 !important;
    z-index: 2;
}

#list .loader {
	text-align:center;
	background-color: rgba(44, 62, 80, 0);
	border-bottom: 0px solid #d9d9d9;
}

#footer
{
    bottom: 0px;
    color: #aaa;
    font-size: 12px;
    line-height: 16px;
    overflow: hidden;
    -webkit-font-smoothing: antialiased;
    font-family: Roboto,RobotoDraft,Helvetica,Arial,sans-serif;
}

#footer:before {
  	content: "Updated by TheRainbowPhoenix - ";
}

}
