css:
@-moz-document domain("vk.com"){
body {
    background: #ffffff;
    line-height: 1;
}

/*шрифт поменьше*/
.module_body,
.wall_module .reply_author,
.wall_module .post_header,
.im-page.im-page_classic .im-mess:not(.im-mess_fwd) > .im-mess--text,
#side_bar .left_label,
.im-mess-stack .im-mess-stack--content .im-mess-stack--pname,
.im-page .im-page--members,
.im-page .im-page--peer-online,
.module_header .header_top,
.header_right_link,
#page_body,
#page_header,
h3 {
    font-size: 12px !important;
    line-height: 1.14;
}
.module_header {
    height: 18px;
    padding: 5px 0px 0px 5px;
    background: #f0f2f5;
}

.header_right_link{
    padding: 5px 0px 0px 0px;
}


.button_blue button,
.button_gray button,
.button_light_gray button,
.flat_button {
    font-size: 12px;
    line-height: 1;
}

/* пункты в меню слева поуже*/
#side_bar .left_label {
    height: 25px;
    line-height: 23px;
}

/*и справа тоже*/
.ui_rmenu_item,
.ui_rmenu_subitem {
    height: 28px;
}

/* надо что-то сделать с изменениме margin и padding счётчика при наведении на пункт, пока не знаю что*/
.left_count_wrap {
    vertical-align: baseline
}

/* классический чат, окно шире*/
.im-page.im-page_classic {
    width: 580px;
}
.im-mess .im-mess--text {
    width: 477px;
}

/*фикс ширины блока ввода сообщения*/
.im-page.im-page_classic .im-page--chat-input {
    width: 580px;
}

/*фикс ширины шапки*/
.im-page.im-page_classic .im-page--header-chat {
    width: 582px;
}

/* шире окно, значит и правую менюшку немного подвинуть*/
.im-right-menu.ui_rmenu {
    margin-left: 590px;
}

/*фон в блоке поля ввода*/
.im-chat-input.im-chat-input_classic {
    background: #edeef0;
    padding-left: 10px;
    padding-right: 10px;
}

/*размер шрифта в полях ввода*/
.fakeinput,
div[contenteditable="true"],
input.big_text,
input.file,
input.search,
input.text,
input[type="button"],
input[type="password"],
input[type="search"],
input[type="submit"],
input[type="text"],
input[type~="email"],
input[type~="password"],
input[type~="search"],
input[type~="text"],
textarea {
    font-size: 12px;
}

/*размер поля ввода*/
.im-chat-input .im-chat-input--text {
    padding-left: 2px;
    padding-right: 2px;
    margin-left: 5px;
    border: solid 1px #e4e6e9;
    transition: none;
}

.im-chat-input .im-chat-input--textarea .placeholder .ph_input {
    padding-left: 8px;
}
.im-chat-input.im-chat-input_classic {
    padding-bottom: 10px;
    padding-top: 10px;
}

.emoji_smile_icon{
	margin-top: 3px;
}

.im-chat-input .im-chat-input--textarea>.flat_button {
    float: left;
}
.im-chat-input .im-chat-input--selector {
    float: right;
}
.im-chat-input.im-chat-input_classic .im-chat-input--rcemoji {
    float: left;
}
.im-chat-input.im-chat-input_classic .im-chat-input--rcemoji {
    margin: 2px 15px 0 4px;
}

/* квадратные аватарки*/
.fans_idolph_wrap,
.page_list_module .thumb,
.top_profile_img,
.nim-peer.nim-peer_small .im_grid > img,
.chat_tab_img,
.group_row_photo,
.group_row_img,
.post_field_user_image,
.post_img,
.module_body .people_cell_img,
.nim-peer .nim-peer--photo-w,
.wall_module .reply_img,
.right_list_img,
.olist_item_photo,
.page_list_module .cell_img,
.ow_ava.ow_ava_small {
    border-radius: 3px;
}

/*картинки в чате поменьше*/
.nim-peer .nim-peer--photo > img {
    width: 32px;
    height: 32px;
    border-radius: 3px;
}

/*в поле ввода оставить те же*/
.nim-peer .nim-peer--photo .im_grid > img {
    border-radius: 3px;
}

/*старая шапка*/
#page_header_cont .back {
    max-width: 1060px;
    min-width: 960px;
    width: 100%;
    height: 40px;
    padding: 0 15px;
    margin: auto;
    position: relative;
    overflow: hidden;
    border-radius: 0 0 10px 10px;
}
#page_header {
    top: -42px;
}
#page_header_wrap {
    position: relative;
    height: 0px;
}

/*висящее меню слева*/
#side_bar,
#side_bar_inner {
    position: fixed !important;
    top: 0px;
    margin-top: 42px !important;
}
/**/
.wall_module .post_header{
	padding: 10px 15px 0px 15px;
}
}
