css:
/* This theme was Made By Skelun */

@-moz-document domain(surrenderat20.net) {

.news-title {
    font-size: 24px;
    line-height: 26px;
    margin: 10px 10px 5px 20px;
}
.news-title a {
    transition: all 200ms ease-out
}
.news-title a:hover {
    color: #ED8000
}
.news-date,
.news-content,
.post-footer {
    font-family: 'Droid Serif', serif;
}
.news-date {
    margin: 0 10px 15px 20px;
    font-size: 12px;
}
.news-content {
    font-size: 14px;
    line-height: 28px;
    padding: 20px;
    color: #000;
    box-shadow: 0 0 100px rgba(0, 0, 0, .3);
}
.news-content h2 {
    font-size: 24px;
    margin: 10px 0 20px;
    text-transform: uppercase;
    position: relative;
}
.news-content h2:after {
    position: absolute;
    bottom: -5px;
    left: 0;
    height: 1px;
    background-color: rgba(0, 0, 0, .1);
    display: inline-block;
    content: "";
    width: 100%;
}
.news-content h2 u {
    text-decoration: none;
}
.news-content img {
    border: none;
    box-shadow: 0 0 10px rgba(0, 0, 0, .1);
    transition: all 200ms ease-in;
}
.news-content a:hover img {
    box-shadow: 0 0 50px rgba(0, 0, 0, .3);
}
.news-content iframe,
.news-content object {
    width: 660px;
}
.separator a {
    margin: 0
}
blockquote {
    margin: 15px 40px
}
.jump-link a {
    padding: 0 20px 20px;
}
.post-footer {
    font-size: 12px;
    line-height: 20px;
    vertical-align: bottom;
    padding: 5px 20px;
}
}
