css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("virustotal.com") {
    body, .footer, .form-actions {
        background: #333030 !important;
    }
    .comment-text,
    .well,
    .dropdown-menu,
    #antivirus-results > tbody > tr:nth-child(2n+1) > td,
    #comment-signin {
        background: #424242 !important;
    }
    #dlg-signin,
    .alert,
    .modal-body,
    .modal-header,
    .modal-footer {
        background: #616161 !important;
    }
    #logo.center {
        padding-right: 16px !important;
    }
    img[alt="VirusTotal"] {
        background: #FFF !important;
        border-radius: 5px !important;
        padding: 5px 8px 0 8px !important;
        box-shadow: 0 0 10px #212121 !important;
    }
    /* Texts */
    
    a:hover,
    #message {
        color: #FFF !important;
    }
    a,
    strong,
    h3,
    h1,
    .action,
    .control-label,
    .header {
        color: #DDD !important;
    }
    td,
    dt,
    address {
        color: #BBB !important;
    }
    .text-gray,
    span,
    .comment-text,
    p,
    dd,
    li {
        color: #AAA !important;
    }
    .important {
        color: #333030 !important;
    }
    .enum {
        color: #AAA;
    }
    /* Tab lists */    
    .navbar-inner {
        background: #424242 !important;
    }
    li { border-radius: 3px; }
    li, li > a {
        transition-duration: .25s !important;
    }
    li.active {
        background: #616161 !important;
    }
    li:hover {
        background: #0F75BC !important;
    }
    li > a {
        color: #DDD !important;
        text-shadow: none !important;
        background: none !important;
    }
    li > a:hover {
        color: #FFF !important;
    }
    .dropdown-menu > li:hover,
    .dropdown-menu > li:hover {
        background: #424242 !important;
    }
    .text-red {
        color: #c52525 !important;
    }
    .action,
    #btn-more-reputation,
    #btn-sign-in,
    .btn.cancel,
    .btn {
        background: #0F75BC !important;
        border-radius: 0px !important;
    }
    .action:hover,
    #btn-more-reputation:hover,
    #btn-sign-in:hover,
    .btn.cancel:hover,
    .btn:hover {
        background: #094670 !important;
    }
    .action {
        padding-right: 1px !important;
    }
    div.file-chooser,
    div.url-chooser {
        background: #FFF !important;
    }
    div.file-chooser input,
    div.url-chooser input {
        border-radius: 0;
    }
    #file div.text-grey {
        margin-top: 10px;
    }
    #votes-resume {
        background: #FFF !important;
        border-radius: 5px !important;
        padding: 5px 8px 0 8px !important;
        box-shadow: 0 0 10px #212121 !important;
    }
    #votes-resume > .popover-spot {
        width: 100% !important;
    }
    img#google-o-meter {
        width: 100%;
    }
    a[tab="comments"] .badge-info {
        background-color: #ffffff;
    }
  	/* PAGE: Main */
  	#action-options {    
    	box-shadow: 0 0 7px #212121 !important;
    	padding: 15px;
	}
  	#action-options li {
    	margin-right: 30px;
    	width: 30%;
	}
	#action-options li:first-child { margin-left: 0px !important; }
  	#action-options li:last-child { margin-right: 0px !important; }
    /* PAGE: Scan results */
	i[alt="type-unsupported"] { color: #0F75BC; }
	div.frame { padding: 0px }
	.alert-success { color: #91cc7d; }
  	/* Extra info about file */
	.extra-info h5 {
		padding: 7px;
		background: linear-gradient(to bottom,#616161 0,#616161 47%,#424242 100%);
		color: #DDD;
		font-weight: 200;
		font-family: Verdana;
    }
	/* PAGE: Community */
  	ul#reputation-list {
    	padding: 15px;
	}
  	ul#reputation-list > li {
    	box-shadow: 0 0 7px #212121 !important;
    	margin-bottom: 15px;
	}
  	ul#reputation-list > li:hover {
    	background: #ddd !important;
	}
  	.status-phrase, ul#reputation-list > li:hover a { color: #616161 !important; }
    /* No boarders and hyperlinks underline */    
    * {
        border: none !important;
        text-decoration: none !important;
        text-shadow: none !important;
    }
}
