@-moz-document domain(vk.com) {
  body {background: #FFF; font-family: tahoma, arial, verdana, sans-serif;}
  #page_body {font-size: 11px;}
  
  
/************ Width ************/
  #page_layout {width: 791px !important;}
  #page_body {width: 631px !important; border-left: 1px solid #D9E0E7;border-right: 1px solid #D9E0E7; box-sizing: border-box; margin: 35px 15px 0 0;}
  div#content { padding: 0 10px;}
/************ HEADER ***********/
