css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vk.com") {
  
body {
  font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
  font-size: 11px !important;
  font-weight: normal !important;
  direction: ltr;
 
}
  
#side_bar .left_label {
    font-size: 11px;
  }
  
#page_wrap {
   font-size: 11px; 
  }
  
#page_body {
    font-size: 11px;
}
    
.im-page .im-page--lastact {
    font-size: 11px;
}  

.im-mess-stack .im-mess-stack--content .im-mess-stack--tools a {
    font-size: 10px;
    vertical-align: 2.5px
}  

.im-typing {
 font-size: 10.5px;
    
}  
    
.left_menu_nav_wrap {
    font-size: 10px;
    
}   

.audio_page_player .audio_page_player_title_performer, .audio_page_player .audio_page_player_title_song, .ui_search_custom input.ui_search_field  {
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
    font-size: 11px;
}
    
.audio_page_player .audio_page_player_title_song {
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
    font-size: 11px;
}

.audio_page_player .audio_page_player_duration{
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
    font-size: 10px;
}  
    
.audio_friend_name, .audio_friend_name_now, .audio_friend_audios_count, .audio_friend_now,.ui_search_fltr_control, .ui_search_fltr{
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
    font-size: 11px;
}  
    
.ui_ownblock_label, .ui_ownblock_hint {
    font-size: 11px;        
    
}   
    
h3 {
    font-size: 11px;
    
}  
    
.right_list_field, .right_list_header {
    font-size: 11px;
    
}     
    
.feedback_content, .feedback_date {
    font-size: 11px;
}
    
.im-mess-stack .im-mess-stack--content .im-mess-stack--pname {
    font-size: 10.5px;
}  
    
.wall_module .reply_box_open .submit_post_field {
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
    font-size: 11px;
}    
  
#page_layout {
    font-size: 11px;
} 

#im--page {
    font-size: 11px;
} 


.pv_comments, .pv_like, .pv_author_info, .pv_cont .pv_comments_header, .pv_cont .pv_reply_form_wrap, #pv_desc {
  font-size: 11px;
    
}
 
   
.audio_row .audio_duration_wrap {
    font-size: 10px;
    padding-right: 6px;

}    

.audio_duration {
    font-size: 0.9em;

}    

.page_photo {
    font-size: 11px;

}
    
.post_date {
    font-size: 11px;

}
    
.ui_toggler_wrap.hot {
    font-size: 11px;

}
    
.flat_button {
    font-size: 11px;
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;

}

.page_action_left,
.page_actions_wrap,
.page_action_left .page_actions_wrap {
    font-size: 11px;

}

.module_header .header_top {
    font-size: 11px;

}

.wall_module .reply_footer {
    font-size: 11px;

}

.wl_replies .reply_footer {
    font-size: inherit;

}    

.people_module .module_body {
    font-size: 11px;

}

.page_list_module .module_body {
    font-size: 11px;

}

.fc_msgs_wrap {
    font-size: 11px;

}

    
.module_body {
    font-size: 11px;
}

.page_album_title {
    font-size: 11px;
    font-weight: bold;

}


.wall_module div.page_media_link_title {
    font-size: inherit;
    font-weight: bold;
    line-height: 15px;
    color: #446790;
    padding-bottom: 0;

}

.wall_module div.page_media_link_title:hover {
    text-decoration: underline;

}

.wall_module div.page_media_link_desc_wrap {
    font-size: inherit;

}    
    
.audio_row .audio_info {
    font-size: 11px;

}
  
.audio_row .audio_performer {
    font-weight: bold;

}

.audio_page_player_title .audio_page_player_title_performer {
   font-weight: bold;
   color: #45688E;

} 

.audio_page_player  {
    font-size: 11px!important;

}
  

.topics_module .module_body {
    padding: 10px 5px;
    font-size: 11px;

}
.replies_open {
    font-size: 11px;

}

textarea {
    font-size: inherit;
    font-family: inherit;

}

  
.ui_tab_count {
    font-size: 11px;

}

.page_list_module .group_desc,
.page_list_module .people_desc,
.page_list_module .people_extra {
    font-size: 0.9em;
    color: gray;

}

.wall_module .reply_author .author {
    font-weight: normal;

}

.wall_module .reply_author,
.wall_signed {
    font-size: 11px;

}

.wall_module .like_count {
    margin-left: 2px;
    font-size: 11px;

}

.wall_module.wl_post .wall_post_text {
    font-family: Georgia, "Times New Roman", Times, serif;
    font-size: 1.45em;
    line-height: 150%;

}    
    
.wl_post_body_wrap .wall_post_text {
    width: auto;

}    
h4.profile_online {
    font-size: 11px;
    font-weight: bold;
  

}
h2.page_name {
    padding-bottom: 2px;
    max-width: 385px;
    line-height: inherit;

}
 
.wall_module .media_desc .lnk {
    font-size: 11px;
    overflow: hidden;

}
    
.notifier_baloon {
    font-size: 11px;

}

    
.im-chat-input .im-chat-input--text {
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
    font-size: 11px;

}  

.im-mess .im-mess--btn    {
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
    font-size: 11px;

}  
    
.nim-dialog.nim-dialog_classic .nim-dialog--name,
.nim-dialog.nim-dialog_classic .nim-dialog--preview,
.nim-dialog.nim-dialog_classic .nim-dialog--text-preview {
    font-size: inherit;

}

.nim-dialog .nim-dialog--date {
    font-size: inherit;
    color: #999999;
    opacity: 1

}    
  
/*.nim-dialog .nim-dialog--name .nim-dialog--name-w {
    color: #2B587A;

}*/

.nim-dialog .nim-dialog--preview,
.nim-dialog .nim-dialog--text-preview {
    color: #67829E;
    line-height: 160%;

}

    
.tt_w {
    font-size: 11px;

}

.wall_module .copy_post_date {
    font-size: 11px;

}

.ui_tabs_header {
    font-weight: normal;
    font-size: 11px;

}

#page_header {
    font-size: 11px;

}

.wall_reply_more {
    font-weight: bold;

}  
    
.wr_header {
   font-size: 11px

}    

.post_author .author,
.wall_module .author,
.wall_module .copy_author {
    font-weight: bold;
    color: #2B587A

}

.wl_head_wrap .post_author .author {
    color: #000;
    font-size: 1.4em;

}
    
h5.copy_post_author,
h5.post_author {
    font-size: inherit;

}
    
}
