css:
@-moz-document domain("vk.com") {
body
{
    /*hpr - Fundo do Flamengo e fundo alternativo cinza */
    background: url(https://www.ricaperrone.com.br/wp-content/uploads/2015/11/torcedores-do-flamengo-comemoram-gol-do-flamengo-em-partida-contra-o-goias-pela-copa-do-brasil-1385396791379_1920x1080.jpg) fixed #D5D5D5 !important;
}

/*hpr - menu principal topo */
#page_header_cont .back {
    background: #000000 !important;
    border-bottom: 3px solid #c10000 !important;
}
input.text.ts_input /* caixa buscar menu topo*/
{
    background-color: #fff !important;
}
.top_nav_btn#top_audio .top_nav_btn_icon /* icone Notificacao*/
{
        filter: grayscale(1) brightness(10);
}
.top_nav_btn#top_notify_btn .top_nav_btn_icon /* icone Musica*/
{
        filter: grayscale(1) brightness(10);
}
.top_profile_arrow /* setinha menu do avatar, cor branco */
{
    opacity: 1 !important;
}
.top_nav_btn.active /*cor vermelha fundo ao selecionar item menu topo*/
, .top_nav_link:hover
, .top_nav_btn:hover
, .top_nav_link.active 
{
    background-color: #c10000;
}

/*hpr - menu topo voltar */
#stl_bg, .stl_active.over_fast #stl_bg
{
    background: rgba(0,0,0,.6) !important;
}
#stl_text
{
    color: #fff !important;
}

/*hpr - menu lateral esquerdo*/
#side_bar ol 
{
    background: #fff;
	padding: 0px 0px 0px 8px !important;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
    border-radius: 2px;
}

/*hpr - menu abaixo menu lateral esquerdo*/
#ads_left:empty+.left_menu_nav_wrap
{
    background: #fff;
    padding: 8px 0px 8px 8px !important;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
    border-radius: 2px;
}
/*hpr - fundo campo buscar topicos */
.ui_search_input_block
{
   background: #fafbfc;
}

/*hpr lista topicos com a foto do ultimo a postar ao lado do post */
.blst_thumb {
    left: 23px !important;
    position: absolute !important;
}
.blst_info {
    padding: 11px 15px 0px 50px !important;
}

/*hpr - Remove FOTO redondada */
.group_row_img 
, .right_list_img
, .search_item_img
, .post_img
, .wall_module .reply_img
, .post_field_user_image 
, .page_list_module .cell_img
, .page_list_module .thumb 
, .blst_img
, .bp_img
, .module_body .people_cell_img 
, img.fc_contact_photo
, .ts_contact_img
, .friends_photo_img
, .fans_fan_img
, .feedback_img
, .wall_module .copy_post_img
, .profile_career_img
, .mention_tt_img
, .mention_tt_person_img
, .ow_ava.ow_ava_comm
, .apps_feed_user_photo img
, .ow_ava.ow_ava_small
, .ow_ava.ow_ava_mid
, .poll_tt_image
{
    border-radius: 4px !important;
}
.top_profile_img
{
    border-radius: 4px !important;
}
/*hpr - FIM remove Foto redonda */
}
