css:
@-moz-document domain("masterchan.org") {
body {
	margin: 0; padding: 0;
	background: #1C1C1C;	
	font: 0.813em Ariel, sans-serif;
	white-space: pre-wrap;
}
a:link, a:active  {
    color: #364D5A;
}

a:visited { 
	color: #18262E 
} 


a:hover {
    color: #3B5F72;
}

.OPpost {
	display:block;
	margin-left: 0.625rem;
	margin-bottom: 0.188rem;		
}

.faq {
	width: 43.750rem;
	height: auto;
	margin: 0 auto;
	font-size:1.02rem;
}
h2 {
font-size: 1rem;
font-weight: bold;
color: #ffffff;
border-bottom: 0.063rem dotted #0A1526;
}
.replyPost{
border-bottom: 0.063rem solid #B9C2D0; border-right: 0.063rem solid #B9C2D0;background-color:#1C1C1C;display:table;margin-bottom: 0.438rem;padding-top:0.313rem;padding-left: 0.313rem;padding-right:0.313rem;border-radius: 0.563rem;
}
.padding {
display:block;
margin-right: 0.188rem;
float: left;
color: #94A9D7;
}
.moderationForm input {
	border: 0;
	padding: 0.188rem;
	margin: 0.313rem;
	border-radius: 0.313rem;
	color: white;
	background: #7C98DF;
	 text-shadow: 0.063rem 0.063rem #475B90;
}
.smallAnonAccBoxes {
	font-size: 0.8rem;
	width:6rem;
}
.anonForm {
	width: 43.750rem;
	margin: 0 auto;
	border: 0.188rem solid white;
	padding: 0.063rem 0.313rem 0.313rem 0.313rem;
	border-radius: 0.313rem;
	background: #E0E5F5;	
}
.formHeader {
	font-size: 1rem;
	color: #293E5F;
	text-align: center;
	background: #CAD0E4;
	padding: 0.250rem;
	margin-top: 0.188rem;
	margin-bottom: 0.438rem;
	font-weight: bold;
	margin-left: auto;
	margin-right: auto;
}

.anonForm input {
	border: 0;
	padding: 0.188rem;
	margin: 0.313rem;
	border-radius: 0.313rem;
	color: #242A3E;
	background: #BECCE4;
	text-shadow: 0.063rem 0.063rem white;	
}

.anonForm input[type="text"]{
	width:7rem;
}
.anonForm input[type="password"]{
	width:7rem;
}

.anonForm label {
margin-left: 0.313rem;
}
.footer input {
	border: 0;
	padding: 0.188rem;
	margin: 0.313rem;
	border-radius: 0.313rem;
	color: Black;
	background: #7C98DF;
	text-shadow: 0.063rem 0.063rem #475B90;
}
.footer a:link {
	 text-decoration: none;
}
.moderationForm input:hover {
	background: #92ACEB;
}
.footer input:hover {
	background: #92ACEB;
}
.pagelink {
	text-decoration: none;
	color: #364D5A;
	padding: 0.625rem;
}
.selectedPage {
	font-weight:bold;
	margin-top: 0.625rem;
	text-decoration: none;
	color: #364D5A;
	padding: 0.625rem;
}
.divide {
	font-size: 1.2rem;
	font-weight: lighter;
	top: -0.063rem;
	color: #364D5A;
	position: relative;
	margin-left:0.4rem;
}
.hyperlinkCatalog {
	margin-top: 0.625rem;
	display:inline-block;
	margin-bottom: 0.625rem;
}
.pagelink:hover {
	background: #E0E5F3;
}
.paginationBar {
	float:left;
	display: inline-block;
	border: 0.063rem solid white;
	border-radius: 0.313rem;
	background: #CFD5E9;
	margin-left: 0.313rem;
	padding-left: 0.313rem;
	margin-bottom:0.313rem;	
	vertical-align: center;
}
.selfModerationBar {
	float:right;	
}
.adminBorder {
	display: inline-block;
	border: 0.063rem solid white;
	border-radius: 0.313rem;
	background: #CFD5E9;
	margin-left: 0.313rem;
	padding-left: 0.313rem;
}

.hyperlinkCatalog {
	padding-right: 0.625rem;
	padding-left: 0.625rem;
}
.lockedImg, .stickyImg, .reportedImg {
	margin-bottom:-10.250rem;position:relative;bottom:10.125rem;
	margin-left: 0.3rem;
}
.reportedRed{
	margin-bottom:-11.450rem;position:relative;bottom:11.125rem;
	margin-left: 0.3rem;
}
.globalThreadContainer{
	
	border-top: 0.13rem dashed #798DB0;
	border-bottom: 0.13rem dashed #798DB0;
	margin-top: 0.625rem;
	margin-bottom: 0.625rem;
	padding-top: 0.625rem;
	padding-bottom: 0.625rem;
	background: #FAFBFF;
	padding-right:1rem;
	min-height:18.5rem;		
}

.offTopic {
	text-transform: uppercase;
	background: #ffffff;
	font-size: 0.6rem;
	font-family: Tahoma;
	border: 0.063rem dashed #B3B3B3;
	padding: 0.188rem 0.25rem 0.188rem 0.25rem;
	font-weight: bold;
	border-radius: 0.188rem;
	letter-spacing: 0.063rem;
	color: #6F6F6F;
	margin-left: 0.188rem;
	margin-right: 0.5rem;
}
.offTopic:hover {
	color: #313131;
}
.onTopicContainer {
	padding-right:1rem;
	min-height:18.5rem;
}
.offTopicContainer {
	background: #1C1C1C;
	padding-right:1rem;
	min-height:18.5rem;
}
.offtopicThread a:link {
	color: #68808E;
}
.offTopicUsername{
	color: #59C643;
	font-weight: bold;
}
.postTable{
	border: 0.063rem solid #536292;
	border-radius: 0.625rem;
	width: 30rem;
	margin: 0 auto;
}
.postTable table {
	padding: 0.188rem;
}
.postTitle{
	background: #ffffff;
	padding: 0.188rem;
	border-top-left-radius: 0.625rem;
	border-top-right-radius: 0.625rem;
	text-align: center;
	font-size: 1rem;
	font-weight: bold;
}
.num {
	text-align: center;
	padding-left: 9rem;		
}
.globalThread {
text-transform: uppercase;
background: white;
font-size: 0.6rem;
font-family: Tahoma;
border: 0.063rem dashed red;
padding: 0.188rem 0.25rem 0.188rem 0.25rem;
font-weight: bold;
border-radius: 0.188rem;
letter-spacing: 0.063rem;
margin-left: 0.188rem;
margin-right: 0.5rem;
}
.globalThread:hover {
	background: #F6F7FC;
}
.username {
	padding-right: 0.125rem;
}
.moderated {
margin-top:0.625rem;
}

.moderated span {
border-radius: 0.438rem;
border: 0.063rem solid #9AA5D7;
padding: 0.375rem;
background:white;
color: red;
font-size: 0.8rem;
font-family: Verdana;
margin-right:0.3rem;
}
.moderated span:hover {
background:#F5DDDE;
color: #511719;
}
.postInfo{
display:inline-block;margin-top:0.188rem;
}

.imageThumb, .mediaFile {
margin-right:0.7rem;float:left;clear:left;padding-top: 0.313rem;margin-bottom: 0.313rem;
}

.hideImageReported {
 -webkit-filter:blur(7px);filter:blur(7px);
}

.postCheckBox {
float:left;display: inline-block;margin-right:0.438rem;
position:relative;top:0.1rem;
}
.postCheckBoxFrontPage {
float:left;display: inline-block;margin:0px;margin-right:0.2rem;padding:0px;
position:relative;top:0.1rem;
}
.postTextBox {
padding-top: 0.313rem;
}

.replyForm {
font-size:0.7rem;
padding: 1rem;
margin: 0 auto;
background: none;
}


.postTextContainer {
margin-left: 0.625rem;  
}
.threadEndHR {
clear:both;width:100%;border: none; height: 0; border-top: 0.063rem solid #B9C2D0;
}
.mediaFileAttrb { 
display:inline-block;margin-right:0.3rem;
}
.username {
color:green;font-weight:bold;
padding-left:0.6rem;
padding-right:0.6rem;
}
.postNumber {
padding-left:0.6rem;
padding-right:0.6rem;
}

.greenText {
    color:green;
}
.spoiler>.greenText {
    color:transparent;
}

.quotedPostNumber {
    color:red;
}
.spoiler >.quotedPostNumber {
    color:black;
}

.spoiler {
    background-color:black;
    color:black;
}
.spoiler:hover {
    color: white;
    background-color:black;
}
.spoiler:hover > .greenText{
    color: white;
    background-color:black;
}
.spoiler:hover > .quotedPostNumber{
    color: white;
    background-color:black;
}

.spoiler >.userHyperlink {
    color:black;
}
.spoiler >.greenText >.userHyperlink {
    color:black;
}
.spoiler:hover > .userHyperlink{
    color: white;
    background-color:black;
}

.spoiler:hover >.greenText >.userHyperlink {
    color: white;
    background-color:black;
}

.omitted {
	color: #223463;
}

.header {
	padding: 0.313rem;
	background: #BFC6DD;
	box-shadow: 0 0.250rem 0.125rem -0.125rem gray;
	margin-bottom: 1.250rem;		
}

.header a:link {
	text-decoration:none;
	color:#800000;
}
.header a:visited {
	text-decoration:none;
	color:#800000;
}



.header a:hover {
	color: #E10000;
}

.headerLoginForm input[type="text"]{
	width: 6rem;
	width: 6rem;
}
.headerLoginForm input[type="password"]{
	width: 6rem;
	width: 6rem;
}

.frontPageLoginForm input[type="text"]{
	width: 8rem;
	width: 8rem;
}
.frontPageLoginForm input[type="password"]{
	width: 8rem;
	width: 8rem;
}

.loginFormFrame input {
border: 0;
padding: 0.188em;
margin: 0.313em;
border-radius: 0.313em;
color: #FFFFFF;
background: #6C7FD0;
text-shadow: 0.063em 0.063em #454D70;
}


.newThreadExpander {	
	width: 100%;
	text-align:center;	
	font-size: 1rem;
	color: #213A79;
	text-decoration: none;
	font-weight: bold;
	cursor: pointer;
}
.masterTitle {
	margin: 0 auto;
	text-align: center;
	font-size:1.2rem;
	margin-right:0.5rem;
}

.masterTitle span {
	color:Black;
	font-weight:bold;
}

.masterTitle a {
	text-decoration: none;
	font-size:2rem;
}
.postForm {
	text-align: center;
	margin: 0 auto;
}

.postFormContainer {
	width:100%;
	text-align: center;
}
.underBar {
	float:left;
	margin-left: 0.15rem;
}
.headerProfile {
	float: right;
}
.headerProfile input {
	display: inline-block;
}
.headerLeft {
	float: left;
}
.headerLoginForm {
	float: right;
}
.helpfulLinks {
	float: right;
	clear: right;
	margin-top: 0.188rem;	
}
.boardLink {
	margin-right: 1rem;
}
.postForm input, textarea {
	border: 0;
	padding: 0.188rem;
	margin: 0.313rem;
	border-radius: 0.313rem;
	color: #242A3E;
	background: #BECCE4;	
	font: 0.813rem Ariel, sans-serif;	
}

.postContainer {
	border: 0.125rem solid white;
	border-radius: 0.313rem;
	padding: 0.313rem;
	width: 29.375rem;
	margin: 0 auto;
	color: #0A132B;
	background: #DEE3F5;
}
.anonACCName {
height: 1.250rem;width:8.125rem;margin-top:0.625rem
}
.anonACCPassword {
	width:8.125rem;
	height: 1.250rem;
}
.catalogContainer {
	padding-left: 35px;
	margin-bottom: 10px;
}
.catalogThread {
	float:left;
	width: 9.375rem;
	overflow:hidden;
	text-align: center;
	height: 250px;
	margin-top:1.6rem;
	border:2px solid #A1B0CB;
	background: #E7ECF5;
	margin-right: 15px;
}
.catalogMedia {
	padding-top: 0.3rem;	
}

.postTextBox {
	padding-top: 0.313rem;	
}

.postFormPostTextBox {
	width: 25.938rem;
}

.newThreadExpander {;
	display: none;
	width: 100%;
	text-align:center;	
}

.reportedWarning {
	clear:both;
	border: 1px dashed red;
	width: 50rem;
	text-align: center;
	padding:1rem;
	background: white;
	margin: 10px auto 10px auto;
		border-radius: 0.3rem;
}

.repliedPosts {
	margin-right:0.2rem;
	font-size:0.7rem;
}

}
