css:
@namespace "http://www.w3.org/1999/xhtml";
@-moz-document domain("lenta.ru") {
body {
    color: #a5bbdc;
}
.g-application {
    padding-left: 200px !important;
    width: 100%;
}
.b-header-main__logo-icon, .b-header-inner__logo-icon {
    background: #ccc none repeat scroll 0 0;
    fill: #ccc !important;
    padding: 3px;
}
.g-layout {
    border: 1px solid #444 !important;
    margin: 1px;
    padding: 5px;
    width: 100%;
}
.g-application {
    background-color: #1d232c;
}
a {
    color: #fff;
}
a, a.dark {
    color: #fff;
}
a.js-hover:hover, a:hover {
    color: #f60;
}
.b-topic-addition .bordered-title a {
    color: #9b59b6;
}
.b-top7-for-main .first-item h2 {
    background: #232f3d none repeat scroll 0 0;
    color: #9b59b6;
    padding: 2px 5px 5px;
}
.first-item {
    border: 1px solid #444;
}
.img, .first-item:hover {
    box-shadow: 0 0 10px 0 rgba(4, 162, 220, 0.5);
}
.b-topic_news .b-topic__title, .b-topic_pressrelease .b-topic__title {
    color: #9b59b6;
}
.b-longgrid-column .g-date .time {
    color: #c3a37a;
}
img, .b-longgrid-column .item {
    transform: scale(1);
    transition: all 0.3s ease 0s;
}
.b-tabloid .bordered-title:hover, img:hover, .b-longgrid-column .item:hover {
    border: 1px solid #000;
    box-shadow: 0 0 10px 0 rgba(4, 162, 220, 0.5);
    transform: scale(1.04);
    z-index: 100;
}
.b-tabloid .bordered-title, .b-tag-page .news {
    transform: scale(1);
    transition: all 0.3s ease 0s;
}
.b-tabloid .bordered-title:hover, .b-tag-page .news:hover {
    transform: scale(1.03);
}
.b-yellow-box__wrap {
    -moz-border-bottom-colors: #9b59b6;
    -moz-border-left-colors: #9b59b6;
    -moz-border-right-colors: #9b59b6;
    -moz-border-top-colors: #9b59b6;
}
.b-yellow-box__header {
    background-color: #9b59b6;
    border-bottom: 0 none !important;
    color: #fff;
}
.b-yellow-box {
    background: #24303d none repeat scroll 0 0;
    border-top: 3px solid yellow;
}
.b-tags-list {
    background: #734689 none repeat scroll 0 0;
    border-bottom: 1px solid yellow;
}
.b-yellow-box .item {
    border-bottom: 1px solid #000;
    border-top: 1px solid #364451;
}
.g-date .time, .g-time {
    color: #9b59b6;
}
.b-tabloid .rightcol, .b-header-main .date, .b-inline-topics-box .topics h4 a {
    color: #999;
}
.b-inline-topics-box .topics .rightcol {
    color: #888;
}
.b-inline-topics-box .b-inline-topics-box__wrap {
    border-bottom: 1px solid #9b59b6;
    border-left: 1px solid #9b59b6;
    border-right: 1px solid #9b59b6;
}
.b-inline-topics-box h2 {
    background-color: #9b59b6;
    color: #fff;
    font-size: 14px;
}
.b-topic-addition .bordered-title {
    border-bottom: 1px solid #9b59b6;
    border-top: 2px solid #9b59b6;
    color: #9b59b6;
}
.button__text {
    display: inline-block;
    position: relative;
    vertical-align: top;
}
* {
    box-sizing: border-box;
}
* {
}
.button_theme_dark {
    color: #000;
    font-weight: 100;
}
.button_theme_dark:hover {
    color: yellow;
    font-weight: 700;
}
section.b-banner.b-banner-bottom.js-advert {
    display: none !important;
}
.b-longgrid-column .item__rubric-icon {
    fill: #888;
}
.button_theme_dark, .button_theme_dark::before {
    background-color: #9b59b6;
}
.b-socials__comments {
    background: #9b59b6 none repeat scroll 0 0;
    border: 0 none;
}
.b-top7-for-main .item {
    background: #24303d none repeat scroll 0 0;
    border-bottom: 1px solid #000;
    border-top: 1px solid #364451;
    padding: 5px;
}
.b-top7-for-main .items {
    margin-top: -1px;
}
.b-longgrid-column .item {
    background: #24303d none repeat scroll 0 0;
    border-bottom: 1px solid #000;
    border-top: 1px solid #364451;
}
.g-date, .g-time {
    color: yellow;
}
.line {
    background-color: #9b59b6;
}
.b-tabloid {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 center;
}
.span4 {
    border-bottom: 1px solid #000;
    border-top: 1px solid #364451;
}
.b-tabloid .b-tabloid__topic {
    background: #24303d none repeat scroll 0 0;
    border-bottom: 1px solid #000;
    border-top: 1px solid #364451;
    padding: 3px;
    transform: scale(1);
    transition: all 0.3s ease 0s;
}
.b-tabloid .b-tabloid__topic:hover {
    border: 1px solid #000;
    box-shadow: 0 0 10px 0 rgba(4, 162, 220, 0.5);
    transform: scale(1.05);
}
.b-tabloid .bordered-title a {
    color: yellowgreen;
}
.b-tabloid .bordered-title {
    border-bottom: 1px solid #000;
    border-top: 1px solid #364451;
}
.b-socials__comments {
    color: #fff;
}
.b-sidebar-menu_origin {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 center;
    overflow: auto;
}
.b-feature_article .b-feature__wrap, .b-feature_extlink .b-feature__wrap {
    background: #000 none repeat scroll 0 0;
}
.b-sidebar-menu_origin ul .delimiter div {
    border-bottom: 1px solid #333;
    border-top: 1px solid #000;
}
.b-topic__relation {
    background-color: #9b59b6;
}
.b-sidebar-menu__age-limit {
    display: none;
}
.b-tabloid .bordered-title {
    text-align: center;
}
.b-top7-for-main .item a:hover::after, .b-top7-for-main h2 a:hover::after, .b-longgrid-column .item h3 a:hover::after, .b-tabloid__item .b-tabloid__topic > a:hover::after, .b-topic-addition .item .title a:hover::after, .b-yellow-box .item a:hover::after {
    color: #fff;
    content: " - Дебилы, бл*ть !";
    transition: all 0.7s ease 0s;
}
.b-longgrid-column .item.article h3 a:hover::after, .b-tabloid__item .b-tabloid__topic > a.js-dh:hover::after {
    content: " - СССка! ";
    transition: all 0.3s ease 0s;
}
}
