css:
@-moz-document regexp("(.*).mobile-review.com/(.*)") {
/* прячем рекламу*/

div.adv_vert2,
td#newsblock.script,
div#authblock,
div#mainbanner,
#newsblock iframe {
	display: none !important
}
/* прячем соц сети и счётчики*/

div#topline,
iframe#fbframe,
ul.social,
table#vkshare0,
div#___plusone_0,
iframe#twitter-widget-0,
div#counters {
	display: none !important
}
/* ограничиваем максимальную ширину страницы*/

div.header,
table#maincontent {
	max-width: 1500px;
	margin: auto !important
}
/* ровняем */

div #toptextblock {
	height: 114px !important
}
#menu,
#newphones,
#newreviews {
	width: 100% !important
}
td#leftcolumn {
	max-width: 350px;
	width: 25% !important
}
td#mainblock {
	min-width: 800px !important;
	padding: 0 30px !important
}
#newsblock {
	max-width: 350px; 
	width: 25% !important
}
html,
body,
#mainblock p {
	font-family: Calibri, Helvetica, sans-serif;
	font: 1vw/1.4em Calibri, sans-serif;
	text-align: justify !important
}
div.article {
	font-size: 14px !important
}
#mainmenu a {
	height: 100%;
	font-size: 2vw !important											
}
#mainmenu {
		width: 25%;
	max-width: 350px	!important
}

/* комментарии Disqus*/

div.posts > p {
	font-size: 13px !important
}
}
