css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com") {
  
  body {
    font-family: 'Arial';
    background: #fff none repeat scroll 0 0;
  }

  #page_body {
    font-size: 12px;
  }

  h5.copy_post_author, h5.post_author,
  .wall_module div.page_media_link_desc_wrap {
    font-size: 12px;
  }

  .ui_tabs_header, .ui_tabs_sub_header,
  .narrow_column,
  .left_menu_nav_wrap,
  #page_info_wrap,
  h4.profile_online {
    font-size: 12px;
  }

  .profile_info_row {
    padding-top: 0;
  }
  
  .counts_module {
    display: none;
  }
  
  img, 
  .page_list_module .thumb,
  .ow_ava.ow_ava_small,
  .nim-peer .nim-peer--photo-w,
  .ui_zoom_inner,
  .fans_fan_bl_wrap,
  .fans_fan_bl,
  .owner_photo_preview100,
  .owner_photo_preview50,
  .ow_ava.ow_ava_comm {
    border-radius: 0 !important;
  }
  
  .audio_play,
  .audio_page_player .audio_page_player_play .icon {
    border-radius: 10%;
  }

  .nim-dialog.nim-dialog_failed .nim-dialog--unread, 
  .nim-dialog.nim-dialog_unread .nim-dialog--unread, 
  .nim-dialog.nim-dialog_unread.nim-dialog_prep-injected .nim-dialog--unread {
    border-radius: 2px;
  }

  .fans_fan_bl_wrap {
    background: none;
    right: 0;
    top: 0;
    padding: 0;
  }

  .fans_fan_bl {
    background-color: #000;
    opacity: 0.5;
  }
  
  #profile_photos_module .module_header,
  #profile_photos_module .header_right_link {
    display: none;
  }
  
  .page_photos_module,
  .page_photo {
    padding: 0;
  }

  .profile_actions .page_action_left {
    width: 194px;
  }

  .profile_msg_split .cut_left {
    width: 192px;
  }

  .page_action_left .flat_button, .page_actions_cont .flat_button {
    margin-top: 0;
  }
  
  .page_avatar {
    /*text-align: right;*/
  }
  
  .profile_content .page_avatar {
    max-height: none;
  }
  
  .page_actions_wide.no_actions .page_action_left {
    width: 230px;
  }
  
  .page_name, h2.page_name {
    font-size: 15px;
    color: #507299;
    font-weight: bold;
  }
  
  .page_photos_module {
    text-align: left;
  }
  
  .page_square_photo {
    width: 133px;
  }
 
  #stl_bg {
    padding-top: 20px;
  }

  #side_bar .left_label {
    font-size: 11px;
    height: 20px;
    padding-left: 10px;
    line-height: 20px;
  }

  #side_bar ol li {
    margin-left: 5px;
  }

  .left_menu_nav_wrap {
    margin-left: 15px;
  }

  #side_bar_inner {
    position: relative !important;
  }

  .ui_rmenu_item, .ui_rmenu_subitem {
    height: 20px;
    line-height: 20px;
  }
  
  .left_settings {
    height: 23px;
    margin: -23px 0 0 -22px;
  }

  .left_count_wrap {
    margin: 0;
    padding-top: 6px;
    line-height: 11px;
    height: 11px;
  }

  #side_bar .left_icon {
    display: none;
  }

  .narrow_column {
    position: relative !important;
    top: 0 !important;
  }
  
  #page_header_cont {
    position: relative;
  }
  
  #page_header_cont .back {
    background: -moz-linear-gradient(top, #3c5c82 0%, #5a80a7 100%); 
    background: -webkit-linear-gradient(top, #3c5c82 0%,#5a80a7 100%);
    background: linear-gradient(to bottom, #3c5c82 0%,#5a80a7 100%);
    border-radius: 0 0 10px 10px;
  }
  
  #page_header_wrap {
    width: 100% !important;;
    display: inline-table;
    position: relative;
  }
  
  #page_body,
  #side_bar_inner {
    margin-top: 0 !important;;
  }

  .body_im #side_bar {
    position: relative;
  }
  
  #page_header {
    /*width: 930px !important;*/
  }
  
  .head_nav_item {
    /*margin-right: 16px;*/
  }
  
  .top_nav_link:hover,
  .top_nav_link.active,
  .top_nav_btn:hover,
  .top_nav_btn.active,
  .top_audio_player:hover,
  .top_audio_player.active {
    background-color: transparent;
  }
  
  .ts_cont_wrap {
    left: 156px;
  }
  
  input.text.ts_input {
    border-radius: 3px;
    background-color: #f6f6f6;
  }

  .top_profile_img {
    display: none;
  }

  .top_nav_link {
    margin-right: 20px;
  }

  .top_profile_name {
    margin-right: 5px;
    padding-right: 0;
  }

  #feed_filters .page_block {
    display: none;
  }

  .owner_photo_bubble_wrap,
  .owner_photo_bubble {
    width: 230px;
  }

  .photos_period_delimiter_fixed {
    margin-top: 0;
  }
  
  .ui_search.ui_search_fixed {
    top: 0 !important;   
  }

  .submit_post {
    padding: 5px 20px;
  }
}
