css:
@-moz-document domain("smash.gg") {
/*
/* made by @flwns 8/18/2016
/*




/*********************/
/* Forms and stuff
/*********************/

.gg-form-info-title h2{
	color: white !important;    
}

.tourney-admin-display-panel, .gg-form-box-section, .gg-form-box, .reg-option-product-group-item, .product-reg-option, .filters-container, .pagination, .flyout-body, .flyout-section, .summit-player-table, .gg-padded-box, .s4c-team-list, .well, .list-group-item, .list-group-item-heading, .list-group-item:after, .shadow-box{
	color: white !important;
	background-color: #2D2D2D !important;
}

.shadow-box div {
    color: white !important;
    /* background-color: #2D2D2D !important; */
}

.level-card-footer, .gg-well, .alert, .gg-list-group-item:hover, .clickable-row:hover, .roster-spot:hover{
	background-color: #3C3C3C !important;
    border-color: black;
}

.page-section, #summit-voting, .fantasy-roster-edit, .tourney-sm, #content{
 	background-color: #191919 !important;   
}

.gg-card{
    background-color: #2D2D2D !important;
	color: #FFFFFF !important;
    border-radius: 5px;
    box-shadow: 0px 0px 0px 2px rgba(0, 0, 0, 0.2);
}

.page-section-heading{
 	color:white;   
}

.PageHeading__container {
 background-color: #2D2D2D;   
}

.PageHeading__container::before{
 background-color: #2D2D2D !important;   
}

/*********************/
/* Account and Account sidebar
/*********************/

.account-flyout-header-bottom{
	color: white !important;
	background-color: #2D2D2D !important;     
}

.tourney-list-group-item{
	background-color: #2D2D2D !important;
	border-bottom: 1px solid white;     
}

.profile-settings-nav, .profile-settings-nav a{
	background-color: #191919 !important;

}
.profile-settings-nav-sidenav, .profile-settings, .profile-settings-nav ul{
	background-color: #191919 !important;

}
 .title{
	color: white !important;    
}

.tournament-list{
	color: white !important;
	background-color: #2D2D2D !important;     
}


/*********************/
/* Brackets 
/*********************/

.match-section{
  border: 1px solid #484848 !important;   
}

.match-affix-wrapper span, .match-player, .entrant, .placeholder{
	color: white !important;
	background-color: #2D2D2D !important;    
}

.rankings-list-inline{
	background-color: rgb(0,0,0,0);   
}

.results-header{
	background-color: #2D2D2D !important;
	border: 1px solid #484848;    
}

.match-player-stocks{
    background-color: #484848 !important;
}

.match-player.entrant.winner .match-player-info .match-player-stocks{
    color: white;
    background: #22B24C !important;
}

.match-character.loser img{
    opacity: 0.666 !important;
}

.match-character-divider{
    background: white !important;
    border: none !important;
    width: 1.5px !important;
    left: -5.5px !important;
}

.match-affix-wrapper .match-label .fa-caret-right, .match-affix-wrapper .match-label .MatchSummary__arrow--right {
    position: absolute;
    right: -6px;
    color: #ccc;
    font-size: 21px;
    top: -50%;
    bottom: 0;
    margin: auto;
    height: 13.5px;
}

.NavBar {
   background-color: #2D2D2D;    
   border-radius: 0;
}

.NavBar__title *{
    color: white !important;
}

.NavBar__filter.active {
    color: #dcdcdc;
}

.NavBar__scroller{
    background: #292929;
    border-radius: 0;
    border: 1px solid black;

}

.NavBar__scroller *{
	color: #E0E0E0;
}

.NavBar__titleOptions .NavBar__option.active {
    background: #494949;
    color: #4d4d4d;
}
/*********************/
/* Events 
/*********************/

.EventCard{
   background-color: #2D2D2D;	
}

.EventCard__information .CardHeading__title{
	color: #ffffff;
}

.EventCard__information .CardHeading__status{
	color: #a9a9a9;
}

.EventCard__information .CardHeading__detail{
	color: #ffffff;
}

.PhaseInfo__title{
   color: #E0E0E0;
}

.EventCard__content .Attendee__placement{
    background: transparent !important;
    color: white;
}

.event-matches-set{
    background-color: #191919 !important; 
}

.MatchCard{
    background-color: #2D2D2D;
    border-radius: 0 !important;
    overflow: hidden;
}

.MatchRecap__score--loser {
    font-weight: bold;
    color: gray;
}

.flex-box.flex-spread, .nominated-player-card .flex-spread.heading-flex {
	background-color: transparent;
}
/*********************/
/* Round robin Pools 
/*********************/

.public-group-seed-row{
	color: white !important;
	background-color: #2D2D2D !important; 	
    border: 1px solid #484848;
}

.public-group-seed-header h5{
    color: white;
}

.public-group-seed-row span{
    color: #E0E0E0 !important;        
}

.rr-match, .blank{
	color: white !important;
	background-color: #2D2D2D !important; 	  
    border: 2px solid #484848;
}

.rr-row-total{
	color: white !important;
	border-bottom: 1px solid white;    
}

.rr-row-total span{
	color: white !important;
  
}

.rr-row-label{
	color: white !important;
	border-bottom: 1px solid white;    
}

.rr-column-label{
	color: white !important;
	border-right: 1px solid white;    
} 

.progression a, .progress-to-link a{
    color: #CB333B !important;
}


/*********************/
/* 		Tables		 
/*********************/

.table, .table-responsive, .table-filter, .well-dark, .fantasy-reg-steps, .avatar-lg{
	color: #E0E0E0;
	background-color: #2D2D2D !important;
}

.table-responsive a{
	background-color: none !important;
}

.table-responsive a:hover{
    color: #CB333B;
    
}

.fa-twitch:before{
	color: #6441A5;   
}


/*********************/
/*  links under the	players	 
/*********************/
.text-social-twitter i{
	color: #27ACED !important;      
}

.fa-twitch:before {
    color: #8949ff;
}

/*.additional-player-info ul{
    background-color: none !important;
	color: #FFFFFF !important;
    border-radius: 3px;
    box-shadow: 0px 0px 0px 2px rgba(0, 0, 0, 0.2);    
}*/

/*********************/
/*  Summit voting fixes
/*********************/

#invited-players > .container-lg > div .col-sm-3{
    background-color: transparent; 
    border-radius: 0px;
    border: none;
}

.modal-open .modal, .modal-backdrop {
    background-color: rgba(0, 0, 0, 0.8);
}

.modal-header{
    color: #E0E0E0 !important;
	background-color: #2D2D2D;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
}

.modal-body .text-center{
	color: #E0E0E0 !important;
    border-bottom-left-radius: 6px;
    border-bottom-right-radius: 6px;
}

.modal-body{
	background-color: #2D2D2D;
	border-radius: 6px;
}
    
.modal-content{
    background-color: #2D2D2D;
    border: 1.7px solid #CB3234;
    box-shadow: 0px 0px 122px -30px #CB3234;
}

.modal-body .text-center img{
    background-color: white;
    border-radius: 4px;
}

/*********************/
/*  Tournament Event card
/*********************/

.TournamentEventCard{
	background-color: #2D2D2D;
}

.PhaseList--clickable .Phase:hover{
 	background-color: #191919;
   
}

.PlayerList--clickable:hover{
 	background-color: #191919;
    
}

.PlacementList{
     margin-top: .5em;   
}

/*********************/
/*  Tournament card (used on the new browsing pages)
/*********************/


.TournamentCard {
 	background-color: #2D2D2D;
	color: #E0E0E0;
    border-radius: 3px;
    border: 1px solid black
}

.TournamentCardHeading{
	color: #E0E0E0 !important;
}


.TournamentCardIcon{
    background-color: transparent !important;
}

/*********************/
/*  New Tourney/results browsing page
/*********************/

.TopLevelFilterBackground--schedule{
        background-color: #2D2D2D;
    	border-bottom: 2px solid #5fbd47;    
}

.DrawerCategory--schedule .fa-calendar:before {
    color: #5fbd47;
}

/* "Tournaments" */
.DrawerCategory--schedule .active .DrawerNode__title, .active .DrawerCategory--schedule .DrawerNode__indicator{
    color: #5fbd47;
}

.DrawerCategory--schedule:hover, .DrawerCategory--schedule:active {
    color: #5fbd47;
}

.DrawerCategory--schedule.active{
	border-left-color: #5fbd47;
    color: #5fbd47;
}

/* Results */

.TopLevelFilterBackground--results{
        background-color: #2D2D2D;
    	border-bottom: 2px solid #efe149;      
}

.fa-trophy:before {
    color: #efe149;
}

.DrawerCategory--results .active .DrawerNode__title, .DrawerCategory--results .active .DrawerNode__indicator{
    color: #efe149;
}

.DrawerCategory--results:hover, .DrawerCategory--results:active {
    color: #efe149;
}

.DrawerCategory--results.active {
    border-left-color: #efe149;
    color: #efe149;
}


.ResultsCard .Card__Content, .ResultsCard .Card__footer{
	background-color: #2D2D2D;
    color: #E0E0E0;
}

.Card__Content .PlacementList__placement span{   
	color: #E0E0E0;
}

.ResultsCard .ResultsCardHeading--empty{
	background: #2D2D2D;  
    color: white;
}

/* Rankings */

.TopLevelFilterBackground--rankings{
        background-color: #2D2D2D;
    	border-bottom: 2px solid #7db5cd;      
}

.fa-rocket:before {
    color: #7db5cd;
}

/* Shops */

.TopLevelFilterBackground--shops{
        background-color: #2D2D2D;
    	border-bottom: 2px solid #d2545b;      
}

.fa-gift:before {
    color: #d2545b;
}

.DrawerCategory--shops .active .DrawerNode__title, .DrawerCategory--shops .active .DrawerNode__indicator{
    color: #d2545b;
}

.DrawerCategory--shops:hover, .DrawerCategory--shops:active {
    color: #d2545b;
}

.DrawerCategory--shops.active {
    border-left-color: #d2545b;
    color: #d2545b;
}

.ShopCard {
    background-color: #2D2D2D;
    color: #E0E0E0;
}

.ShopLevelProgress__footer{
    background-color: #2D2D2D;
    color: #E0E0E0;
    border: 1px solid #E0E0E0;
}

.ShopCard .ObjectCard__row a, .ShopCard .ObjectCard__row--featured a{
	color: #E0E0E0;   
}

.ShopCard .CardFooting, .ShopCard .CardFooting--bordered{
    
}

/* Fantasy */

.TopLevelFilterBackground--fantasy{
        background-color: #2D2D2D;
    	border-bottom: 2px solid #b981f7;      
}

.fa-book:before {
    color: #b981f7;
}

.DrawerCategory--fantasy .active .DrawerNode__title, .DrawerCategory--fantasy .active .DrawerNode__indicator{
    color: #b981f7;
}

.DrawerCategory--fantasy:hover, .DrawerCategory--fantasy:active {
    color: #b981f7;
}

.DrawerCategory--fantasy.active {
    border-left-color: #b981f7;
    color: #b981f7;
}

.FantasyCard {
    background-color: #2D2D2D;
    color: #E0E0E0;
}

/* Pool Cards */

.PoolCard, .PoolCard--clickable{
	color: #E0E0E0;
    background-color: #2D2D2D;   
}

 .PoolCard .List .SeedRow{
	color: #E0E0E0;
    background-color: #2D2D2D;
	border-bottom: 1px solid #4c4c4c;
}

/*********************/
/*  other stuff i didnt feel like organizing 
/*********************/
label, li, li.active, p, strong, h1, h2, h3, h4, .gg-card-body, .text-xlg, .tournament-info, .tourney, .page-nav, .tourney-details-affix, a.navbar-nav, .tmg-team-list-left{
    color: #E0E0E0;
}

.gamertag-title, .gamertag-title-lg, .gamertag-title-large, .gamertag-title, .avatar-body{
 	background: none !important;   
}

.gamertag-title-large, .gamertag-title{
    color: #E0E0E0;
}

.gamertag-title span, .gamertag-title-large span{
	color: #E0E0E0; 
}

.active .title, .text-bold, .page-section-tabs{
    color: #CB333B;
}

.page-nav {
	color: #E0E0E0 !important;
	background-color: #2D2D2D;
}

.help-block, .prefix, .text-muted{
	color: #B4B4B4;
}

.col-sm-3, .flex-box{
	background-color: #2D2D2D;
    border-radius: 10px;
    border: none;
}

.btn-social-twitter{
	background-color: #0087ba !important;    
}

.btn-social-twitter:hover{
	background-color: #19bbf9 !important;    
}

.btn-social-twitter i {
	color: white;
}

.btn-white.btn-outline:hover, .btn-lg:hover{
	background-color: #2D2D2D;	   
}

.btn-white{
    color: white;
    background-color: rgba(45,45,45,.55);
}

.btn-success.btn-outline{
    color: white;
    background-color: #22b24c;
}

.btn-success.btn-outline:hover{
	color: white;
    background-color: #00ff4a;   
}

.gg-carousel-info .btn-lg:hover, .gg-carousel-info .btn-primary:hover{
    color: #CB333B;
}

.gg-list-group-item-heading a, .has-dropdown a, .flyout-section a{
	color: white !important;

}

.flyout-section a:hover{
    color: #CB333B !important;
    
}

.header-tab-dropdown, .flyout-section{
    border-radius: 3px;
}

.Select-placeholder, .Select-input input{
    background-color: #2D2D2D !important;    
}

.pagination span, .pagination li, .pagination ul{
 	color: white;
	background-color: #484848 !important;
    border-color: black !important;
}

.pagination span:hover{
   	background-color: #CB333B !important;
}

.tourney-subnav a, .total{
    color: #E0E0E0 !important;    
}

.tourney-details-affix-title a, .tourney-details-affix{
	color: white !important;
}

.live-details-box{
    background-color: #5F5F5F !important;    
}

.match-station{
    top: 0px;
    right: 0px;
    height: 47px;
    width: 40px;
    border-radius: 2px;	
}

.live-details-box-titles a, .public-placement{
	color: white; 
}

.match-timer span, .stream-name, .match-timer-spacer {
    background-color: transparent !important;
    color: #2D2D2D !important;
    font-weight: bold;
}

.live-details{
	background-color: #191919 !important;
}

.alert{
    background-color: #1f1f1f !important;
    border-color: #cb333b;   
}

.alert-success{
    color: #cb333b;
}

.donation-message .gamertag h3{
	color: #CB333B;   
}

.donation-message .gamertag list-inline{
	color: white;   
}

.markdown-container table{
	color: white;   
}

.gg-card .gg-card-banner.gg-card-banner-grey{
    background-color: #2D2D2D;
}

.text-muted-dark{
	color: #E0E0E0;   
}

.gg-card.order-card .gg-card-heading, .gg-card.order-card .gg-card-footer{
    color: #E0E0E0;
    background-color: #2D2D2D;    
}

.gg-card.order-card .right-spacer-lg a{
    color: #E0E0E0;
}

body{
	background-color: #191919;
}

.profileHeader, .profileNavTrack .tabletUpNavTrack {
    background: #191919;
}

.navBanner, .profileNavContainer, .profileNavContainer .navContPadding, .gg-component-reset, .tabletUpNavTrack {
    background: #191919 !important;
}

.featureGroupHeader{
    color: #E0E0E0 !important;
}

.featureList .featureListItemLink, .featureListItem a.active, .profileLayout .markdown-container p, .profileLayout .page-section-heading{
    color: #E0E0E0 !important;
}

.featureList .featureListItemLink:hover, .featureListItem .subItem a:hover, .fa .fa-calendar-o .fa-fw, .subItems .subItem.selected a{
	color: #CB333B !important;
}

.gg-color-blue {
    color: #CB333B !important;
}

.gg-color-black {
    color: #E0E0E0;
}

.TournamentEventCardFooter--dark {
    background: #2d2d2d;
    border-top: 1px solid white;
}

.StatSummaryHeading {
    background: #2d2d2d;
}

.CardFooting--bordered {
    border-top: 1px solid #f2f2f2;
        background: #2d2d2d;
}

.sggKmg8h, .tappable-component {
    background: #2d2d2d !important;
}

.sgg15Vs9 table {
    background: #2d2d2d !important;
    color: #e0e0e0 !important;
}
}
