css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com") {
  body {
    background-color: #fff;
/*     background-image: url('https://i.ytimg.com/vi/0iw78YXaMkg/maxresdefault.jpg') no-repeat; */
  }
  .page_block {
    margin: 8px 0 0;
  }
  .side_bar_inner {
    margin: 6px 0 0;
  }
  .page_block, .im-page .im-page--toolsw {
    background-color: #edeef0;
  }
  .post_field_warp {
    margin-left: 60px;
  }
  .post_info {
    background-color: #f7f8fa;
  }
  .wall_module .post_header {
    padding: 8px 20px 8px;
  }
  .post_field_wrap, .reply_field_wrap {
    margin-left: 60px;
  }
  .button_gray button, .flat_button.secondary {
      background-color: #5e81a8;
      color: #fff;
  }
  .button_gray button:hover, .flat_button.secondary:hover {
      background-color: #fff;
      color: #5e81a8;
  }
  .page_actions_dd_label {
    color: #edeef0;
  }
  .page_actions_dd_label:hover {
    color: #5e81a8;
  }
  .page_top {
    padding-bottom: 5px;
  }
  .page_name {
    color: #31537a;
    font-size: 14px;
    font-weight: bold;
  }
  .profile_info_row {
    padding: 0px;
  }
  .profile_more_info_link {
    margin: 0px 0 -5px;
  }
  .page_counter {
    padding: 2px 15px;
  }
  .page_photos_module {
    padding: 7px 20px 20px;
    height: 80px;
  }
  .page_square_photo {
    width: 123px;
    height: 93px;
  }
  .module_header {
    background-color: #dcdfe6;
  }
  .module_header .header_top, .header_right_link {
    color: #31537a;
    font-weight: bold;
    line-height: 31px;
    padding: 0 5px;
  }
  .header_right_link {
    color: #acb3bf;
    font-weight: normal;
  }
  .feed_has_new .feed_row ~ .feed_row .page_block {
    margin: 10px 0 0;
  }
  .wall_module .wall_text {
    padding: 2px 20px 7px;
  }
  .wall_module .wall_text .page_media_thumbed_link:last-child, .wall_module .wall_text .page_media_poll_wrap:last-child {
    margin-bottom: 2px;
  }
  .post_full_like_wrap {
    padding: 2px 0 4px;
  }
}
