css:
@-moz-document domain("vk.com") {
body{
	background-color: #FFF;
}
.page_block{
    box-shadow: 0 1px 0 0 #ddd, 0 0 0 1px #ddd;
}
.im_grid img, .people_cell_img, .post_field_user_image, .post_img, .copy_post_img, .cell_img, .thumb, .reply_img, .fans_fan_img, .ow_ava, .ow_ava_small, .friends_photo_img, .nim-peer--photo-w, .group_row_photo, .group_row_img, .right_list_img, .apps_feed_user_photo img, .ui_zoom_inner, .search_item_img, .ts_contact_img, .feedback_img{
    border-radius: 2px !important;
}
.event:after {
    bottom: 5%;
    right: 5%;
}
.ui_zoom_wrap, .friends_photo, .friends_photo_wrap {
	overflow: visible !important;   
}

.online:after {
    bottom: -5% !important;
    right: -5% !important;
	border: 2px solid #fff !important;
}

.module_body, .ui_tab {
    font-size: 12px;
}
#page_body {
    font-size: 12px;
}
.left_label {
    font-size: 12px !important;
}

#feed_filters {
    display: none;   
}

.post_full_like{
	float: right;   
}
.reply_link_wrap{
 	float: left !important;   
}
#stl_side{
 	display: none !important;   
}
.post_like, .post_share {
    float: right !important;
}
.post_like{   
    margin-right: 0px;
}
.flat_button {
    background-color: #507299 !important;
    color: #ffffff !important;
}
.flat_button span{
    color: #ffffff !important;
}
.top_nav_link {
    font-weight: 500;
}
.feedback_row_clickable div a .feedback_photo_icon {
    top: 28px !important;
    left: 28px !important;
}
#feed_rows .feed_row .feedback_row_wrap .feedback_row .feedback_image .feedback_photo_icon {
    top: 37px !important;
    left: 37px !important;
}
}
