css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?/inputtools/.*") /* url-prefix("https://www.google.com/inputtools/") */ {
  
  /* Bkgd */
  body, html {
    background-color: #161616;
    color: #6B6B6B;
  }
  
  /* Header */
  #maia-header {
    background: #161616;
    border-bottom: none;
  }
  
  /* Top, right Google + button */
/*   .FP {
    background-color: #161616;
  } */
  
  /* Below Header Pane */
  #maia-nav-x {
    background: #333333;
  }
  
  /* Toolbar */
  .editor-toolbar {
    background-color: #2B2B2B;
    border-bottom-color: #444444;
  }
  
  .editor {
    border-color: #444444;
  }
  
  /* Language button */
  .goog-menu-button {
    background-color: #444444;
    background-image: none;
    color: #aaaaaa;
  }
  
  .goog-menu-button-outer-box, .goog-menu-button-inner-box {
    border-color: #444444;
  }
  
  /* Keyboard button */
  .ita-kd-icon-button {
    filter: invert(80%);
    color: #aaaaaa;
  }
  
  .ita-kd-dropdown-menu {
    background-color: #444444;
  }
  
  .ita-kd-menuitem {
    color: #aaaaaa;
  }
  
  .ita-kd-menuitem-hover {
    background-color: #595959 !important;
    color: #d0d0d0 !important;
  }
  
  .ita-kd-menuitem-inputtool-icon, .ita-kd-menuitem.ita-kd-selected .ita-kd-checkbox {
    filter: invert(97%);
  }
  
  /** Language dropdown **/
  .goog-menu {
    background-color: #444444;
  }
  
  /* normal language option */
  #gt-sl-menu tr {
    background-color: #444444;
  }
  
  .goog-menuitem {
    color: #aaaaaa;
  }
  
  /* hover langauge option */
  .goog-menuitem.goog-menuitem-highlight {
    background-color: #444444;
  }
  
  .goog-menuitem.goog-menuitem-highlight .goog-menuitem-content {
    color: #aaaaaa;
  }
  
  /* current language selection */
  #gt-sl-menu .goog-menuitem-group .goog-option-selected {
    background-color: #6a6a6a;
  }
  
  .goog-option-selected .goog-menuitem-content {
    color: #d0d0d0;
  }
  
  /* Real-time dropdown */
  .ita-ppe-box {
    background: #333333;
    color: #aaaaaa;
    border-color: #3f3f3f;
  }
  
  .ita-ppe-hlt {
    background: #595959;
    color: #d0d0d0;
  }
  
  /* Chinese character drawing canvas */
  .ita-kd-img {
    filter: invert(80%);
  }
  
  .ita-hwt-ime {
    background-color: #333333;
  }
  
  .ita-hwt-candidates {
    background-color: #333333;
  }
  
  .ita-hwt-candidate {
    color: #aaaaaa;
  }
  
  .ita-hwt-candidate.ita-hwt-candidate-hover {
    background-color: #595959;
    color: #d0d0d0;
  }
  
  .ita-hwt-buttons {
    background-color: #333333;
  }
  
  .ita-hwt-jfk-standard {
    filter: invert(80%);
  }
  
  /* 'Special Characters' */
  .goog-flat-menu-button {
    background-color: #161616;
    filter: invert(80%);
  }
  
  /* Special Characters pop-up pane */
  .modal-dialog-title {
    color: #aaaaaa;
  }
  
  .ita-cp-whole-pad, .modal-dialog-title, .modal-dialog-content, .ita-cp-hwt {
    background-color: #333333;
  }
  
  .goog-char-picker-grid .goog-flat-button {
    filter: invert(80%);
  }
  
  .goog-char-picker-hovercard {
    background-color: #333333;
    border-color: #444444 !important;
    color: #aaaaaa;
  }
  
  .goog-char-picker-unicode {
    color: #aaaaaa;
  }
  
  .goog-char-picker-menu .goog-menuitem {
    color: #838383;
  }
  
  .ita-cp-lens { /* Search */
    filter: invert(80%);
  }
  
  /* 'Install the Chrome extension', 'Get it on Google Play' */
  .maia-button.maia-button-secondary {
    background-color: #161616;
    background-image: none;
    text-shadow: none;
    border-color: #333333;
  }
  
  /* Footer */
  #maia-footer-local, #maia-footer {
    background-color: #161616;
  }
  
  /* Overlay virtual keyboard */
  .vk-box {
    background-color: #161616;
  }
  
  .vk-btn {
    background-color: #161616;
    border-color: #d0d0d0;
    background-image: none;
    color: #d0d0d0;
  }
  
  .vk-cap.vk-sf-b {
    filter: invert(97%);
  }
  
  .vk-t-btn {
    filter: invert(97%);
  }
  
  /*** Other InputTools Pages ***/
  
  /* On Chrome */
  .maia-promo, .maia-aside {
    background-color: #161616;
  }
  
  /* On Google Services */
  .maia-article img {
    filter: invert(80%);
  }
  
}
