css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bt.dk") {

/* Bookmark + Share + Facebook*/
*[class*="addthis last"],
.FBConnectButton,
iframe[src*="facebook"],
*[class*="fb_share"],
li[class*="share"],
*[src*="del-artikel-p-facebook"]
{display:none!important}

/* Centering page */
body.vinkel-none {
margin: auto !important; }

.bt-content {
margin: auto !important; }


/* Site improvements */
.deck-logo {
border-bottom: none !important; 
padding: 5px 0 5px !important; }

.deck-primarymenu {
border-top: none !important; }

#top_banner,
#red_right_repeat,
#red_right_bottom
{display:none!important}

#sitemap {
padding-right: 0px !important; }
/* Gamle layout */

body {
cursor: default !important;
background-image: none !important;
background-color: #FFFFFF !important;
background-repeat: no-repeat !important; }

/* Reklamer */
*[id*="AT_"],
*[class*="AT_"],
*[class*="BSFlashAd"],
*[id*="ADFBannerPlace"],
*[id*="ADFExpand"],
*[id*="AT_FLASH"],
*[id*="FlashID"],
*[src*="adtech"],
*[src*="adform"],
*[src*="adserver"],
*[action*="adtech"],
*[action*="adform"],
*[href*="adtech"],
*[href*="adform"],
*[id*="nordicswf"],
*[src*="gallup"],
*[src*="gemius"],
*[src*="statcamp"],
*[id*="quad_ad"],
*[src*="doubleclick"],
*[src*="2mdn.net"],
*[data*="emediate"],
*[src*="emediate"],
#google_ads_frame2,
#promoted-articles,
#ncwrap,
#_atssh,
#linkback,
#mini-panel-bt_newsletter_subsribtion,
.bt-proev-avisen,
.bt-ad-label,
.jobzonen,
.jobzonenhead,
.jobzonencontent,
.jobzonen-300,
.ADS01,
.SEC02,
.CTYPE,
.app-teaser,
*[href*="www.univers.dk/tl"],
*[class*="banner"],
*[url*="aka-cdn-ns.adtech.de"],
#laesershoptilbud,
iframe[id*="rnd"]
{display:none!important}

/* Tip BT */
#mini-panel-bt_tip_small_teaser,
*[id*="tip_bt_teaser"],
a[href="/tip"],
.divider-tip
{display:none!important}

/* Breaking News */
.sprite
{display:none!important}

}
