css:
@-moz-document url(http://phpfiddle.org/) {
/* 
 * Name:    PhpFiddle Dark
 * Author:  Jeff Russ
 * Version: 0.1
 */


/* so vertically stacked toolbar on left doesn't get covered */
#main_tabs { margin-left:30px!important; }

/* font awesome */
.fa {  color: #ac8f39 !important; }

/* outermost darkest areas */
[class^="layout_"],
[class^="basic"],
[class^="ui-widget-"],
[class^="ui-widget-"] > *,
#eetree, .jqueryFileTree > li > a,
#file_put {
    background-color: #222 !important;
    border-color: #444 !important;
    color: #ac8f39 !important;
}
/* more darkest - MUST BE SEPARATE */
.ui-widget-header,
.layout_header,
#resource_form + div,
.ui-state-default, 
.ui-state-default > a,
.ui-widget-content .ui-state-default, 
.ui-widget-header .ui-state-default,
select.rounded {
    background-image: none !important;
    background-color: #444 !important;
    border-color: #666 !important;
    color: #dc9 !important;
}



/* syntax highlighting based on lesser-dark.css theme */
.CodeMirror {
    line-height: 1.3em;
    background-color: #262626 !important;
    color: #EBEFE7 !important;
    text-shadow: 0 -1px 1px #262626 !important; 
}
 div.CodeMirror-selected {
    background-color: #45443B !important; 
} 
.CodeMirror-line::selection,
.CodeMirror-line > span::selection,
.CodeMirror-line > span > span::selection,
.CodeMirror-line::-moz-selection,
.CodeMirror-line > span::-moz-selection,
.CodeMirror-line > span > span::-moz-selection{
    background-color: rgba(69, 68, 59, .99) !important;
}
 .CodeMirror-cursor {
    border-left: 1px solid white !important;
}
 pre { padding: 0 8px !important; } /*editable code holder*/

.CodeMirror span.CodeMirror-matchingbracket {
    color: #7EFC7E !important;
}
.CodeMirror-gutters {
    background-color: #262626 !important;
    border-right:1px solid #aaa !important;
}
.CodeMirror-guttermarker { color: #599eff !important; }
.CodeMirror-guttermarker-subtle { color: #777 !important; }
.CodeMirror-linenumber { color: #777 !important; }

span.cm-header { color: #a0a !important; }
span.cm-quote { color: #090 !important; }
span.cm-keyword { color: #599eff !important; }
span.cm-atom { color: #C2B470 !important; }
span.cm-number { color: #B35E4D !important; }
span.cm-def { color: white !important; }
span.cm-variable { color:#D9BF8C !important; }
span.cm-variable-2 { color: #669199 !important; }
span.cm-variable-3 { color: white !important; }
span.cm-property { color: #92A75C !important; }
span.cm-operator { color: #92A75C !important; }
span.cm-comment { color: #666 !important; }
span.cm-string { color: #BCD279 !important; }
span.cm-string-2 { color: #f50 !important; }
span.cm-meta { color: #738C73 !important; }
span.cm-qualifier { color: #555 !important; }
span.cm-builtin { color: #ff9e59 !important; }
span.cm-bracket { color: #EBEFE7 !important; }
span.cm-tag { color: #669199 !important; }
span.cm-attribute { color: #9C5208 !important; }
span.cm-hr { color: #999 !important; }
span.cm-link { color: #00c !important; }
span.cm-error { color: #9d1e15 !important; }

.CodeMirror-activeline-background { background-color: #3C3A3A !important; }
.CodeMirror-matchingbracket {
    outline:1px solid grey !important;
    color:white !important;
}

/* resets for output "Window" */
#document, iframe {
    margin: 0 !important;
    margin-bottom: 0 !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    margin-top: 0 !important;
    padding: 0 !important;
    padding-bottom: 0 !important;
    padding-left: 0 !important;
    padding-right: 0 !important;
    padding-top: 0 !important;  
    border: 0 !important; 
    border-style: none !important; 
    border-width: medium !important; 
    border-color: black !important; 
    border-bottom: 0 !important; 
    border-bottom-color: black !important; 
    border-bottom-left-radius: 0 !important; 
    border-bottom-right-radius: 0 !important; 
    border-bottom-style: none !important; 
    border-bottom-width: medium !important; 
    border-collapse: separate !important; 
    border-image: none !important; 
    border-left: 0 !important; 
    border-left-color: black !important; 
    border-left-style: none !important; 
    border-left-width: medium !important; 
    border-radius: 0 !important; 
    border-right: 0 !important; 
    border-right-color: black !important; 
    border-right-style: none !important; 
    border-right-width: medium !important; 
    border-spacing: 0 !important; 
    border-top: 0 !important; 
    border-top-color: black !important; 
    border-top-left-radius: 0 !important; 
    border-top-right-radius: 0 !important; 
    border-top-style: none !important; 
    border-top-width: medium !important; 
    background-color: white !important; 
    background-image: none !important; 
    background-repeat: repeat !important; 
    background-size: auto auto !important; 
    color: black !important;
    outline: black !important;
}
#document *, iframe * {
    margin: inherit !important;
    margin-bottom: inherit !important;
    margin-left: inherit !important;
    margin-right: inherit !important;
    margin-top: inherit !important;
    padding: inherit !important;
    padding-bottom: inherit !important;
    padding-left: inherit !important;
    padding-right: inherit !important;
    padding-top: inherit !important;  
    border: 0 !important;
    border-style: none !important;
    border-width: medium !important;
    border-color: black !important;
    border-bottom: 0 !important;
    border-bottom-color: inherit !important;
    border-bottom-left-radius: inherit !important;
    border-bottom-right-radius: inherit !important;
    border-bottom-style: none !important;
    border-bottom-width: medium;
    border-collapse: inherit !important;
    border-image: inherit !important;
    border-left: 0 !important;
    border-left-color: black !important;
    border-left-style: none !important;
    border-left-width: 0 !important;
    border-radius: inherit !important;
    border-right: 0 !important;
    border-right-color: inherit !important;
    border-right-style: none !important;
    border-right-width: 0 !important;
    border-spacing: inherit !important;
    border-top: 0 !important;
    border-top-color: inherit;
    border-top-left-radius: inherit !important;
    border-top-right-radius: inherit !important;
    border-top-style: none;
    border-top-width: medium;
    background-color: white !important;
    background-repeat: repeat !important;
    background-size: auto auto !important;
    color: inherit !important;
    outline: inherit !important;
}
}
