css:
@-moz-document url-prefix("https://vk.com") {
#side_bar {
    position: fixed;
    height: 90px;
    z-index: 50;
    background: #edeef0;
    left: 0;
    margin-left: auto !important;
    margin-right: auto !important;
    width: 100%
}

#side_bar .left_label {
    height: 0px;
    width: 0px;
}

#side_bar .left_icon {
    height: 25px;
}

#side_bar ol li .left_row {
    width: 32px;
    height: 32px;
    margin: auto !important;
}

#side_bar ol li {
    padding: 10px;
    margin: auto !important;
}

#side_bar ol {
    display: inline-flex;
    margin: auto !important;
}

.side_bar_inner {
    position: fixed !important;
    /*bottom: 0px !important;*/
    
    margin-top: 42px !important;
    z-index: 0;
    padding: 5px;
    margin-left: auto !important;
    margin-right: auto !important;
    width: 100% !important;
    left: 720px;
}

.left_settings {
    display: none;
}

#side_bar ol li:last-of-type {
    display: inline-flex;
}

#side_bar ol li:last-of-type .left_settings {
    float: none;
    width: 32px;
    height: 32px;
    margin: -1px 0 0 2px;
    display: block;
}

#ads_left,
.left_menu_nav_wrap {
    display: none !important;
    width: 0px;
    height: 0px;
}

#side_bar .more_div {
    display: none !important;
}

#page_body {
    margin-top: 85px !important;
}
/*Counter*/

.left_count_wrap {
    position: absolute;
    background: #b3bed0;
    border-radius: 20px;
    padding: 1px 4px;
    height: 17px !important;
    width: 12px !important;
    text-align: center;
    margin-left: 19px;
    margin-top: -4px;
    vertical-align: middle;
    z-index: 1;
}

#side_bar ol li .left_row:hover .left_count_wrap {
    padding: 1px 4px;
    margin-left: 19px;
    margin-top: -4px;
}
/*Counter ends*/
/*Page body width*/

#page_body {
    width: 911px !important;
}

#page_layout {
    width: 911px !important;
}
/*Page body width*/
/*Right block becomes smaller and goes aboe the news*/

.ui_rmenu,
#feed_filters > div.page_block:nth-of-type(1),
#friends_possible_block {
    width: 911px !important;
}

.ui_rmenu_item,
.ui_rmenu_subitem {
    display: inline-table !important;
    padding: 0 10px 0 10px !important;
}

.ui_rmenu_item_sel,
.ui_rmenu_item_sel:hover,
.ui_rmenu_subitem_sel:hover {
    border-bottom: 2px solid #6285af !important;
    border-left: 0px solid #6285af !important;
}

#ui_rmenu_news_list {
    display: inline-table !important;
}

.wide_column_left .narrow_column_wrap {
    float: none;
}

.wide_column_left .wide_column_wrap {
    margin-right: 0px !important;
}
/*Right block becomes smaller*/
/*REmoved recomended groups block*/

#feed_filters > div.page_block:nth-of-type(2) {
    display: none;
}
/*removed uselessnews sections*/

#ui_rmenu_recommended,
#ui_rmenu_search,
#ui_rmenu_articles
/*, #ui_rmenu_news */

{
    display: none !important;
}

.feed_lists_icon .ui_actions_menu_icons {
    display: none;
}

#friends_possible_block .right_list {
    display: inline-flex;
}
/*removed uselessnews sections*/
/*Image link in post fix*/

.page_media_link_img {
    height: auto;
}
/*Filter for news */

.feed_filters_wrap {
    display: -webkit-inline-box;
}

.feed_filters_wrap .checkbox:first-child {
    margin-top: 11px;
}

#feed_filters .right_list_header {
    display: none !important;
}

._feed_filter_row {
    border-right: 1px solid #e3e4e8 !important;
    padding: 0px 5px 0px 5px;
}

._feed_filter_row:last-of-type {
    border-right: none !important;
}
/*Audio player - friend list*/

._audio_additional_blocks_wrap._audio_additional_blocks_wrap_right {
    width: 911px;
}

.audio_friend {
    display: inline-flex;
    padding: 2px 5px;
}

.audio_friends_list {
    margin-left: 11px;
}

.audio_friend.audio_friend_selected {
    padding: 2px 5px;
    background-color: #e5ebf1;
    border-left: 0px solid #6285af !important;
}
/*Audio player - friend list*/
/*Videos - fix width of video rows*/

.videocat_row {
    width: 100%;
}

.video_item {
    width: 274px;
}

.video_item .video_item_thumb {
    width: 100%;
}

.videocat_featured_playlist {
    width: 426px;
}

.videocat_inline_player a,
.videocat_featured_big {
    width: 590px;
}

.videocat_featured_playlists {
    width: 100%;
}

.video_items_list {
    width: 100%;
}
/*Videos - fix width of video rows*/
/*Document page filter*/

.ui_rmenu {
    display: inline-flex;
}
/*Document page filter*/
/*Groups list page - filters*/

#groups_filters_wrap {
    width: 911px;
}

.right_list_row.clear_fix {
    display: inline-block;
}

.ui_rmenu_slider._ui_rmenu_slider {
    display: none;
}
/*Groups list page - filters*/
/*Group main page fix*/
}
