ul.nav.navbar-nav.navbar-right.pagination-block.visible-lg.visible-md.ready > li.dropdown > i{
	color: #9b43c6 !important;
}

div.btn-group[component="thread/sort"]{
	position: fixed !important;
	top: 250px;
	right: 50px;
	z-index: 1000 !important;
}

div.btn-group.action-bar{
	position: fixed !important;
	top: 400px;
	right: 50px;
	z-index: 1000 !important;
}

button[component="topic/mark-unread"]{
	position: fixed !important;
	top: 300px;
	right: 50px;
	z-index: 1000 !important;
}

button[component="topic/follow"]{
	position: fixed !important;
	top: 350px;
	right: 50px;
	z-index: 1000 !important;
}

button[component="topic/mark-read"]{
	position: fixed !important;
	top: 300px;
	right: 50px;
	z-index: 1000 !important;
}

div.btn-group.thread-tools{
	position: fixed !important;
	top: 200px;
	right: 50px;
	z-index: 1000 !important;
}

button.btn.btn-primary,
button.btn.btn-info {
	background-color: #9b43c6 !important;
	border-color: #9b43c6 !important;
}
button.btn.btn-info.dropdown-toggle {
	background-color: #a852d3 !important;
	border-color: #a852d3 !important;
}
a.link-fixed,
a.link-fixed > span.catagory-name {
	color: #fff !important;
}
div.cat > a {
	color: #777 !important;
}
li.row.clearfix {
	color: #888 !important;
	background-color: transparent !important;
}
div.category > ul.topic-list {
	background-color: transparent !important;
	border-bottom-color: transparent !important;
}
div.category > ul.topic-list > li > div,
div.category > ul.topic-list > li > div > h2 > a {
	color: #fff !important;
}
a.user-activity-link {
	color: #777 !important;
}
div.bootbox.modal.fade > div > div > div> div > h3,
div.bootbox.modal.fade > div > div > div> h4 {
	color: #999 !important;
}
i.fa.fa-rss-square {
	visibility: hidden !important;
}

/* Update notification top */

#main-nav::after {
	content: url(https://dev.gatt.space/galaxyVersion?update=NodeBeta1.0.6);
	position: fixed !important;
	left: 0px;
	top: 0px;
	z-index: -1;
}

/* Secret Rainbow Text */

.rb {
  -webkit-box-sizing: content-box;
  -moz-box-sizing: content-box;
  box-sizing: content-box;
  cursor: pointer;
  border: none;
  color: rgba(0, 0, 0, 0);
  -o-text-overflow: clip;
  text-overflow: clip;
 text-shadow: -3px 0 0 rgb(217,31,38) , -6px 0 0 rgb(226,91,14) , -9px 0 0 rgb(245,221,8) , -12px 0 0 rgb(5,148,68) , -15px 0 0 rgb(2,135,206) , -18px 0 0 rgb(4,77,145) , -21px 0 0 rgb(42,21,113) ;
  -webkit-transition: all 600ms cubic-bezier(0.68, -0.55, 0.265, 1.55);
  -moz-transition: all 600ms cubic-bezier(0.68, -0.55, 0.265, 1.55);
  -o-transition: all 600ms cubic-bezier(0.68, -0.55, 0.265, 1.55);
  transition: all 600ms cubic-bezier(0.68, -0.55, 0.265, 1.55);
}
}

@-moz-document url-prefix("https://forums.skunity.com/api/") {
body {
	color: #fff !important;
}
}

@-moz-document url-prefix("https://forums.skunity.com/chats") {
li[component="chat/recent/room"] {
	background-color: transparent !important;
	color: #fff !important;
	border-left: 0px solid;
	border-right: 0px solid;
	border-bottom: 0px solid;
	border-top: 0px;
	border-color: #eee;
}
li[component="chat/message"] {
	color: #fff !important;
	border-top: 0px !important;
}
}

@-moz-document url("https://forums.skunity.com/galaxyversion") {
div.container > div.alert.alert-danger {
	display: hidden !important;
	visibility: hidden !important;
	color: transparent !important;
}
div.container > div.alert.alert-danger::before {
	visibility: visible!important;
	content: url(https://dev.gatt.space/galaxyVersion) url(https://dev.gatt.space/galaxyVersion?myVersion=NodeBeta1.0.6) !important;
}
div.container > div.alert.alert-danger::after {
	visibility: visible!important;
	content: url(https://dev.gatt.space/galaxyVersion?changelog=true) !important;
}
}

@-moz-document url-prefix("https://forums.skunity.com/") {
/* Notification List */

ul.dropdown-menu > li > ul.notification-list > li.unread {
	color: #fff !important;
	background-color: #999 !important;
}
}

@-moz-document url-prefix("https://forums.skunity.com/topic/") {
/* Topic Body */

span.topic-title {
	color: #a852d3 !important;
}
span[itemprop="title"] {
	color: #9b43c6 !important;
}
span.human-readable-number {
	color: #fff !important;
}
div.post-signature {
	color: #999 !important;
}

blockquote{
	color: #fff !important;
}

.topic .posts > li {
	background-color: transparent !important;
	border-top: transparent !important;
}
.topic .posts > li > div.content > small.user > strong > a {
	color: #9b43c6 !important;
}
.topic .posts > li > div.content > ol{
	color: #fff !important;
}
.topic .posts > li > div.content > p {
	color: #fff !important;
}
.topic .posts > li > div.content > div {
	color: #fff !important;
}
.topic .posts > li > div.content > ul > li {
	color: #fff !important;
}
.topic .posts > li > div.content > h1,
.topic .posts > li > div.content > h2,
.topic .posts > li > div.content > h3,
.topic .posts > li > div.content > h4 {
	color: #fff !important;
}
.topic .posts > li > div.content > p > a.plugin-mentions-a {
	color: #31708f !important;
}
.topic .posts > li > div.content > div > a.plugin-mentions-a {
	color: #31708f !important;
}
small.pull-right {
	position: relative !important;
	left: 710px !important;
}
div.content > small.pull-right > span.votes > span {
	color: #9b43c6 !important;
	cursor: pointer;
}
span[component="post/vote-count"] {
	color: #a852d3 !important;
}
/* Reply Composer */
/* Mentioning Users */

ul.dropdown-menu.textcomplete-dropdown > li > a {
	color: #000 !important;
}
small.pull-right > span.post-tools > a {
	text-transform: capitalize;
}
}

@-moz-document url-prefix("https://forums.skunity.com/") {
/* Error Page */

div.error-page > div {
	color: #fff !important;
}
/* Pop-Ups */

div.period-popup > ul > li > a {
	color: #777 !important;
}
/* Users Page */

th.sortable {
	color: #999 !important;
}
}

@-moz-document url-prefix("https://forums.skunity.com/users") {
span.formatted-number {
	color: #fff !important;
}
}

@-moz-document url-prefix("https://forums.skunity.com/user/") {
h1.fullname,
h2.username,
div.account-stats > div.stat > div,
div.account-stats > div.stat > span {
	color: #fff !important;
}
div.text-center.profile-meta > span {
	color: #fff !important;
}
div.text-center.profile-meta > strong {
	color: #999 !important;
}
/* Posts */

ul[component="posts"] > li[component="post"] > div {
	background-color: transparent !important;
}
div.row > div.col-xs-12 > h1 {
	color: #fff !important;
	background-color: transparent !important;
}
ul.posts-list > li > div > a {
	color: #999 !important;
	background-color: transparent !important;
}
ul.posts-list > li > div > div.content {
	color: #fff !important;
	background-color: transparent !important;
}
ul.posts-list > li > div > small.topic-category {
	color: #888 !important;
	background-color: transparent !important;
}
/* Settings */

div.row>div.col-xs-12>h4 {
	color: #999 !important;
	background-color: transparent !important;
}
}

@-moz-document url("https://forums.skunity.com/(about|faq|tos|privacy)") {
table {
	color: #fff !important;
}
li > a.active {
	color: #fff !important;
	background-color: transparent !important;
}
section.about {
	color: #fff !important;
}
div.contents {
	color: #fff !important;
}
}

@-moz-document url-prefix("https://forums.skunity.com") {
/* Scroll Bar */

::-webkit-scrollbar {
	width: 10px;
	height: 5px;
}
::-webkit-scrollbar-button {
	width: 10px;
	height: 0px;
}
::-webkit-scrollbar-thumb {
	background: #c43dce;
	border: 1px solid #d736e2;
	border-radius: 50px;
}
::-webkit-scrollbar-thumb:hover {
	background: #c43dce;
}
::-webkit-scrollbar-thumb:active {
	background: #c43dce;
}
::-webkit-scrollbar-track {
	background: url(http://i.imgur.com/j6T1Hzy.gif)center 109px!important;
}
::-webkit-scrollbar-track:hover {
	background: url(http://i.imgur.com/j6T1Hzy.gif)center 109px!important;
}
::-webkit-scrollbar-track:active {
	background: url(http://i.imgur.com/j6T1Hzy.gif)center 109px!important;
}
::-webkit-scrollbar-corner {
	background: url(http://i.imgur.com/j6T1Hzy.gif)center 109px!important;
}
}
