css:
@-moz-document domain("www.bloxcity.com") {
nav[style="box-shadow:none;border-bottom: 1px solid #e8e8e8;"]{
color: white;
    
}
nav[style="box-shadow:none;border-bottom: 1px solid #e8e8e8;"] {
    background-image: url(http://i.imgur.com/apyZTxc.png)!important;
    background-repeat: repeat-x;
	background-color: rgb(220, 126, 207)!important;
    background-position: 1px 24px;
}
.white {
	background-color: rgba(255, 255, 255, 0) !important;
}
nav a, nav ul a {
    color: #fff!important;
}
body {
    	background: url(http://i.imgur.com/ZIivopW.png) top center repeat-x rgb(250, 206, 254)!important;
}
.site-footer {
    background: #facefe!important;
    color: #333!important;
}
div[style*="padding:4px 0;font-size:18px;color:white;font-weight:300;"]{
    color: #333!important;
}
/*.dropdown-content {
	background-color: rgb(220, 126, 207)!important;
    top: inherit!important;
    color: #333!important;
}
*/
.dropdown-content li:hover{
    background-color: rgba(0, 0, 0, 0.15)!important;
}
.blue, .red, .btn, .btn-large {
	background: rgb(220, 126, 207)!important;
}
.card-panel {
	background: #cb6fca!important;
}
div[style*="background:#1976d2;color:white;padding:15px 25px;"]{
	background: rgb(220, 126, 207)!important;
}
.side-menu-link, .side-menu-title {
	background: white!important;
    padding: 25px;
    box-shadow: 0 2px 2px rgba(0, 0, 0, 0.15)!important;
}
.side-menu-bar {
    margin-top: inherit!important;
    	background: white!important;
    padding-bottom: inherit!important;
}
.bc-content {
    box-shadow: 0 2px 2px rgba(0, 0, 0, 0.15)!important;
}
.pagination li.active{
	background: rgb(220, 126, 207)!important;
}
.right ul.dropdown-content {
    background-color: rgb(220, 126, 207)!important;
    top: inherit!important;
}
.dropdown-content{
    top: inherit!important;
}
input:not([type]):focus:not([readonly]),input[type=text]:focus:not([readonly]),input[type=password]:focus:not([readonly]),input[type=email]:focus:not([readonly]),input[type=url]:focus:not([readonly]),input[type=time]:focus:not([readonly]),input[type=date]:focus:not([readonly]),input[type=datetime]:focus:not([readonly]),input[type=datetime-local]:focus:not([readonly]),input[type=tel]:focus:not([readonly]),input[type=number]:focus:not([readonly]),input[type=search]:focus:not([readonly]),textarea.materialize-textarea:focus:not([readonly]) {
    border-bottom: 1px solid rgb(220, 126, 207)!important;
    box-shadow: 0 1px 0 0 rgb(220, 126, 207)!important;
}
input:not([type]):focus:not([readonly])+label,input[type=text]:focus:not([readonly])+label,input[type=password]:focus:not([readonly])+label,input[type=email]:focus:not([readonly])+label,input[type=url]:focus:not([readonly])+label,input[type=time]:focus:not([readonly])+label,input[type=date]:focus:not([readonly])+label,input[type=datetime]:focus:not([readonly])+label,input[type=datetime-local]:focus:not([readonly])+label,input[type=tel]:focus:not([readonly])+label,input[type=number]:focus:not([readonly])+label,input[type=search]:focus:not([readonly])+label,textarea.materialize-textarea:focus:not([readonly])+label {
    color: rgb(220, 126, 207)!important;
}
.side-menu-link:hover {
    background: #E0E0E0!important;
}
div[style*="padding:10px 0;font-size:14px;color:#666;"]{
background-color: white;
    padding: 15px!important;
    padding-top: 25px!important;
}
blockquote {
    border-left: 5px solid #DC7ECF;
}
}

@-moz-document url-prefix("https://www.bloxcity.com/forum/thread/"), url-prefix("https://www.bloxcity.com/forum/reply/") {
div[style*="padding:10px 0;font-size:14px;color:#666;"]{
	background-color: #DC7ECF!important;
    color: white!important;
    padding: 15px!important;
}
a[style*="color:#323a45;font-weight:600;"]{
    color:white!important;
    padding-top: 25px!important;
}
}
