css:
@-moz-document regexp(".*wikia.com\\/d.*") {
/* Hero */
.discussion-hero-unit {
  height:auto;
  text-align:left;
}

.discussion-hero-unit .discussion-hero-unit-content {
  padding:20px 30px 16px;
}

/* left rail */
.rail.left {
  margin-top:270px;
}
  
.community-badge-image, .post-details-view .community-badge .community-badge-image.community-badge-image-default, .post-list-view .community-badge .community-badge-image.community-badge-image-default,
.community-badge h2 {
  display:none;
}
.post-list-view .community-badge {
  margin:0;
}
.discussions-collapsable.discussion-user-activity-glossary {
  display:none;
}

/* right rail */
.discussion-community-unit .discussion-app-join-text,
.discussion-community-unit .apple-store-badge,
.discussion-community-unit .google-play-badge {
  display:none;
}
.post-list-view .rail:last-child,
[class*=column]+[class*=column]:last-child {
  position:absolute;
  padding:0;
  margin-top:16px;
  width:200px;
}
.actions {
  right:20px;
}
.discussion.forum {
  width:auto;
  margin:0;
}
.rail .discussion-contributors-title {
  padding-top:0;
}
.discussion-content-wrapper .rail:last-child {
  margin-top:120px;
}
.discussion.forum .post-detail, .discussion.forum .post-reply {
  border:0;
}
.post-details-view .discussion-community-unit {
  margin:0;
}
/* content */

.post-details-view .discussion {
  padding-left:50px;
}
@media only screen and (min-width: 1064px) {
.large-6 {
    width: 75%;
}
.discussion-inline-editor.is-sticky .discussion-inline-editor-floating-container,
.discussion.post .replies-list,
  .discussion.post .load-older,
.discussion.post .post-detail,
  .discussion.post .replies-list, .discussion.post .replies-list .post-reply,
  .post-details-view .discussion-inline-editor .discussion-inline-editor-floating-container, .post-details-view .discussion-inline-editor.is-sticky .discussion-inline-editor-floating-container {
    width: 785.729px;
}
}

@media only screen and (min-width: 1575px) {
.extra-large-6 {
    width: 80%;
}
.discussion-inline-editor.is-sticky .discussion-inline-editor-floating-container,
.discussion.post .replies-list,
  .discussion.post .load-older,
.discussion.post .post-detail,
  .discussion.post .replies-list, .discussion.post .replies-list .post-reply,
  .post-details-view .discussion-inline-editor .discussion-inline-editor-floating-container, .post-details-view .discussion-inline-editor.is-sticky .discussion-inline-editor-floating-container {
    width: 960.781px;
}
}

/* POSTS : Positioning */
.discussion.forum .post-detail,
.discussion-reported-posts .post-reply, .discussion-user-list .post-reply{
  margin-bottom:3px;
}
.discussion .side-spaced {
    padding:2px 5px;
}
.discussion .avatar img, .discussion .avatar svg {
  display:none;
}
.discussion .user-avatar .avatar-username, 
.discussion .post-detail .timestamp, .discussion.post .post-detail .post-category-name, .discussion.post .post-detail .post-counters,
.discussion .discussion-reply .user-avatar .avatar-username {
  font-size:12px;
}
.discussion .user-avatar .avatar-username,
.discussion .discussion-reply .user-avatar .avatar-username {
  display:inline-block;
  position:relative;
}
.post-detail .user-avatar-badge,
.post-details-view .post-detail .user-avatar-badge,
.post-reply .user-avatar-badge {
  display:inline-block;
  position:relative;
  top:0;
  left:0;
}
.discussion .avatar {
  display:none;
}

/* POSTS : Content */
.discussion.post .post-title,
.discussion .post-title {
  font-size:16px;
  margin:0;
}
.discussion .top-note {
  line-height:20px;
  text-indent:10px;
  height:20px;
}
.discussion.forum .post-detail .post-header {
  padding:0;
}
.discussion.post .post-detail .discussion-content,
.discussion .discussion-content,
.discussion .discussion-content .post-details-link {
  font-size:14px;
  line-height:130%;
}
.discussion.forum .clamp {
  overflow:auto;
  display:block;
}

/* DELETED posts */
.is-deleted .discussion-content,
.is-deleted .og-container {
  display:none;
}
.is-deleted:hover .discussion-content,
.is-deleted:hover .og-container {
  display:block;
}

/* POSTS : ACTION BUTTONS */
.discussion .post-bottom-row {
  margin-right:100px;
  padding-bottom:10px;
}
.post-details-view .post-actions,
.discussion .post-actions {
  position:absolute;
  margin-top:-40px;
  right:0;
  border:0;
}
.discussion .post-actions a, .discussion .post-actions li {
  width:30px;
  display:inline-block;
  font-size:0;
}
.discussion .post-actions>li:not(:last-child) {
  border:0;
}
/* POSTS : Badges tweak */
@media only screen and (min-width: 1064px) {
.post-list-view .post-reply .user-avatar-badge {
  left:0;
}
  
}
.post-details-view .post-detail.is-deleted .user-avatar-badge, .post-details-view .post-detail.is-locked .user-avatar-badge, .post-details-view .post-detail.is-reported .user-avatar-badge, .post-detail.is-deleted .user-avatar-badge, .post-detail.is-locked .user-avatar-badge, .post-detail.is-reported .user-avatar-badge, .post-list-view .post-reply .user-avatar-badge, .post-reply.is-deleted .user-avatar-badge, .post-reply.is-locked .user-avatar-badge, .post-reply.is-reported .user-avatar-badge {
  top:0;
}
}
