css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com") {
  body {
    /*background: #ffe7ff;*/
    /*background: linear-gradient(#d3c9fe, #ffe7ff);*/
    background: fixed linear-gradient(#ffe7ff 0%, #d3c9fe 100%);
  }
  #page_header_cont .back {
    /*background: none;*/
    background-color: rgba(255, 255, 255, 0.6);
    border-bottom: none;
  }
  input.text.ts_input {
    background: #fff url(https://vk.com/images/icons/dev_head_magglass.png) no-repeat;
    background-position: left 8px;
  }
  .top_nav_btn:hover, .top_nav_link:hover, .top_audio_player:hover {
    background-color: rgba(255, 255, 255, 0.8);
  }
  .page_block {
    box-shadow: 0px 0px 0px 1px #e7e8eb, 0 0 0 1px #f3f4f8;
    background-color: rgba(255, 255, 255, 0.6);
  }
  .counts_module {
    border-top: 1px solid #f7f8fc;
  }
  .page_top {
    border-bottom: 1px solid #f7f8fc;
  }
  .wall_module .copy_quote {
    border-left: 1px solid #dee6ee;
  }
  .friends_user_row, .friends_list_bl, .nim-dialog .nim-dialog--content {
    border-top: none;
  }
  .group_list_row {
    border-bottom: none;
  }
  .top_nav_link, .top_audio_player .top_audio_player_title {
    color: #8c80bf;
  } 
  .fakeinput, div[contenteditable="true"], input.big_text, input.dark, input.search, input.text, textarea {
    background: rgba(255, 255, 255, 0.5);
    border-radius: 5px !important;
  }
  .left_count_wrap, .submit_post, .audio_friends_list {
    background: none;
  }
  .im-mess.im-mess_unread, .im-page .im-page--dialogs-footer.ui_grey_block {
    background:  rgba(255, 255, 255, 0.6);
  }
  .im-page .im-page--center-empty, .ui_tabs_header {
    background: none;
  }
  .im-page .im-page--header, .im-page .im-page--search-header {
    background:  rgba(255, 255, 255, 0.3);
  }
  .ui_search {
    background-color: rgba(255, 255, 255, 0.3);
  }
  .im-page .im-page--history-new-bar {
    background: none;
  }
  .page_block_header, .audio_page_player, .im-page .im-page--history-new-bar > span {
    background-color: rgba(255, 255, 255, 0.6);
  }
  .im-page .im-page--top-date-bar-inner, .im-page.im-page_classic .im-page--header-inner {
    background-color: rgba(255, 255, 255, 0.8);
  }
  #side_bar ol li .left_row:hover {
    background: rgba(255, 255, 255, 0.3);
  }
  .nim-dialog.nim-dialog_hovered:not(.nim-dialog_deleted), .nim-dialog.nim-dialog_selected:not(.nim-dialog_deleted), .nim-dialog.nim-dialog_unread.nim-dialog_classic:not(.nim-dialog_deleted), .nim-dialog:hover:not(.nim-dialog_deleted) {
    background: rgba(255, 255, 255, 0.3);
  }
  .im-page.im-page_classic .im-page--dcontent, .im-page.im-page_classic .im-page--chat-body-wrap-inner {
    background: none;
  }
  .im-page.im-page_classic .im-page--chat-input {
    background: #ffeeff;
  }
  .im-page.im-page_classic .im-page--header-chat, .im-page.im-page_classic .im-page--header {
    border-top: solid 17px rgba(255, 255, 255, 0.8);
  }
  .im-mess.im-mess_selected, .im-mess.im-mess_selected:hover {
    background: rgba(255, 255, 255, 0.6);
  }
  .top_audio_player .top_audio_player_btn .top_audio_player_btn_icon {
    opacity: 1;
  }
  .stl_active.over_fast #stl_bg {
	  background-color: rgba(255, 255, 255, 0.6);
}
}
