/*
 * Dark IR intranet (dazzling green)
 *
 * Author	 : Sean Hamlin
 * Updated	 : January  5, 2009
 * Description	 : Modifies the colors, logos, and a few other images
 * Usage      	 : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://infoweb.ird.govt.nz/iris/) {

body {
  background: #262626 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAENCAYAAADOoA9GAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABZSURBVHjaYmBgYPjPACP+MYFIEPEflfUflwQRSojgUiyGX4IkJTTRi5P4R39T/tFByT8qa6OJof8GPJgGPjHQI/FTki+pWFCQXlQhsZiBmBFMwLlgwAgQYAAYPe0bQ81rlgAAAABJRU5ErkJggg==") top left repeat-x !important;
  color: #C6C6C6 !important;
}

/* defaults for all links */
a:link { color: #848484 !important; }
a:visited { color: #5F5F5F !important; }
a:active, .link:active { color: #fff !important; }

/* disable dashed outline on focused elements */
:focus { outline: none !important; }

/* Logo */
#irlogo a img { display:none !important; }
#irlogo a { width:228px !important;  height:56px !important; display:block !important; background-image: url("data:image/png;base64,") !important; }

/* Main Column */
#content-wrapper { background: #292929 url('data:image/png;base64,') repeat-y scroll top center !important; border-right:1px solid #2EA01F !important; border-left:1px solid #2EA01F !important; border-bottom:1px solid #2EA01F !important; }

#content-wrapper ul li:first-child { border-top:1px dotted #2EA01F !important; margin-top: 5px !important; }
#iris-featured-news ul li:first-child { border:none !important; }
#navigation ul li:first-child { border:none !important; }
#subsection-content ul li:first-child { border:none !important; }

/* Middle Column */
.column-middle { border-left:none !important; border-right:none !important; padding-left:6px !important; padding-right:6px !important; }
#iris-opinfo em { text-align:right !important; color:inherit !important; }

/* Main text color */
#iris-news li, #iris-tools li, #iris-links li, #iris-jobs li, #iris-opinfo li, #iris-pict li, #iris-media li { border-bottom:1px dotted #2EA01F !important; }
#iris-tools li a:hover, #iris-links li a:hover, #iris-opinfo li a:hover, #iris-pict li a:hover, #iris-featured-news li a:hover { background-color:#000 !important; }

/* Headings */
h1 { color: #fff !important; background-color: #292929 !important; }
h2 { font-size:2em !important; background-color: transparent !important; border-bottom:3px solid #596932 !important; color:#596932 !important; margin: 0 0 5px 8px !important; padding: 0 !important; width:auto !important; }
h3 { border-top:none !important; border-bottom:3px solid #596932 !important; color:#596932 !important; background:transparent !important; padding:0 !important;  margin: 0 0 0 8px !important; font-size:2em !important; font-weight:normal !important; }
#navigation-right h3 { height:auto !important; }
#date p { padding-left:100px !important; background-color: #292929 !important; }

/* Service Alerts */
#service-alert h4 { border-bottom:3px solid #AF2134 !important; border-top:none !important; margin:0 0 5px 0 !important; font-size:2em !important;}
.flag-active { height:auto !important; }
.flag-active .wrap { float:none !important; padding:0 !important; width: 338px !important; margin-left:27px !important;}
.flag-active .wrap p { margin:0 !important; padding: 10px !important; }
.flag-resolved { border-color:#5E9937 !important; height:auto !important; }
.flag-resolved .wrap { float:none !important; padding:0 !important; width: 338px !important; margin-left:27px !important;}
.flag-resolved .wrap p { margin:0 !important; padding: 10px !important; }

/* Menus */
#global-nav { background:#292929 url('data:image/gif;base64,') repeat scroll 0 0 !important; }
#global-nav li { border-right:none !important; }
#global-nav li ul { background-color: #292929 !important; }
#global-nav li:hover { background-color: #000 !important; }
#global-nav li.daddy { background: url('data:image/gif;base64,') no-repeat scroll 95% center !important; }
#global-nav .has_child { background: url('data:image/gif;base64,') no-repeat right center !important; }
#global-nav li ul { border-color:#2EA01F !important; }
#global-nav li li { border-color:#2EA01F !important; }

/* Text */
#date p { color: #fff !important; }
p { color:inherit !important; }
.example {  background-color: #5DA36F !important; }

/* ads */
.advert-top { display:none !important; }

/* Featured News */
#iris-featured-news img { border:1px solid #2EA01F !important; }
#skip-news p { float:none !important; margin-top:-60px !important; text-align:right !important; }




}
