css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://gingkoapp.com/app#") {

  body {
    background-image: none;
    background-color: #090909;
  }

  .navbar {
    background-color: #111;
  }

  .columns .blank {
    background: #111;
    border-left: none;
    border-right: none;
  }

  .cards {
    border-top: none;
    background-color: #222;
    color: #999;
  }

  .cards:last-child {
    border-bottom: none;
  }

  .card h1, .card h2, .card h3, .card h4, .card h5, .card h6 {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    line-height: 1.5;
    padding: 0;
  }

  .card h1 {
    font-size: 150%;
    font-weight: bold;
  }

  .cards.cards-active {
    background: #111911;
    color: #ccc;
  }

  .card.card-parent {
    background: #232;
    color: #ccc;
  }

  .card.card-active {
    background: #020;
    color: #eef;
  }

  .cards.cards-child {
    background: #111c11;
    color: #eef;
  }

  .card textarea {
    background: #090909;
    color: #fff;
  }

  .fullscreen-overlay {
    background: #090909;
    color: #eee;
  }

  .fullscreen-overlay textarea:focus, .fullscreen-overlay textarea:hover {
    background: #090909;
    color: #eee;
  }

}
