/*
	Gitter Dark by viluon
	https://github.com/viluon/gitter-dark
*/

@-moz-document domain(gitter.im) {
	/* Applies mainly to developer.gitter.im */
	body {
		font-family: "Open Sans";
		font-weight: 500;
		font-size: 1rem;
		line-height: 1.4;
		color: #ccc;
	}

	/* Code for Gitter.im chatrooms */

	/* the body */
	.fonts-loaded body {
		font-family: "Open Sans";
		font-weight: normal;
		font-size: 14.5px;
	}

	.chat-connectivity-indicator {
		background-color: rgb(29, 29, 29);
	}

	/* general input */
	input, button, select, textarea {
		font-family: "Open Sans";
	}

	input, textarea, select, .uneditable-input {
		display: inline-block;
		width: 210px;
		height: 20px;
		padding: 4px;
		margin-bottom: 9px;
		font-size: 15px;
		line-height: 20px;
		color: #393133;
		border: 1px solid #ccc;
		border-radius: 3px;
	}

	/* The header of the left panel */
	.panel-header__title--all {
		background-color:#233147;
	}

	/* The expand profile menu button */
	.panel-header__container--all {
		background-color:#233147;
	}

	/* The profile menu */
	.lm-profile-menu {
		background-image: none;
	}

	.room-list-item__icon img {
		background: transparent;
	}

	.chat-item__action {
		color: white;
	}

	.trpContentPanel, .chat-item:not(.unread) {
		background-color: rgb(29, 29, 29) !important;
	}

	.trpMoreBadge div {
		color: white;
	}

	h1,h2,h3,h4,h5,h6{ color: white !important; }

	#chat-container, .chat-item__text {
		color:#aaa;
	}

	/* This is the /me item */
	.chat-item__status .chat-item__text {
		color:#3664c9 !important;
	}

	.js-chat-name { color: white !important; }

	/*
		CHAT HEADER and surroundings
	*/
	.chat-header {
		background-color:#233147;
	}

	.chat-input__text-area {
		background-color: #1D1D1D;
		padding-left: 1em;
		color: #ccc;
		font-weight: 400;
		font-size: 17px;
		line-height: 20px;
		/*box-shadow: 0px 0px 5px #444;*/
	}

	.chat-input__text-area {
		display: block;
		width: 100%;
		min-height: 44px;
		margin: 0;
		padding: 0;
		-webkit-box-shadow: none;
		box-shadow: none;
		border: none;
		/* outline: none; */
		font-size: 1em;
		line-height: 1.38em;
		resize: none;
		border-radius: unset;
	}

	/* Markdown help button */
	.chat-input__button {
		color: white;
	}

	.chat-input__container {
		background-color: rgb(29, 29, 29) !important;
	}

	/* The wrapper for the textarea and associated UI */
	.chat-input {
		background-color: rgb(29, 29, 29) !important;
		border-top: 1px solid rgba(255, 255, 255, 0.38);
	}

	.right-toolbar {
		background-color: rgb(29, 29, 29);
		color:#aaa;
	}

	.right-toolbar.right-toolbar--collapsible {
		background-color: rgb(29, 29, 29);
		color:#aaa;
	}

	.chat-header__title {
		color:white;
		font-family: "Open Sans";
		font-weight: 200;
	}

	.chat-header__topic {
		color: white;
	}

	/* Left panel bg */
	.room-menu__panel::before {
		content: ' ';
		display: inline-block;
		position: absolute;
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
		background: #1D1D1D;
		background-image: none;
		background-position: bottom right;
		background-repeat: no-repeat;
		background-size: cover;
		z-index: -1;
	}

	/* left panel chat headings */
	.fonts-loaded .room-item__title {
		font-weight: 500;
	}

	/* left panel scroll box */
	.nano > .nano-pane {
		background: none;
	}

	.nano > .nano-pane > .nano-slider {
		background-color: #347fef;
	}

	/* Add to room button (at the right) and also the button in Markdown help */
	.button-caribbean--xsmall, .modal--default__footer__btn {
		background-color: #18345e;
	}

	/* See all people in the room (or details, when there isn't that many people) */
	.remaining-view-secondary-button,
	.remaining-view-button-secondary-text,
	.right-toolbar-region .remaining-view-secondary-button,
	.right-toolbar-region .remaining-view-button-secondary-text,
	.secondary-button-caribbean--xsmall {
		background-color: #18345e;
		color: white;
	}

	/* Disable the weird anim on the details button */
	.secondary-button-caribbean--xsmall::after {
		transform: none;
	}

	/* New room and apply changes in settings buttons */
	.button-caribbean--small, .modal--default__footer__btn--neutral {
		background-color: #18345e;
		color: white;
	}
	/* event versions */
	.button-caribbean--small:hover,
	.button-caribbean--small:focus,
	.button-caribbean--xsmall:hover,
	.button-caribbean--xsmall:focus,
	.chat-input__btn:focus,
	.chat-input__btn:hover {
		background-color: #ccc;
		color: #18345e;
	}

	/* footer behind the button */
	.panel-footer__item--all {
		background-image: none;
	}

	/* for Gitter NEXT */
	.staging-indicator {
		background-color: rgb(29, 29, 29) !important;
	}

	/* buttons */
	/*  Join Room button */
	.chat-input__btn {
		background-color:#18345e;
		color: white;
		transition: none;
	}
	/*  Settings */
	.chat-header__settings {
		background-color: transparent;
		color: white;
		transition: none;
	}

	/* left menu panel */
	.room-menu__panel {
		background-image: none;
	}

	/* emoji autocomplete */
	div.textcomplete-wrapper ul.dropdown-menu, ul.dropdown {
		background-color: rgb(29, 29, 29);
		color: white;
	}

	/* > bold on hover */
	div.textcomplete-wrapper ul.dropdown-menu li > a:hover {
		font-weight: 600;
	}

	/* > white */
	div.textcomplete-wrapper ul.dropdown-menu li a {
		color: white;
	}

	/* People list /*
	/* > online indicators */
	.trpDisplayPicture.offline .trpStatus {
		top: 4px;
		background-color: rgb(200, 107, 16);
		-webkit-box-shadow: none;
		box-shadow: none;
	}

	.trpStatus {
		background-color: #05c300;
		top: 0px;
		left: -6px;
		border-radius: 1em;
		border: 1px solid white;
	}

	/* > rectangular avatars */
	.trpDisplayPicture .avatar__image, .room-item__avatar, .room-menu-options__item__avatar, .chat-header__avatar img {
		border-radius: 0px;
	}

	/* Unread notification */
	.room-item__unread-indicator.has-unreads, .room-menu-options__item__unread-indicator.has-unreads {
		background-color: #347fef;
	}
	/* The X button for unread notifications pointing upwards */
	.banner-wrapper .banner button.side {
		border: 1px solid;
		border-left: none;
	}

	/* The button that takes you to unread messages */
	.banner-wrapper .banner .unread {
		background-color: #347fef;
		color: #347fef;
		border: 1px solid #347fef;
	}

	/* COMMIT */
	/* > date */
	.activity-item-inner > .activity-time {
		color: white;
	}

	/* > the commit message */
	.commit {
		background-color: transparent !important;
		color: #c6c57d !important;
	}

	/* Links */
	a {
		color: #3498db;
	}

	/* > settings menu links */
	div.textcomplete-wrapper ul.dropdown-menu a, ul.selectable a {
		color: white;
	}

	/* QUOTES */
	blockquote {
		border-left: 5px solid #3d3d3e;
	}

	blockquote p {
		margin-bottom: 0;
		font-size: 14px;
		font-weight: 400;
		line-height: normal;
	}

	/* code */
	code {
		border: 1px solid rgba(255, 255, 255, 0.14);
		/*background-color: rgba(98, 113, 153, 0.2);*/
		background-color: rgb(41, 41, 41);
		border-radius: 2px;
		padding: 0 4px;
		overflow-x: auto;
		word-wrap: break-word;
		font-size: 1.4rem;
		color: rgba(255, 255, 255, 0.81);
	}

	/* Mentions */
	.chat-item.mentioned .chat-item__text .mention, .chat-item.mentioned .chat-item__text .groupmention {
		color: #227ee6;
		background-color: #e9e9e9;
		border-radius: 2px;
		font-weight: 600;
	}

	/* only actually shows when visiting a new room to join */
	.chat-input__box {
		background-color:transparent;
	}

	.menu {
		background-color:#162E34 !important;
	}

	.menu-list__overflow-shadow {
		background: linear-gradient(to bottom,rgba(56, 52, 53, 0) 0, #162E34 100%);
	}

	.room-list-item--current-room {
		background-color:#6A797D;
	}

	.trpBtnWhite {
		background-color:#6A797D;
		border-bottom: none;
	}

	.trpButtonMedium.trpBtnWhite.gtrCreateRoomButton:not(:hover){
		color: white;
	}

	.room-list-item.room-list-item--current-room span {
		color: white;
	}

	.trpAddBadge.js-add-more div {
		color: white;
	}

	.popover.in {
		background: rgb(59, 59, 59) !important;
	}

	.popover.top .arrow:after {
		border-top-color: rgb(59, 59, 59) !important;
	}

	.popover.bottom .arrow:after {
		border-bottom-color: rgb(59, 59, 59) !important;
	}

	.popover.right .arrow:after {
		border-right-color: rgb(59, 59, 59) !important;
	}

	.popover.left .arrow:after {
		border-left-color: rgb(59, 59, 59) !important;
	}

	.popover-footer.trpBodySmall {
		background: rgb(59, 59, 59) !important;
	}

	.popover.in .popover-title {
		background: rgb(79, 79, 79) !important;
		color: white;
		border-color: white;
	}

	.popover-content .chat-item__text, .popover-content .chat-item__from {
		color: white !important;
	}

	.chat-header__avatar {
		background-color: black;
	}

	.js-search-input.search-input-field {
		background: black;
		box-shadow: 0px 0px 5px #444;
		color: white;
	}

	.trpPopoverDetails h1, .trpPopoverDetails h2, .trpPopoverDetails .vcard-stats, .trpPopoverDetails .trpUserDetails {
		color: white;
	}

	::-webkit-scrollbar {
		width: 12px !important;
	}

	::-webkit-scrollbar-track {
		-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3) !important;
		border-radius: 10px !important;
	}

	::-webkit-scrollbar-thumb {
		border-radius: 10px !important;
		-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5) !important;
		background: #668;
	}

	.chat-item__container--status {
		background: rgb(70, 70, 70) !important;
		border: 0;
	}

	.octicon-tools:before {
		color: white;
	}

	.trpAddBadge{
		color: white !important;
	}

	hr {
		border-top: 0;
		border-bottom: 1px solid rgb(59, 59, 59);
	}

	span[data-group-name="all"],
	.unread,
	.modal--default__header,
	.modal--default__content,
	.modal--default__footer,
	body,
	.popover-footer,
	.banner-wrapper .banner .mention {
		background-color: rgb(29, 29, 29) !important;
	}

	.permission__details, .markdown-help-content, .modal--default__content {
		color: white !important;
	}

	.welcome-modal__collaborators, .welcome-modal__collaborator, .collaborators-container, .userhome-main {
		background-color: rgb(29, 29, 29) !important;
		color: white !important;
	}

	.trpChatInput, .dropdown, div.result.selected, .result:hover {
		background-color: rgb(29, 29, 29) !important;
		color: white !important;
	}

	span.groupmention {
		background-color: transparent !important;
	}

	.menu-list__overflow-shadow {
		background: none;
	}

	.issue.open {
		background-color: #072c1a !important;
		color: #169c58 !important;
	}

	.issue.closed {
		background-color: #2e0905 !important;
		color: #9f2013 !important;
	}

	div.chat-connectivity-indicator {
		-webkit-filter: invert(100%) hue-rotate(180deg)
	}

	i.search__toggle-icon.octicon.octicon-search {
		color: #333;
	}

	/* Misc */
	.modal--default__footer__link {
		color: #347fef;
	}
}
