css:
@-moz-document domain("vk.com") {
/* General */
#page_layout {
    min-width: 960px;
    width: calc(70%) !important;
}
#page_body {
    width: calc(100% - 165px) !important;
}

/* Messages */
.im-chat-input .im-chat-input--textarea {
    width: calc(100% - 120px)
}
.im-mess,
._im_mess {
    padding-right: 40px
}

/* Wall content */
.page_gif_large {
    width: 100%
}
.page_gif_large a,
.page_post_sized_thumbs {
    margin: 0 auto;
}
._post_content {
    padding: 5px;
}

/* Video */
.videocat_row {
    width: 100%
}

/* Apps */
.apps_recent_block {
    width: calc(100% - 360px) !important;
}
.apps_recent_all {
    width: 100% !important;
}
.apps_featured_slider {
    width: 100%;
}
}
