css:
@-moz-document domain("murdermystery2.com") {
html,
body,
#reply-control,
.modal-body,
.modal-footer {
    background: rgb(31, 31, 31) !important;
    color: rgb(250, 250, 250) !important;
}
/* ===== Thread browsing (with replies) ===== */

.topic-list.categories a.title {
    color: #dadada !important;
}
#topic-title h1 a {
    color: rgb(250, 250, 250) !important;
}
.extra-info-wrapper a.badge-wrapper.bullet span.badge-category,
a.badge-wrapper.bullet span.badge-category {
    color: rgb(255, 255, 255) !important;
    background-color: transparent;
}
.topic-meta-data span.username a {
    color: rgb(250, 250, 250);
}
.topic-body {
    border-top: 0px;
}
.topic-body,
#list-area,
.topic-list {
    background-color: rgb(31, 31, 31) !important;
}
.topic-body,
#list-area,
.topic-list,
.topic-avatar,
#topic-closing-info {
    border-top: 1px solid rgb(84, 84, 84);
}
.topic-body .cooked {
    color: rgb(240, 240, 240);
}
.composer-popup.education-message {
    background-color: #292929;
}
aside.quote .title,
body code {
    color: rgb(242, 242, 242);
}
aside.quote .title,
blockquote,
body code {
    background-color: rgb(70, 70, 71) !important;
}
/* Post reply area */

.ember-view.btn.btn-primary.create.standard {
    background-color: #08c;
}
#reply-control .grippie {
    background-color: rgb(80, 80, 80) !important;
}
.reply-to a,
a:link,
a:hover,
a:active,
a:visited,
a.reply-new:hover,
a.reply-new:active,
a.reply-new:visited {
    color: #26b0f5;
}
.topic-list a.title.visited:not(.badge-notification) {
    color: rgb(180, 180, 180);
}
.d-editor {
    border-color: rgb(20, 20, 20) !important;
}
.d-editor-preview {
    border: 1px dashed rgb(20, 20, 20) !important;
}
#reply-control .wmd-controls .d-editor-input,
#reply-control .wmd-controls .d-editor-preview {
    background-color: rgb(40, 40, 41) !important;
}
#reply-control .d-editor-button-bar {
    background-color: rgb(40, 40, 41) !important;
    border-bottom: 2px solid rgb(55, 55, 55);
}
#reply-control .d-editor-button-bar button {
    color: rgb(240, 240, 240);
}
textarea.ember-view.ember-text-area.d-editor-input,
#reply-control .wmd-controls .d-editor-preview {
    color: rgb(240, 240, 240) !important;
}
/* Autocompletion (e.g. for emoji) */

.autocomplete {
    background-color: rgb(40, 40, 40);
    border: 1px solid rgb(80, 80, 80);
}
.autocomplete ul li {
    border-bottom: 1px solid rgb(55, 55, 55);
}
.autocomplete ul li a.selected[href] {
    background-color: rgb(140, 18, 14);
}
.discourse-no-touch .autocomplete ul li a[href]:hover {
    background-color: rgb(70, 70, 70);
    text-decoration: none;
}
/* Avatar Actor */

img.avatar.actor {
    background-color: rgb(31, 31, 31);
    border-radius: 50%;
}
body .avatar-wrapper {
    background-color: #1f1f1f;
    display: inline-block;
    border-radius: 50%;
}
/* Poll */

div.poll li[data-poll-option-id][data-poll-selected="selected"] {
    background: #117904 !important;
}
div.poll {
    border: 1px solid #1f1f1f;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
}
div.poll .poll-info {
    color: #ffffff;
    text-align: center;
    vertical-align: middle;
}
div.poll .results .bar-back {
    height: 7px;
    background: #b5b5b5;
}
div.poll .results .bar {
    height: 7px;
    background: #17960f;
}
div.poll li[data-poll-option-id] {
    color: #fdfdfd;
    background: #353535;
    box-shadow: inset 0 -6px rgba(0, 0, 0, 0.25), inset 0 0 0 100px rgba(0, 0, 0, 0);
    padding: .5em .7em .7em .5em;
    border-radius: 4px;
}
div.poll .results .percentage {
    font-size: 20px;
    float: right;
    color: #ffffff;
    margin-left: 5px;
}
/* Misc */

.topic-map .participants .user a {
    color: #f5f5f5;
    font-weight: bold;
    font-size: 0.929em;
}

.select2-drop-active {
    border: 1px solid #08c;
    border-top: 0;
}
.user-menu .notifications li.read.last.heading:hover {
    background-color: #1f1f1f !important;
}
.menu-panel.drop-down,
.menu-panel {
    background-color: #1f1f1f !important;
    border: 1px solid rgb(80, 80, 80);
}
.user-main .user-content {
    background: rgb(31, 31, 31) !important;
}
.user-main .user-stream .avatar-link {
    float: left;
    margin-right: 4px;
    background-color: #1f1f1f;
}
-container a.select2-choice {
    background: #fff;
    border-radius: 4px;
    border-color: #272727;
}
.badge-wrapper.bullet {
    display: inline-flex;
    align-items: baseline;
    margin-right: 10px;
    background-color: transparent;
}
.menu-panel li.category-link {
    float: left;
    width: 45%;
    margin: 5px 5px 0 8px;
}
body input[type="text"]:focus,
body input[type="password"]:focus,
body input[type="datetime"]:focus,
body input[type="datetime-local"]:focus,
body input[type="date"]:focus,
body input[type="month"]:focus,
body input[type="time"]:focus,
body input[type="week"]:focus,
body input[type="number"]:focus,
body input[type="email"]:focus,
body input[type="url"]:focus,
body input[type="search"]:focus,
body input[type="tel"]:focus,
body input[type="color"]:focus {
    border-color: #DEDEDE;
    outline: 0;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.3), 0 0 5px #DEDEDE;
}
.close {
    color: #bcbcbc;
}
.close:hover {
    float: right;
    font-size: 1.429em;
    text-decoration: none;
    color: #dedede;
    cursor: pointer;
}
.modal-inner-container {
    max-width: 710px;
    margin: 0 auto;
    background-color: #fff;
    border: 1px solid #616161;
    box-shadow: 0 3px 7px rgba(0, 0, 0, 0.8);
    background-clip: padding-box;
}
.modal-header {
    border-bottom: 1px solid #292929;
}
.search-menu .search-context .show-help {
    float: right;
    background-color: #1f1f1f !important;
}
.custom-body {
    background-color: #2b2b2b;
}
body .form-horizontal textarea {
    background-color: rgb(58, 58, 58);
    color: #fff;
}
#banner {
    padding: 10px;
    border-radius: 5px;
    background: #2f2f2f;
    box-shadow: 0 1px 2px #545454;
    color: #ccc;
    z-index: 1001;
    overflow: auto;
}
#banner .close {
    font-size: 1.786em;
    margin-top: -5px;
    color: #c7c7c7;
    padding-left: 5px;
}
aside.onebox header a[href],
aside.onebox .onebox-body a[href] {
    color: rgb(50, 119, 192);
}
.dropdown-menu .disabled>a,
.dropdown-menu .disabled>a:hover {
    text-decoration: none;
    color: #222;
    background-color: #292929;
    cursor: default;
}
nav.post-controls button.create {
    margin-right: 0;
    color: #a0a0a0;
    margin-left: 10px;
}
.similar-topics a[href] {
    color: #dedede;
}
.similar-topics .search-link .fa,
.similar-topics .search-link .blurb {
    color: #b7b7b7;
}
.composer-popup h3 {
    color: #fbfbfb;
}
.similar-topics {
    background-color: #1f1f1f;
}
.select2-results .select2-highlighted,
.period-chooser #period-popup ul li a[href]:hover {
    background-color: #616161;
    color: #fff;
}
.popup-menu {
    background-color: #272727;
    width: 205px;
    padding: 10px;
    border: 1px solid #404040;
    z-index: 999;
}
.emoji-modal .toolbar li a.selected {
    background-color: #525252;
}
.emoji-modal .toolbar li a {
    padding: 8px;
    background-color: #353535;
}
.emoji-modal .nav a {
    color: #bfbfbf;
}
.emoji-table-wrapper {
    min-width: 442px;
    min-height: 185px;
    background-color: #282828;
}
#share-link {
    position: absolute;
    left: 20px;
    z-index: 1200;
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.4);
    background-color: #2f2f2f;
    padding: 6px 10px 10px 10px;
    width: 300px;
    display: none;
}
.select2-container {
    border-radius: 3px;
    border: 1px solid #313131 !important;
    margin: 0;
    position: relative;
    display: inline-block;
    zoom: 1;
    vertical-align: middle;
}
.directory table tr.me td .username a,
.directory table tr.me td .name,
.directory table tr.me td .title,
.directory table tr.me td .number,
.directory table tr.me td .time-read {
    color: #eaeaea;
}
.directory table tr.me td {
    background-color: #3a3a3a;
}
.fps-result .blurb .search-highlight {
    color: #b1b1b1;
}
.search-link .blurb {
    color: #c3c3c3;
    display: block;
    word-wrap: break-word;
    font-size: 11px;
    line-height: 1.3em;
}
.search-link .blurb .search-highlight {
    color: #ffffff;
}
.search-menu li:not(.category):not(.heading):hover a:not(.badge-notification) {
    background-color: #616161;
}
div.menu-links-header a:hover {
    background-color: #e45735 !important;
    transition: ease 0.5s;
}
.dropdown-menu li>a:hover,
.dropdown-menu .active>a,
.dropdown-menu .active>a:hover {
    color: #222;
    text-decoration: none;
    background-color: #313131;
}
.dropdown-menu span.title {
    font-weight: bold;
    display: block;
    font-size: 1em;
    color: #e6e5e5;
}
.dropdown-menu {
    position: absolute;
    bottom: 115%;
    left: 0;
    z-index: 999;
    display: none;
    float: left;
    width: 550px;
    margin: 1px 0 0;
    list-style: none;
    background-color: #232323;
    border: 1px solid #292929;
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.4);
    background-clip: padding-box;
}
.caret {
    display: inline-block;
    width: 0;
    height: 0;
    vertical-align: middle;
    border-top: 4px solid #d8d8d8;
    border-right: 4px solid transparent;
    border-left: 4px solid transparent;
    content: "";
    margin-left: 5px;
}
#user-card .bio a {
    color: #ddd;
}
body .form-horizontal textarea {
    background-color: rgb(41, 41, 41);
}
.topic-map .information {
    color: #e2e2e2;
}
.btn-primary:active {
    background-color: rgb(17, 143, 191);
    background-image: linear-gradient(to bottom, #0093E7, #0093E7);
    color: #fff;
}
.modal-header {
    background-color: rgb(41, 41, 41);
}
a {
    color: #08c;
}
.small-action,
.post-links-container .post-links {
    border-top: 1px solid #4c4c4c;
}
.topic-list a.title {
    color: rgb(192, 50, 50) !important;
}
.extra-info-wrapper .topic-link {
    color: #eee;
}
.alert.alert-info.clickable {
    color: #76abfd;
}
.alert.alert-info {
    background-color: #212020;
}
.modal .modal-body label {
    color: #fff
}
.period-chooser #period-popup {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    font-size: 1.2em;
    border: 1px solid #545454;
    padding: 5px;
    background: #3c3c3c;
    position: absolute;
    z-index: 999;
    box-shadow: 0 2px 2px rgba(0, 0, 0, 0.4);
}
.period-chooser #period-popup ul li a,
.period-chooser #period-popup ul li a:visited {
    display: block;
    padding: 5px;
    color: #fff;
}
.user-preferences .static {
    color: #e2e2e2;
}
.btn[href] {
    color: #fff;
}
.d-editor-preview {
    color: #e2e2e2;
}
.user-main .user-stream .edit-reason {
    background-color: #635538;
}
.user-main .user-content .user-invite-controls {
    background-color: #2a2a2a;
}
.d-header .icons .unread-notifications {
    right: 0;
    background-color: #f35555;
}
div.ac-wrap {
    overflow: auto;
    max-height: 150px;
    background-color: #212121;
    border: 1px solid #232323;
    padding: 5px 4px 1px 4px;
}
.autocomplete ul li a[href] span.username {
    color: #ececec;
}
.select2-drop,
.select2-container-multi .select2-choices {
    background-color: rgb(41, 41, 41) !important;
    color: #eee !important;
    z-index: 99998;
    margin: 0;
    width: auto;
    padding: 0;
    left: 1;
    top: 0;
    min-height: -webkit-contain-floats;
    min-width: -webkit-contain-floats;
    overflow:auto;
}
.select2-container a.select2-choice {
    background: #212121;
    border-radius: 3px;
    border-color: #212121;
    color: #fff
}
.badge-wrapper.bullet span.badge-category {
    color: #fff !important;
    display: inline-block;
    overflow: hidden;
    text-overflow: ellipsis;
}
.search-title {
    position: relative;
    margin: 10px 0 15px;
    max-width: 780px;
    border-bottom: 3px solid #7d7d7d;
    width: 100%;
}
/* Emoji selector */

.emoji-modal {
    background-color: rgb(52, 52, 52) !important;
}
table.emoji-page td {
    background-color: rgb(40, 40, 40) !important;
}
/* Name of mentioned users in posts */

a.mention,
a.mention-group {
    background-color: rgb(30, 30, 30);
}
/* Code snippets */

.hljs,
.hljs-keyword {
    color: rgb(230, 230, 230);
}
.category-combobox .category-desc,
.select2-drop .category-desc {
    color: #f7f6f6;
    font-size: 0.857em;
    line-height: 16px;
}
.category-combobox .topic-count,
.select2-drop .topic-count {
    font-size: 11px;
    color: #ececec;
    display: inline-block;
}
/* Tag labels */

.discourse-tag.box {
    background-color: rgb(48, 48, 48);
    color: rgb(231, 231, 231) !important;
    border: 1px solid rgb(80, 80, 80);
}
/* Buttons */

.btn {
    background-color: rgb(45, 45, 45);
    color: rgb(240, 240, 240);
}
.btn:hover {
    background-color: rgb(75, 75, 75);
    color: rgb(240, 240, 240);
}
/* "Stats ribbon" (under the very first post of each thread) */

.topic-map {
    border-color: rgb(55, 55, 55);
}
.topic-map ul,
section.avatars,
section.links {
    background-color: rgb(70, 70, 70);
}
.topic-map .buttons .btn {
    background-color: rgb(70, 70, 70);
    border-color: rgb(70, 70, 70) -moz-use-text-color -moz-use-text-color rgb(70, 70, 70);
    border-top: none;
    border: none;
}
.topic-map .buttons .btn:hover {
    color: rgb(184, 184, 184);
    background-color: rgb(61, 61, 61);
}
.topic-map section {
    border-top: 1px solid rgb(55, 55, 55);
    background-color: rgb(41, 41, 41);
}
.topic-map .map .number,
.topic-map .map i {
    color: rgb(240, 240, 240);
}
.topic-map h3 {
    color: rgb(240, 240, 240);
}
.badge-notification.clicks {
    background-color: rgb(35, 35, 35);
    color: rgb(230, 230, 230);
}
/* Wiki editing */

.select2-drop,
.select2-container a.select2-choice,
.select2-container-multi .select2-choices {
    background-color: #212121;
}
/* GitHub issue */

aside.onebox {
    border: 5px solid rgb(70, 70, 70);
}
aside.onebox header a[href],
aside.onebox .onebox-body a[href] {
    color: rgb(192, 50, 50);
}
aside.onebox .labels span {
    background-color: rgb(35, 35, 35) !important;
}
