.category-name {
    color: rgb(250, 250, 250);
}
.category-description,
th.category,
th.latest,
th.stats.topics,
.topic-list td.value,
.topic-list.categories td.stats .unit {
    color: rgb(240, 240, 240) !important;
}
/* Number of new posts */

.badge-notification.new-posts,
.badge-notification.unread-posts {
    background-color: rgb(0, 185, 255);
    color: rgb(255, 255, 255);
}
/* Categories drop-down menu in mobile view */

.list-controls .nav-pills .drop {
    background-color: rgb(45, 45, 45);
    border: 1px solid rgb(80, 80, 80);
}
.topic-list th,
.topic-list td {
    color: rgb(240, 240, 240) !important;
}
/* Links with thread titles in mobile view */

.topic-list a.title {
    color: rgb(192, 50, 50);
}
/* Stat numbers in mobile view */

.category-list-item > footer figure {
    color: rgb(136, 136, 136) !important;
}
/* Thread excerpts in mobile view */

.topic-list .topic-excerpt {
    color: rgb(210, 210, 210);
}
/* Table row borders in mobile view */

tr.category-topic-link {
    border-top: 1px solid rgb(60, 60, 60);
    border-bottom: 1px solid rgb(60, 60, 60);
}
/* ===== List of threads inside categories ===== */

.topic-list a.title.visited:not(.badge-notification) {
    color: #7d7d7d !important;
}
.topic-list .main-link a.title {
    color: #dadada !important;
}
/* Buttons above table */

.list-controls .category-breadcrumb li.bullet > .badge-category {
    background-color: rgb(45, 45, 45) !important;
    color: rgb(240, 240, 240) !important;
}
.nav-pills > li > a {
    color: rgb(240, 240, 240) !important;
    background-color: rgb(56, 55, 55);
    /* For mobile view */
}
.nav-pills > li > a:hover {
    color: rgb(240, 240, 240);
    background-color: #e45735;
}
.nav-pills > li.active > a,
.nav-pills > li > a.active {
    color: rgb(240, 240, 240);
    background-color: rgb(56, 55, 55);
}
/* Disabled button (e.g. when you're not allowed to create new topics in a category) */

.btn[disabled],
.btn.disabled {
    background-color: rgb(114, 114, 114);
}
.btn[disabled]:hover,
.btn.disabled:hover {
    background-color: rgb(84, 84, 84);
}
/* Category and tutorial list drop down */

.list-controls .category-dropdown-menu {
    background-color: #2B2C2B !important;
    border: 1px solid rgb(80, 80, 80);
}
.list-controls .category-dropdown-menu .home {
    color: rgb(250, 250, 250);
}
/* First table row */

.topic-list th {
    color: rgb(240, 240, 240);
}
.topic-list .sortable:hover {
    background-color: rgb(55, 55, 55);
}
/* Borders */

.topic-list > tbody > tr:first-of-type {
    border-top: 3px solid rgb(84, 84, 84);
}
.topic-list > tbody > tr {
    border-bottom: 1px solid rgb(84, 84, 84);
}
/* Number of views and activity */

td {
    color: rgb(136, 136, 136) !important;
}
/* Number of replies */

.topic-list .num a.badge-posts {
    color: rgb(176, 176, 176) !important;
}
/* User profile pop-up */

#user-card {
    color: rgb(230, 230, 230);
}
#user-card h1 a,
#user-card h1 i {
    color: rgb(240, 240, 240);
}
#user-card .card-content {
    background: none repeat scroll 0% 0% rgba(54, 54, 54, 0.85);
}
#user-card .metadata h3 .desc,
#user-card .metadata h3 a {
    color: rgb(144, 144, 144);
}
#user-card .usercard-controls a,
#user-card .location-and-website .website-name a {
    color: rgb(230, 230, 230);
}
#user-card .usercard-controls a.btn.btn-primary {
    background-color: rgb(56, 55, 55);
}
#user-card .usercard-controls a.btn.btn-primary:hover {
    background-color: #0093E7
}
/* ===== Modal	 ===== */

.bootbox.modal {
    border: 1px solid rgb(80, 80, 80);
}
.modal-footer {
    border-top: 1px solid rgb(80, 80, 80);
}
/* ===== Navigation bar ===== */

.menu-panel.drop-down,
.menu-panel {
    /* Both desktop and mobile view */
    
    background-color: #1f1f1f !important;
    border: 1px solid rgb(80, 80, 80);
}
.menu-panel.drop-down a:hover,
.menu-panel.drop-down a {
    background-color: #1f1f1f
    /* color: rgb(192, 50, 50); */
}
a.widget-link.user-activity-link,
a.widget-link.logout {
    color: rgb(250, 250, 250);
}
.menu-panel.drop-down i.fa,
div.menu-links-header .fa,
div.menu-links-header a,
.user-menu .notifications .fa {
    color: rgb(255, 255, 255) !important;
}
.user-menu .notifications li span {
    color: rgb(250, 250, 250);
}
.menu-panel ul.menu-links li a:hover,
.menu-panel ul li.heading a:hover {
    background-color: #0093E7
}
.menu-panel ul.menu-links li a,
.menu-panel ul li.heading a {
    transition: background 0.15s ease 0s;
    color: rgb(250, 250, 250);
}
.user-menu .notifications li {
    background-color: #616161 !important;
}
.user-menu .notifications li.read:hover {
    /* background-color: #0f6b29 !important; */
    
    background-color: #505050 !important;
}
.user-menu .notifications li.read {
    /* background-color: rgb(100, 100, 101) !important; */
    
    background-color: #1f1f1f !important;
}
hr {
    /* .menu-panel hr { */
    
    border-color: rgb(240, 40, 40) -moz-use-text-color -moz-use-text-color;
}
.menu-panel li.category-link .badge-notification {
    color: rgb(230, 230, 230);
}
hr {
    border-top-color: #4c4c4c;
}
input[type="text"],
body input[type="password"],
body input[type="datetime"],
body input[type="datetime-local"],
body input[type="date"],
body input[type="month"],
body input[type="time"],
body input[type="week"],
body input[type="number"],
body input[type="email"],
body input[type="url"],
body input[type="search"],
body input[type="tel"],
body input[type="color"] {
    background-color: rgb(80, 80, 80) !important;
    color: rgb(240, 240, 240) !important;
    border: 1px solid rgb(31, 31, 31) !important;
}
/* Account/avatar drop-down menu in mobile mode */

div.menu-links-header a:hover {
    background-color: rgb(7, 91, 31);
}
/* ===== Account preferences ===== */

.user-main .user-content {
    background: rgb(31, 31, 31) !important;
}
.user-main .user-stream .item {
    background-color: rgb(31, 31, 31) !important;
    border-bottom: 1px solid rgb(84, 84, 84);
}
.user-main .user-stream .excerpt {
    color: rgb(240, 240, 240);
}
.stats-section li {
    background-color: rgb(41, 41, 41) !important;
}
.top-sub-section li {
    background-color: rgb(31, 31, 31) !important;
    box-shadow: 0px 0px 0px rgb(35, 35, 35);
    border-left: 0px solid rgb(35, 35, 35);
}
.top-sub-section .topic-info {
    color: rgb(240, 240, 240);
}
.user-info .user-detail .username a {
    color: rgb(240, 240, 240);
}
.user-info .user-detail .name {
    color: rgb(225, 225, 225);
}
/* Notifications: user name */

.user-main .user-stream .notification p span {
    color: rgb(240, 240, 240);
}
/* Nav buttons on top */

.user-main .nav-pills a i,
.user-main .nav-pills a:hover i {
    color: rgb(240, 240, 240);
}
.user-main .nav-pills a:hover {
    background-color: #e45735;
}
.nav-pills>li.active>a,
.nav-pills>li>a.active {
    color: #fff;
    background-color: #359be4;
}
/* Links on the left (in Activities, Notifications, and Messages) */

.user-main .nav-stacked > li > a {
    color: #878787 !important;
}
.user-main .nav-stacked > li > a.active {
    color: rgb(240, 240, 240) !important;
}
/* Account info (collapsed) */

.user-main .about.collapsed-info .details {
    background: none repeat scroll 0% 0% rgba(56, 56, 56, 0.85);
    border-bottom: 1px solid rgb(45, 45, 45);
}
.user-main .about.collapsed-info .controls ul a.btn {
    color: rgb(230, 230, 230);
}
.user-main .about.collapsed-info .controls ul a.btn.btn-primary {
    background-color: rgb(56, 55, 55);
}
.user-main .about.collapsed-info .controls ul a.btn.btn-primary:hover {
    background-color: #0093E7
}
/* Account info (after clicking on Expand) */

.user-main .about .secondary {
    border-bottom: 1px solid rgb(84, 84, 84);
    border-top: 1px solid rgb(84, 84, 84);
}
.user-main .about .secondary dl {
    background-color: rgb(41, 41, 41);
}
.user-main .about .secondary dl dd,
span.relative-date {
    color: rgb(225, 225, 225);
}
.user-main .about .details {
    background: none repeat scroll 0% 0% rgba(31, 31, 31, 0.85);
}
.groups .group-link {
    color: rgb(192, 50, 50);
}
.user-main .about .controls a.btn.btn-primary {
    background-color: rgb(56, 55, 55);
}
.user-main .about .controls a.btn.btn-primary:hover {
    background-color: #0093E7
}
/* Badges */

.badge-card .badge-contents {
    background-color: rgb(41, 41, 41);
}
.badge-card .badge-contents .badge-info h3 {
    color: rgb(242, 242, 242);
}
.badge-card .badge-contents .badge-info {
    color: rgb(230, 230, 230);
}
.badge-card .badge-contents .badge-icon {
    background-color: rgb(45, 45, 45);
}
#user-card.show-badges .badge-section .user-badge,
#user-card.show-badges .badge-section .more-user-badges {
    background: #403e3e;
    border: 1px solid #333333;
    color: #fff;
}
.user-main .user-content {
    border: none;
}
/* ===== Account preferences: mobile ===== */
/* Navigation drop down menus */

.mobile-view .mobile-nav a {
    color: rgb(240, 240, 240);
    background-color: rgb(45, 45, 45) !important;
}
.mobile-view .mobile-nav .drop.expanded {
    background-color: rgb(45, 45, 45) !important;
    border-color: rgb(74, 74, 74);
}
}
