css:
@-moz-document domain("w.qq.com") {
.btn_black {
    border-color: rgba(0, 0, 0, 0);
    background: rgba(255, 255, 255, 0.2);
    background: none!important;
}
.btn_black.active {
    background: #333;
    background: -webkit-gradient(linear, left top, left bottom, from(#2d2d2d), to(#444));
    background: -webkit-linear-gradient(top, #2d2d2d, #444);
    background: linear-gradient(top, #444, #2d2d2d)
}
@media all and (min-width:1000px) {
    .panel header {
        display: -webkit-box;
        display: -moz-box;
        display: box;
        -webkit-box-orient: horizontal;
        -moz-box-orient: horizontal;
        box-orient: horizontal;
        height: 45px;
        width: 100%;
        border-radius: 2px 2px 0 0
    }
    .panel footer {
        width: 100%;
        position: absolute;
        left: 0;
        bottom: 0;
        border-radius: 0 0 2px 2px;
        -webkit-border-radius: 0 0 2px 2px;
        -moz-border-radius: 0 0 2px 2px
    }
}
@media (min-width: 1000px) {
    #panelRightButton-2 {
        margin-right: -150px;
    }
    .wrap {
        width: 80%;
        height: 90%;
        margin-top: 40px;
    }
    #face_images > .wrap {
        width: 4300px;
        height: 100%;
        margin-top: 0px;
    }
    .chat_toolbar {
        border-radius: 0 0 2px 2px;
    }
    .chat_container {
        background: #fafafa!important;
        width: 100%;
        min-height: 100%;
    }
    #main_container {
        width: 25%;
        height: 100%;
        float: left;
    }
    #panelHeader-1,
    #panelHeader-2,
    #panelHeader-3,
    #panelHeader-4 {
        height: 100px!important;
    }
    #panelBody-1,
    #panelBody-2,
    #panelBody-3,
    #panelBody-4 {
        top: 55px;
    }
    #panelTitle-1,
    #panelTitle-2,
    #panelTitle-3,
    #panelTitle-4 {
        display: none;
    }
    .accountHeader {
        z-index: 10001;
        position: absolute;
        display: block;
        width: 100%;
        top: 40px;
    }
    .accountHeader .avatar_wrap {
        width: 40px;
        height: 40px;
        left: 20px;
        top: -22px;
        position: absolute;
        border: 3px solid #fff;
        box-shadow: 0px 0px 0px #000;
        border-radius: 5px;
    }
    .chat-panel,
    .search-panel {
        width: 100%;
        height: 100%;
        top: 0px;
    }
    .main-panel {
        position: relative;
        display: block!important;
        width: 100%;
        background: #fff;
        border-radius: 4px;
        height: 100%;
        top: 0px;
        box-shadow: 0px 0px 0px rgba(0, 0, 0, 0)!important;
        -moz-transition: none!important;
        -webkit-transition: none!important;
        transition: none!important;
        -webkit-transform: none!important;
        -moz-transform: none!important;
        transform: none!important;
    }
    .profile-panel,
    .member-panel,
    .record-panel,
    .plugin-displayer-panel {
        z-index: 100010;
        top: 0;
        left: 0;
        height: 100%;
        width: 100%;
    }
    .container {
        position: relative;
        width: 70%;
        height: 100%;
        float: left;
        margin-left: 15px;
    }
    .wallpaper-ctrl {
        display: none;
    }
    .suggestion {
        display: none;
    }
}
.black footer,
.black header {
    background: #333;
    color: #000;
    background: #ededed;
    box-shadow: 0px 0px 10px #a7a8a8;
}
.list>li .list_text,
.list_group_title .list_text {
    display: inline-block;
    text-overflow: ellipsis;
    white-space: nowrap;
    vertical-align: middle
}
.list_white>li {
    border-color: rgba(0, 0, 0, 0);
    background: #fff;
    padding-left: 8px;
    padding-right: 8px;
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.list_white>li:hover {
    border-color: rgba(0, 0, 0, 0);
    background: #dfdfdf;
    padding-left: 8px;
    padding-right: 8px;
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.list_white>li.selected {
    border-color: #2373a5;
    background: #5393c5;
}
nav li {
    position: relative;
    overflow: hidden;
    -webkit-box-flex: 1;
    -moz-box-flex: 1;
    box-flex: 1;
    height: 50px;
    border-left: 0px solid #555!important;
    border-right: 0px solid #555!important;
    text-shadow: 0 0px 0 rgba(0, 0, 0, 0)!important;
    color: #000;
    font-size: 14px;
    text-align: center;
    cursor: pointer;
}
nav li.selected {
    background: #eee!important;
    border-left-color: #222!important;
    color: #000!important;
    text-shadow: 0 0px 0 rgba(0, 0, 0, 0)!important;
    box-shadow: inset 0 0 0px rgba(0, 0, 0, 0)!important;
}
.chat_toolbar {
    color: #000;
    background: #ededed;
}
.chat_container {
    background: #fafafa;
    width: 100%;
    min-height: 100%;
}
.btn_blue {
    border-color: #5f9cc5;
    background: #6facd5;
    text-shadow: 0 0px 0px #194b7e;
}
.member_scroll_area {
    overflow: hidden;
    bottom: 40px;
    top: 0;
    position: absolute;
    left: 0;
    width: 100%;
    background: gray;
}
.scrollWrapper {
    overflow: hidden;
    bottom: 40px;
    top: 0;
    position: absolute;
    left: 0;
    width: 100%;
}
.pannel_menu_list {
    position: absolute;
    width: 100%;
    height: 80px;
    left: 0;
    top: -90px;
    overflow: hidden;
    background: #e5e5e5;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.5);
    display: none;
    -webkit-transition: top .2s ease-out;
    -moz-transition: top .2s ease-out;
    transition: top .2s ease-out;
}
.pannel_menu_list li {
    height: 80px;
    width: 80px;
    text-align: center;
    float: left;
    font-size: 12px;
    background: #d0d0d0;
    border-right: 0px solid rgba(0, 0, 0, 0);
    border-left: 0px solid rgba(0, 0, 0, 0);
    cursor: pointer;
    color: #000;
}
#plugin {
    display: none;
}
}

@-moz-document domain("w.qq.com") {
.chat_content_group.self>.chat_content {
    background: #6facd5;
    color: #fff;
}
.chat_content {
    display: inline-block;
    min-height: 16px;
    max-width: 50%;
    background: #e5e5e5;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 10px;
    padding: 10px 15px;
    word-break: break-all;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0);
    line-height: 1.4;
}
.chat_content_avatar {
    float: left;
    width: 40px;
    height: 40px;
    margin-right: 10px;
    border-radius: 100%;
}
.chat_time span {
    color: rgba(255, 255, 255, .8);
    background: rgba(0, 0, 0, .2);
    padding: 2px 8px;
    display: inline-block;
    margin: 10px;
    font-size: 13px;
    -webkit-border-radius: 8px;
    -moz-border-radius: 8px;
    -ms-border-radius: 8px;
    -o-border-radius: 8px;
    border-radius: 8px;
}
.panel_header,
.panel_footer {
    box-shadow: 0px 0px 10px #a7a8a8;
}
}

@-moz-document domain("w.qq.com") {
@media (min-width: 720px) {
    .panelShowFace {
        bottom: 0px!important;
    }
}
@media (min-width: 640px) {
    .panelShowFace {
        bottom: 0px!important;
    }
}
@media (min-width: 480px) {
    .panelShowFace {
        bottom: 0px!important;
    }
}
@media (min-width: 321px) {
    .panelShowFace {
        bottom: 0px!important;
    }
}
@media (min-width: 1000px) {
    #face_images {
        transform: scale(0.6)!important;
        -ms-transform: scale(.6)!important;
        -moz-transform: scale(.6)!important;
        -webkit-transform: scale(.6)!important;
        -o-transform: scale(.6)!important;
        border-radius: 4px;
        position: fixed;
        bottom: -20px!important;
    }
}
#face_images {
    transform: scale(1);
    -ms-transform: scale(1);
    -moz-transform: scale(1);
    -webkit-transform: scale(1);
    -o-transform: scale(1);
    border-radius: 4px;
    position: fixed;
    bottom: 50px;
}
.btnsWrap {
    background: #ededed!important;
}
.btnsWrap>li.selected {
    background: #a7a8a8!important;
}
.btnsWrap>li {
    float: left;
    list-style-type: none;
    margin: 5px 10px 0 0;
    padding: 2px;
    width: 10px;
    height: 10px;
    -webkit-border-radius: 10px;
    -moz-border-radius: 10px;
    -ms-border-radius: 10px;
    border-radius: 10px;
    background: #ccc;
}
}

@-moz-document domain("w.qq.com") {
nav a .icon {
    display: inline-block;
    width: 24px;
    height: 24px;
    background-size: 100% 100%;
    margin-top: 6px;
}
.contact .icon {
    background-image: url(http://wenon.org/images/ui/icon/message-text.png);
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.selected.contact .icon {
    background-image: url(http://wenon.org/images/ui/icon/message-text-selected.png);
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.conversation .icon {
    background-image: url(http://wenon.org/images/ui/icon/account.png);
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.selected.conversation .icon {
    background-image: url(http://wenon.org/images/ui/icon/account-selected.png);
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.setup .icon {
    background-image: url(http://wenon.org/images/ui/icon/settings.png);
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.selected.setup .icon {
    background-image: url(http://wenon.org/images/ui/icon/settings-selected.png);
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
nav li.selected {
    background: #eee!important;
    border-left-color: #222!important;
    color: #39f!important;
    text-shadow: 0 0px 0 rgba(0, 0, 0, 0)!important;
    box-shadow: inset 0 0 0px rgba(0, 0, 0, 0)!important;
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
nav li {
    position: relative;
    overflow: hidden;
    -webkit-box-flex: 1;
    -moz-box-flex: 1;
    box-flex: 1;
    height: 50px;
    border-left: 0px solid #555!important;
    border-right: 0px solid #555!important;
    text-shadow: 0 0px 0 rgba(0, 0, 0, 0)!important;
    color: #000;
    font-size: 14px;
    text-align: center;
    cursor: pointer;
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
#panelHeader-1,
#panelHeader-2,
#panelHeader-3,
#panelHeader-4,
#panelHeader-5,
#panelHeader-6,
#panelHeader-7,
#panelHeader-notifySetting {
    background: #3399ff;
    color: #fff;
}
.loginout {
    background: #f74634!important;
    text-shadow: 0 0 0px rgba(0, 0, 0, 0);
    color: #fff;
    text-align: center;
}
.list_page .group>.row:last-child {
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}
.list_page .group>.row:first-child {
    border-top: none;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
.list_page .group>.row {
    border-left: none;
    border-right: none;
    border-bottom: none;
    padding: 10px;
    background: #fff;
}
.list_group_white_title {
    border-top-color: #ccc;
    border-width: 0px;
    background: #fff;
}
@media (max-width: 1000px) {
    .scrollWrapper {
        overflow: hidden;
        bottom: 0px;
        top: 0;
        position: absolute;
        left: 0;
        width: 100%;
    }
}
.menu_list_icon {
    width: 48px;
    height: 32px;
    margin: 10px auto 5px;
    background-image: none;
    background-repeat: no-repeat;
}
.viewQzone {
    background-image: url(http://wenon.org/images/ui/icon/star.png)!important;
    background-repeat: no-repeat!important;
    background-size: cover!important;
    width: 48px!important;
    height: 48px!important;
    margin: 10px 15px 15px 15px;
    background-color: rgba(0, 0, 0, 0)!important;
}
.viewInfo {
    background-image: url(http://wenon.org/images/ui/icon/file-document-box.png)!important;
    background-repeat: no-repeat!important;
    background-size: cover!important;
    width: 48px!important;
    height: 48px!important;
    margin: 10px 15px 15px 15px;
    background-color: rgba(0, 0, 0, 0)!important;
}
.viewMembers {
    background-image: url(http://wenon.org/images/ui/icon/account-multiple.png)!important;
    background-repeat: no-repeat!important;
    background-size: cover!important;
    width: 48px!important;
    height: 48px!important;
    margin: 10px 15px 15px 15px;
    background-color: rgba(0, 0, 0, 0)!important;
}
.btn_setting .btn_img {
    background: url(http://wenon.org/images/ui/icon/menu.png);
    width: 24px!important;
    height: 24px!important;
}
.list_group.active .list_arrow_right {
    background-image: none!important;
}
.btn_face .btn_img {
    background: url(http://wenon.org/images/ui/icon/emoticon.png);
}
.btn_small.btn_arrow_left:after {
    display: none;
}
.switch>input {
    margin: 0;
    width: 90%;
    height: 60%;
    -webkit-border-radius: 6em;
    border-radius: 6em;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    -webkit-appearance: none;
    cursor: pointer;
    /* padding-top: 20px; */
    
    margin-top: 6px;
}
.switch-white>input:checked {
    background-image: -webkit-linear-gradient(#5393c5, #6facd5);
    background-image: -o-linear-gradient(#5393c5, #6facd5);
    background-image: linear-gradient(#3399ff, #3399ff);
}
.switch-white>span {
    background-image: -webkit-linear-gradient(white, #f1f1f1);
    background-image: -o-linear-gradient(white, #f1f1f1);
    background-image: linear-gradient(#fafafa, #fafafa);
}
.switch>span {
    position: absolute;
    top: 50%;
    left: 0em;
    margin-top: -.7em;
    width: 1.4em;
    height: 1.4em;
    -webkit-border-radius: 1.6em;
    border-radius: 50%;
    pointer-events: none;
    -webkit-transition: left .1s linear;
    -o-transition: left .1s linear;
    transition: left .1s linear;
    border-color: #d0d0d0;
    border-width: 1px;
    border-style: solid;
}
.switch>input:checked~span {
    left: 1.8em;
}
.online_state_setting ul {
    position: absolute;
    border: none!important;
    top: 20px;
    left: -40px;
    background: #ededed;
    width: 80px;
    border-radius: 2px;
    box-shadow: 0px 2px 5px rgba(0, 0, 0, .5);
}
.online_state_setting li:hover {
    background: #fafafa;
}
.online_state_setting li {
    padding: 10px 10px;
    cursor: pointer;
}
.profile_signature {
    line-height: 22px;
    overflow: hidden;
    font-size: 12px;
}
.group:hover,
.group:hover>.row,
.list_group_white_title:hover {
    background: #ededed;
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.group,
.group>.row,
.list_group_white_title {
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.member_tab .list_item {
    background: whitesmoke;
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.member_tab .list_item:hover {
    background: #dfdfdf;
    -webkit-transition: All 0.3s ease-in-out;
    -moz-transition: All 0.3s ease-in-out;
    -o-transition: All 0.3s ease-in-out;
}
.search_wrapper {
    position: relative;
    border: none!important;
    padding: 10px 6px;
    background: #ededed!important;
}
.search_inner {
    position: relative;
    border: 0px solid #d0d0d0;
    border-radius: 4px;
    padding: 5px 32px 0 20px;
    background: #ededed;
    box-shadow: none;
}
#member_searchInput,
#searchInput {
    background: #ededed;
}
.searchCancel {
    position: absolute;
    height: 33px;
    width: 50px;
    top: 10px;
    right: 10px;
    display: none;
    background: #ededed;
    border: none;
    color: #a7a8a8;
}
.searchCancel:focus {
    position: absolute;
    height: 33px;
    width: 50px;
    top: 10px;
    right: 10px;
    display: none;
    background: #ededed;
    border: none;
    color: #a7a8a8;
}
.searchClear {
    position: absolute;
    top: 5px;
    right: 10px;
    border: none;
    width: 24px;
    height: 24px;
    background: url(http://wenon.org/images/ui/icon/close.png) center;
    background-repeat: no-repeat;
    background-size: cover;
}
#send_chat_btn>.btn_text {
    display: none;
}
.chat_toolbar> .btn_blue {
    height: 30px;
    background: url(http://wenon.org/images/ui/icon/send.png);
    border: none;
    width: 30px!important;
    background-size: cover;
    background-repeat: no-repeat;
    top: 5px;
}
}
