.WB_global_nav { background: #fff url(http://images.h-ui.net/bg/gray/001.png) repeat; height: 36px; }
.WB_global_nav .gn_header { width: 1080px !important; height: 36px; }
.WB_global_nav .gn_search_v2 { width: 300px; float: right; margin: 3px 0 0 0; }
.WB_global_nav .gn_search_v2 .W_input { width: 256px; }
.WB_global_nav .gn_search_v2 .gn_topmenulist_search { width: 300px; }
.WB_global_nav .gn_position { float: left; margin: 0; }
