.PCD_header .pf_photo .icon_bed { position: absolute; right: 0; bottom: 0; }
.PCD_header .pf_username { position: absolute; left: 480px; top: 20px; }
.PCD_header .pf_intro { position: absolute; left: 480px; top: 55px; margin: 0; }
.PCD_header .pf_opt { position: absolute; left: 480px; top: 90px; margin: 0; height: 30px; }
.PCD_header .pf_opt .btn_bed { margin: 0 5px 0 0; }
