.WB_tab_b .inner { border: none; padding: 0; }
.WB_tab_a { margin: 0 0 5px 0; box-shadow: none; }
.WB_tab_a .tab_box_b .tab li:first-child, .WB_tab_a .tab_box_b .tab li:last-child { border-radius: 0; }
.WB_main_l{display:block !important;width:100%;}
.unlogin_left_nav{width:100%;margin:10px 0 0 0;}
.unlogin_left_nav .lev_Box{position: relative !important;width: 100%;top:0;}
.unlogin_left_nav .lev{margin:0;width: 10%;float: left;}
.unlogin_left_nav .lev .ico_block {float: none;font-size: 20px;width: 0;text-align: center;margin: 0 3px 0 0;}
.WB_left_nav .S_txt1,.WB_left_nav .S_ficon,.WB_left_nav .S_txt1, .WB_left_nav a.S_txt1:hover,.WB_left_nav .S_ficon, .WB_left_nav a:hover .S_ficon{color:#333;}
.WB_left_nav .lev a:hover,.WB_left_nav .lev_curr{background: transparent;}
.unlogin_left_nav .lev a{text-align: center;}
}
