css:
/*gismeteo.ru ТЕМНЫЙ*/@namespace "http://www.w3.org/1999/xhtml";
@-moz-document domain("gismeteo.ru"), domain("gismeteo.by"), domain("gismeteo.ua"), domain("gismeteo.pl"), domain("gismeteo.lt"), domain("gismeteo.lv"), domain("gismeteo.md"), domain("gismeteo.kz") {
body {
    background: #1d232c none repeat scroll 0 0;
    color: #ccc;
}
.rframe {
    background: #24303d none repeat scroll 0 0;
    border: 1px solid #364451;
    font-weight: 300;
}
.rfc {
    height: 0;
}
#information li {
    background-position: 0 -118px;
    border-bottom: 1px solid #000;
    border-top: 1px solid #364451;
    margin: 0;
    padding: 7px;
}
#information li a {
    padding: 5px;
}
.rfc, .rframe div.h2, .rframe h2, .rframe .tside, #weather .fcontent .header {
    background: #9b59b6 none repeat scroll 0 0;
}
.rframe h2, .rframe div.h2, #weather .fcontent .header {
    border: 0 none;
    text-shadow: 1px 1px #000;
}
#projects .section .sprite li {
    border-bottom: 1px solid #000;
    border-top: 1px solid #364451;
}
.cities ul, .cities h6 {
    background: #1d232c none repeat scroll 0 0;
    height: 100%;
    padding: 5px 0;
    white-space: nowrap;
    width: 115px;
}
.rframe .tside {
    height: 0;
}
.rframe .tab {
}
#cities-more {
    background: #304156 none repeat scroll 0 0;
}
#search-city {
    background: #304156 none repeat scroll 0 0;
    height: 32px;
    margin: 10px 12px 0;
    padding: 7px 0 0 5px;
}
a {
    color: #ccc;
}
a:hover {
    color: #f60;
}
#weather-news.dynamic.wide li.news.item {
    background: #304156 none repeat scroll 0 0;
    border: 1px solid #222;
    float: left;
    font-size: 14px;
    height: auto;
    margin-right: 10px;
    width: 98%;
}
.links-black A {
    color: #fff;
    text-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f, 0 0 0.2em #87f;
}
.links-black A:hover span {
    background-image: none;
    color: #fff;
    text-shadow: 0 0 0.2em #f87, 0 0 0.2em #f87, 0 0 0.2em #f87;
}
.mycity {
    background: #555 none repeat scroll 0 0;
}
.rframe .stab {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 center;
}
#weather .section {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 center;
}
.items_cols.js-cols {
    background: #233040 none repeat scroll 0 0;
    border: 1px solid #364451;
}
.items_cols .item {
    background: #1d232c none repeat scroll 0 0;
    border: 1px solid #364451;
    display: inline-block;
    margin: 0 0 15px 20px;
    min-height: 250px;
    padding: 3px;
    vertical-align: top;
    white-space: normal;
    width: 150px;
}
.items .item a.item__a .item__title {
    background-image: none;
    color: #ccc;
}
.body-clouds {
    background: #1d232c none repeat scroll 0 0;
}
.wmtop {
    background-color: #555;
}
.wmtop, .wmptab .tside, .wmtprev, .wmtnext, .wmtprev .control, .wmtnext .control {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 center;
}
#weather h2, #weather div.h2 {
    color: yellow;
}
#weather .barp, #weather .wind, #weather .hum, #weather .water {
    background: #fff none repeat scroll 0 0;
}
#ad-left.rframe, #ad-right.rframe, #yandex_ad, .mitsubishi, #google_ads_frame1 {
    display: none !important;
}
#ad-left, #ad-lb, #ad-right, #ad-rian, #rbc, #yandex, .media_top, .media_middle, #media-right, #weather-right, #weather-left, #ad-rbkua, #ad-gis-news.rframe, #weather-rbkua, div[id*="sup_ad_"], iframe[src*="adriver"], img[src*="adriver"] {
    display: none !important;
}
.media_frame.x400, #media-right, .by .media_middle {
    min-height: 0;
}
#menu {
    background: #000 none repeat scroll 0 0;
    border: 1px solid #364451;
    font-size: 13px;
    font-weight: 100;
    line-height: 25px;
    padding: 5px;
}
#menu li, #menu li.wide {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 center;
    border: 1px solid #414141;
    color: #fff;
    cursor: pointer;
    display: block;
    float: left;
    font-size: 12px;
    margin: 0 10px 0 0;
    padding: 0 12px 0 14px;
    text-transform: uppercase;
    transform: scale(1);
    transition: all 0.3s ease 0s;
    vertical-align: middle;
    white-space: nowrap;
    width: auto;
}
#menu li:hover {
    background: #000000 none repeat scroll 0 0;
    border: 1px solid #ffffff;
    box-shadow: 0 0 0.2em #87f;
    color: #fff;
    font-size: 12px;
    font-weight: 500;
    text-shadow: 0 0 0.2em #87f, 0 0 0.2em #87f, 0 0 0.2em #87f;
    text-transform: uppercase;
    transform: scale(1.13);
}
#weather .fcontent {
    height: 250px;
}
#weather-news .fcontent, #interesting .fcontent {
    height: auto;
    overflow: hidden;
}
.foo-nav.links-em a em, .soc2 H4 {
    color: #fff;
}
.link-gm EM {
    color: goldenrod;
}
.cat_cont {
    background: #24303d none repeat scroll 0 0;
    border: 1px solid #364451;
}
.calendar td.disabled {
    background-color: #999;
}
.calendar td.empty {
    background-color: #000;
}
#graph .section:hover {
    background: #fff none repeat scroll 0 0;
}
.countries .h6, .wmaps li a, .wmttabs li, #information li, #projects .section .sprite .widjet, #projects .section .sprite .extensions, #weather-maps li a, .cities li, .subregions_list__column li, #projects .section .sprite .radars, #weather-news.dynamic.wide li.news.item {
    transform: scale(1);
    transition: all 0.3s ease 0s;
}
.countries .h6hover, .wmaps li a:hover, .wmttabs li:hover, #information li:hover, #projects .section .sprite .widjet, #projects .section .sprite .extensions:hover, #weather-maps li a:hover, .cities li:hover, .subregions_list__column li:hover, #projects .section .sprite .radars:hover, #weather-news.dynamic.wide li.news.item:hover {
    transform: scale(1.07);
}
.items-list .item_mid {
    background: #222933 none repeat scroll 0 0;
    border: 1px solid #364451;
    transform: scale(1);
    transition: all 0.3s ease 0s;
}
.items-list .item_mid:hover {
    box-shadow: 0 0 10px 0 rgba(4, 162, 220, 0.5);
    transform: scale(1.03);
}
.item_mid .item__descr {
    color: #9b59b6;
}
.article__title {
    color: #9b59b6;
}
.wmaps li .map {
    border: 1px solid #666;
}
.wdata tbody th, .wdata tbody td {
    color: #fff;
}
#intown li.icon.cur, #intown li.icon.cur a {
    color: yellow;
}
#intown li.icon.cur, #intown li.icon.cur a {
    color: yellow;
    text-transform: uppercase;
}
.page_holiday_spring .nav, .weather_current .weather_frames .weather_frame_forecast .forecast_frame .forecast_wrap .cities, .weather_now .information .info_item .ii, .header, .tabs._left .tab {
    background: #24303d none repeat scroll 0 0;
    border: 1px solid #364451;
}
.content, .weather_current .weather_frames {
    background-color: #24303d;
}
.cities.cities_frame, .maps_frame, .hw_wrap .forecast_wrap, .widget_gm .gm_items, .astronomy_frame .astronomy_blocks .astronomy_block, .weather_now.blockIt, .forecast_frame .forecast_underground, .nav {
    background: #222933 none repeat scroll 0 0;
    border: 1px solid #364451;
}
.weather_frame_now, .forecast_frame, .weather_current .weather_frames .weather_frame_forecast .forecast_frame .forecast_wrap, .hw_wrap.forecast_frame, .cities {
    background: #222933 none repeat scroll 0 0;
}
.maps_frame, .media_with_map .media_left .media_frame {
    padding: 0;
}
.cities .cities_clear, .cities .cities_title, .cities .cities_title_block::before, .forecast_frame .headerline .title {
    background-color: #9b59b6;
}
.forecast_frame .headerline::after {
    border-bottom: 1px solid #9b59b6;
}
.content .mcontainer {
    background: #222933 none repeat scroll 0 0;
    border: 1px solid #364451;
    box-sizing: unset;
}
.subnav, .content .list .list_item.active {
    background-color: #9b59b6;
}
.weather_current .weather_frames, .forecast_frame .forecast_wrap, .tabs .tab {
    box-sizing: unset;
}
.tabs {
    margin: 5px;
}
.black, .gm_frame {
    color: #999;
}
.black, .gm_frame .gm_data .gm_td {
    color: #111;
}
.cities .cities_degrees {
    color: yellowgreen;
}
.forecast_frame .tabtempline_inner, .forecast_frame .templine_inner {
    background: #555 none repeat scroll 0 0 !important;
}
.forecast_frame .pressureline_inner {
    background: rgba(161, 230, 255, 0.3) none repeat scroll 0 0;
}
.weather_now .information .info_item .ii:first-child, .weather_now .information .info_item .ii:last-child {
    padding: 5px;
}
.forecast_frame .frame_items_10, .forecast_frame .frame_items_13, .forecast_frame .frame_items_14, .forecast_frame .frame_items_8 {
  background: #111 none repeat scroll 0 0;
  }
.widget__row:not(:last-child) {
  background: #555 none repeat scroll 0 0;
  margin-bottom: 5px;
    }    
.whiteBlockWithShadow, .widget {
  background-color: #222933;
    border: 1px solid #364451;
    }    
.widget__row_table .widget__value {
  color: #333;
    }    
.flexbox .main, .flexbox .right {
  color: #fff;
    }    
.w_time sup, .darkgray, .gray  {
  color: #ccc;
    }    
.tabs .tab {
  background-color: #111 !important;
    }    
.tabs._center .tab:nth-child(2), .tabs._left .tab:nth-child(1), .tabs._right .tab:nth-child(3) {
  background-color: #111;
    }    
   
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
}
