css:
@-moz-document domain("post.smzdm.com") {
/* post-list */
#global-search{padding: 0 !important;margin: 0;height: auto;background: transparent;position: fixed;top: 0;z-index: 1002;left: 800px;}
#global-search .search-inner{width: auto;margin: 0;padding: 0;height: auto;}
#global-search .search-inner>*{display:none;}
#global-search .search-inner .sub-right-content{display:block;margin:0;}
#sub-head{display:none;}
.feed-hot-hits .feed-right-top{display:none;}
.feed-hot-hits .feed-top-hot{width:100%;float:none;}
.feed-top-hot .feed-hot-list{width:100%;}
.feed-hot-hits .feed-hot-list .slick-list{width:calc(100% - 14px);}
.filter-feed-wrap{width:100%;float:none;}
.filter-tab-wrap,.primary-filter-tab-wrapper{width:100%;}
.primary-filter-tab{width:1081px;}
.feed-grid-wrap #feed-main-list{width:100%;}
.feed-grid-wrap #feed-main-list .feed-row-wide .feed-block{margin-right:26px;}
.feed-grid-wrap #feed-main-list .feed-row-wide:nth-child(4n) .feed-block{margin-right:0;}
.feed-grid-wrap #feed-main-list .z-feed-content .z-feed-foot{display:none;}
.sq-category{display:none;}
/* post-content */
#sub-header.sub-fix-search{min-height: 40px;}
#global-nav .nav-inner .nav-controls .control-list{display:none;}
#global-nav nav .nav-list>li:nth-child(4),
#global-nav nav .nav-list>li:nth-child(5){display:none;}
#global-search.mini-bar{display:none;}
#feed-wrap{position:relative;}
#feed-side{position:absolute;left:0;top:0;width:100%;margin:0;}
#feed-side>div{display:none;}
#feed-side div.J_side_fixed_copy{display:none !important;}
#feed-side .author-right{display:block;width:100%;margin:0;}
#feed-side .author-right>*{display:none;}
#feed-side .author-right:after{content:' ';clear:both;display:block;}
#feed-side .author-right .user_tx{display:block;float:left;padding:10px;border:none;}
#feed-side .author-right .user_tx .tx_Info{padding:0;}
#feed-side .author-right .user_tx>p{display:none;}
#feed-side .author-right .user-data{display:block;float:right;width:200px;margin:0 10px 0 0;}
.left-wrap{width:100%;margin:92px 0 0 0;}
.left-wrap .poster-wraper .detailed_banner{margin:0 auto;}
.left-wrap .experience-meta-nowrap,
.left-wrap .experience-zan,
.left-wrap .author-card-left,
#rewardModule{display:none;}
.comment-box .textarea_comment{width:848px;}
.comment_conBox{width:968px;}
.article-card .wrap-article ul .article-contant .related-right{width:738px;}
.fusion-card,#J_old_mention,#leftLayer,#footer{display:none !important;}
}
