css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://iboodhunt.1dagskoopjes") {
 
  body, .modal, #mc_embed_signup{
    background-color: #0b1621;
    background-image: none;
    color: #fff;
  }
  .holygrail, .well, .tw-time, .modal-header, .modal-footer{
    background-color: #13273a;
  }
  .holygrail .colmid, .well-chat-content, .shoutbox-timeline, .twtr-timeline, .dropdown-menu, .nav-tabs > .active > a, .nav-tabs > .active > a:hover{
    background-color: #0f1e2e;
  }
  .page-header{
    border-bottom-color: #aaa;
  }
  .bs-icon-header, .bs-icon-header-big{
    display: none;
  }
  h1, h2, h3, h4, h5, h6, .tw-user a, .tw-time, .tw-time:hover, .dropdown-menu, .dropdown-menu li > a, .nav-tabs > .active > a, .nav-tabs > .active > a:hover {
    color: #fff;
  } 
  .priceNow, .priceShip, .adminmsg{
    color: #f6b402;
  }
  textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input{
    background: #13273a ;
    border-color: #fff;
    color: #fff;
  }
  .chat-avatar img{
    box-shadow: none;
  }
  .transgrad{
	  background: linear-gradient(to bottom, rgba(0, 0, 0, 0), rgba(11, 22, 33, 1));
  }
  .alert-success{
    background-color: #596f08;
    color: #ecf8c0;
    border-color: #98bf0d; 
  }
  .alert-warning{
    background-color: #bc8f08;
    color: #fff1cc;
    border-color: #edb409; 
  }
  .alert-error{
    background-color: #a5351e;
    color: #fee7e1;
    border-color: #95221e; 
  }
  .alert-info{
    background-color: #0e89b4;
    color: #d7f4fe;
    border-color: #0c99a6; 
  }
  [class^="icon-"]{
    background-image: url("http://iboodhunt.1dagskoopjes.nl/img/glyphicons-halflings-white.png");
  }
  .icon-iboodhunt{
    background-image: url("http://iboodhunt-static.1dagskoopjes.nl/img/iboodhunticon.png");
    background-position: 0 0;
    background-repeat: no-repeat;
    height: 35px;
    margin-bottom: -10px;
    margin-left: -10px;
    margin-right: 10px;
    margin-top: -2px !important;
    vertical-align: text-top;
    width: 32px;
  }
  img {
			/*opacity: 0.5;*/
    -webkit-filter: brightness(50%); /* Chrome, Safari, Opera */
     filter: brightness(50%);
	}

	img:hover, .iboodhuntinstantlogo, [src^="http://iboodhunt-static.1dagskoopjes.nl/images/1dkavatar"], [src^="http://iboodhunt-static.1dagskoopjes.nl/images/smilies/"] {
			/*opacity: 1;*/
    -webkit-filter: brightness(90%); /* Chrome, Safari, Opera */
     filter: brightness(90%);
	}
  .progressBar, .subnav-brand-li, .navbar .nav .main-cat-1.active a, .navbar .nav .main-cat-1 a:hover, .navbar .nav .main-cat-1.animate a{
    background-color: #018ab9;
  }
  .navbar .nav .main-cat-1.active::before{
    background-color: #035f7e;
  }
  #cdt .spr{
    background-image: url("https://d29am0ph5s0t8u.cloudfront.net/production/cdt/widgets/sprites/image/1506226/oc-sprite.png?version=57c6f066-11");
  }

  .thumbnail_ibood.history_thumb div.caption div{
    color: #999!important;
  }
 ::-webkit-input-placeholder { /* WebKit, Blink, Edge */
    color:    #ccc;
  }
  ::-moz-placeholder { /* Mozilla Firefox 19+ */
    color:    #ccc;
   opacity:  1;
  }
  :-ms-input-placeholder { /* Internet Explorer 10-11 */
    color:    #ccc;
  }
}
