css:
@-moz-document domain("vk.com") {
  #page_layout {
      width: 1260px !important;
  }

  #page_body {
      width: 1100px !important;
  }

  .page_post_sized_thumbs.clear_fix {
      margin-left:auto; 
      margin-right: auto;
  }

  .clear_fix.pv_photo_wrap {
    display: flex;
    flex-direction: column;
  }

  .pv_img_area_wrap a, #pv_photo {
    width: auto !important;
    height: auto !important;
  }

  .narrow_column.ui_scroll_container.ui_scroll_default_theme.ui_scroll_unnecessary, #pv_narrow { 
    width: 100%;
  }

  #layer_wrap #layer {
    margin-top: 40px !important; 
  }

  .pv_cont .pv_close_btn {
      right: 60px !important;
  }


  #im--page, .im-page.im-page_classic {
      width: 920px !important;
  }

  .page_block.ui_rmenu.im-right-menu._im_right_menu.ui_rmenu_pr {
      margin-left: 935px !important;
  }

  #feed_filters > div:nth-of-type(2), #group_recom_wrap {
      display: none;
  }
   #ads_left {
     display: none !important;
   }

  .index_rcolumn input.big_text {
   width: 330px;
   max-width: 100%;
  }
  
  .index_page #wrap2 {
   width: 1260px;
  }
  
  #side_bar_inner[style] {
    position: relative !important;
    margin-top: 0 !important;
    bottom: initial !important;
  }

  #side_bar {
      position: fixed;
  }
  
  
.im-page.im-page_classic .im-mess-stack {
    max-width: 920px;
}

.im-page.im-page_classic .im-page--header {
    width: 920px;     
}    

.im-page.im-page_classic .im-page--header-chat {
    width: 922px;
    box-shadow: 0 6px 4px -4px rgba(0, 0, 0, .2);
}

.im-page.im-page_classic .im-mess:not(.im-mess_fwd) {
	max-width: 920px;
}

.im-page.im-page_classic .im-page--chat-input {
    width: 920px;
/*     box-shadow: 0 -6px 4px -4px rgba(0, 0, 0, .2); */
}

.im-chat-input.im-chat-input_classic .im-chat-input--textarea {
    width: 80%;
}

.im-chat-input _online._im_peer {
    float:right;
}

.im-chat-input .im-chat-input--text {
        transition: min-height, 0.2s;
}

.im-chat-input .im-chat-input--text:focus {
    min-height: 200px;
}

.im_msg_media.im_msg_media_link {
    max-width: 60%;
}

.page_media_link_img {
    height: auto;
}

.im-page .im-page--history-new-bar {
    border-bottom: 1px solid rgba(93%, 93%, 93%, 0.3);
}

.im-mess-stack .page_post_sized_thumbs.clear_fix {
    margin-left: 0;
}
  .fc_tab_wrap {
        width: 455px;
}
}
