css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("del.icio.us") {

  .googleAd970-90Outer {
    display: none;
  }
  
  .logo {
    margin-top: 0.4em;
  }
  
  .navPanel {
    margin-left: 1em;
    margin-right: 1em;
  }
  
  /* Add Bookmark link */
  
  .navPanel li:nth-child(4),
  .navPanel li:nth-child(4) a {
    background: #ca9022;
    padding: 0.5em;
    margin-left: 1em;
  }
  
  .navPanel li:nth-child(4) a:hover {
    color: #000;
  }
  
  .searchPanel button {
    margin-top: 0.5em;
    margin-right: 0.5em;
  }
  
  .breadcrumbPanel {
    padding-bottom: 0em !important;
  }
  
  .pop {
    width: 35em !important;
  }
  
  .popcon, 
  .show, 
  .pop, 
  .popInner, 
  .saveSocialLinks, 
  #delAlert,
  #delAlert *,
  body * {
    transition: none !important;
  }
  
  .popInner {
    margin: 0px !important;
    max-width: none !important;
  }
  
  .popInner .saveSocialLinks button, 
  .popInner .saveSocialLinks a {
    background: #c7c7c7 !important;
    color: #000 !important;
    font-size: 0.75em !important;
    padding: 0.4em 0.6em !important;
    vertical-align: middle !important;
  }
  
  .popInner .saveSocialLinks button:hover, 
  .popInner .saveSocialLinks a:hover {
    background: #0076e8 !important;
    color: #000 !important;
  }
  
  
  body {
    background: #e7e7e7 !important;
  }
  
  .header {
    box-shadow: none !important;
    padding: 0.5em 0 !important;
  }
  
  .headerSearchPan {
    width: auto !important;
  }
  
  input,
  button {
    font-size: 0.75em !important;
    line-height: 1.35em !important;
  }
  
  ul.tagName {
    margin-top: 1em;
    padding-top: 0px !important;
  }
  
  ul.tagName a {
    padding: 0.3em;
    background: #c7c7c7;
    text-decoration: none !important;
    color: black;
    border-radius: 0.2em;
    vertical-align: middle;
  }
  
  ul.tagName a:hover {
    color: white;
    background: #0076e8;
  }
  
  .articleInfoPan {
    padding-left: 0px !important;
    min-height: auto !important;
  }
  
  .articleInfoPan p:nth-child(2) {
    display: none;
  }
  
  .articleInfoPan p:nth-child(3) {
    font-size: 0.75em;
  }
  
  .adMrginTop {
    display: none;
  }
  
  .profileDetails {
    display: none;
  }
  
  .followBtn {
    font-size: 0.8em !important;
    padding: 0.005em 0.25em !important;
    margin: 0px !important;
    width: 6em !important;
    margin-bottom: 1em !important;
  }
  
  .profileLeftPan {
    width: 10% !important;
  }
  
  .profileContent .container {
    padding-right: 0px !important;
  }
  
  .sharePanel {
    background: #c7c7c7 !important;
    color: black !important;
    border: none !important;
    font-size: 1em !important;
  }

  .sharePanel a {
    color: #555 !important;
  }
  
  .sharePanel a:hover {
    color: #0a539a !important;
  }
  
  .profileMidpanel {
    width: 80% !important;
    margin-left: 4em;
  }
  
  .profileMidpanel h1 {
    font-size: 0.75em !important;
    font-family: "HelveticaNeueLTPro-Roman",sans-serif !important;
  }
  
  .profileMidpanel .tabMenu a {
    border-radius: 0px;
    background: none;
    color: black;
  }
  
  .profileMidpanel .tabMenu .active a {
    background: #c7c7c7 !important;
  }
  
  .contentRightPan, .userHomeRight {
    display: none;
    width: 0px;
  }
  
  .savesCount, .savesCount a {
    display: none !important;
  }
  
  .thumbTBriefTxt p {
    color: green !important;
  }

}
