css:
@-moz-document domain("trello.com") {
@media screen and (min-width: 1368px) {
    .board-header {
        position: absolute;
        top: -42px;
        right: 215px;
        left: 500px;
        z-index: 2;
    }
    .header-logo {
        display: none;
    }
    #header {
        z-index: 1;
    }
}

@media screen and (min-width: 1220px) and (max-width: 
1368px) {
    .board-header {
        position: absolute;
        top: -42px;
        right: 215px;
        left: 360px;
        z-index: 2;
    }
    .header-logo {
        display: none;
    }
    #header {
        z-index: 1;
    }
}
}
