body.sf .left_count_wrap {
    font-weight: 900
}

a.groups_messages_block:before {
    background: url(https://i.imgur.com/58bQxow.png) no-repeat 0 -28px;
}
/* i luv old checkboxes <3 */

.checkbox:before {
    background: url(https://vk.com/images/icons/check.gif) 0 0 no-repeat;
    height: 14px;
}

.checkbox:hover:before {
    background-position: 0 -28px;
}

.checkbox.on:before {
    background-position: 0 -14px;
}

.checkbox.on:hover:before {
    background-position: 0 -42px;
}
/* header text shadow */

.top_audio_player, .top_profile_name {
    text-shadow: 0 1px 0 rgba(0, 0, 0, 0.39);
}
/* header selected */

.top_nav_btn.active, .top_nav_link.active {
    /*background-color: #43648c;
    background: linear-gradient(to bottom, #4C6C92 0%, #5D81A7 100%);*/
    
    box-shadow: inset 0 4px 23px -2px rgba(0, 0, 0, 0.38);
    background: transparent;
}
/* new public page advices */

.public_help_steps_module .public_help_step_image {
    background-color: #ECF1F5;
}
/* SF specific */

.box_title {
    font-weight: 500!important;
}
/* hide some shit */

