    background-image: url(https://i.imgur.com/C9KExBy.png);
    background-repeat: no-repeat;
    width: 12px;
}
/* friends icon in usual state */

.chat_onl:after {
    background-position: -39px -45px;
}
/* friends icon when popup opened */

.layers_shown .chat_onl:after {
    background-position: -52px -45px;
    opacity: 0.5;
    width: 11px;
}
/* search icon */

.chats_sp.fc_clist_search_icon {
    background-position: -98px 0px;
}
/* friends chat top title */

.fc_tab_title {
    padding: 4px 3px;
    font-size: 11px;
}
/* friends chat top icons */

.fc_tab_close_wrap, .fc_tab_max_wrap, .fc_tab_pin_wrap {
    margin-top: 8px;
}
/* peer name in friends chat */

.fc_contact_name {
    font-size: 11px;
}
/* bigger photos in friends chat */

img.fc_contact_photo, span.fc_contact_photo {
    width: 38px;
    height: 38px;
}
/* online indicator in friends chat */

span.fc_contact_photo:after {
    right: -1px;
    bottom: -1px;
}
/* make buttons great again! */

